css:
@-moz-document domain("pengfu.com") {
/*移除关注*/
.clearfix  .addbtnwraps{
    display:none;
}

/*不想登录*/
.clearfix>.fr{
    display:none;
}
.clearfix >.fl{
    float :clear;
    width:70%;
    margin:0 15%;
}

/*不想反馈*/
.suggestion{
    display:none;
}

/*banner瘦身*/
.nav.fl{
    padding-top:0;
}
.logo{
    display:none;
}
.nav-pfyc{
    display:none;
}

/*不要footer*/
.footer-p1{
    display:none;
}

/*不要刺眼的查看全部*/
.more-btn-box{
    border-top:1px solid #9f9 !important;
    background-color:rgba(68, 166, 123,0.4) !important;
    transition:background-color 0.25;
}
.icon-con.more-btn,.icon-con.shouqi-btn{
    filter:hue-rotate(160deg);
}

.content-img:hover + .more-btn-box{
    border-top:1px solid #f99 !important;
    background-color:rgba(242, 88, 93,0.4) !important;
}
.content-img:hover + .more-btn-box .icon-con.more-btn,
.content-img:hover + .more-btn-box .icon-con.shouqi-btn{
    filter:hue-rotate(0deg)
}

/*不是很想评论*/
.action.clearfix{
    padding:0;
}
.fl.shareBox{
    display:none;
}
}
