css:
@-moz-document domain("peninsuladailynews.com") {

body{
    background-color:#000040 !important; /*most of document body (but not all)*/
	color: #C08040 !important; /*the one day ago text */
}



/*Top header--logo area*/
.top-bar, .top-bar ul {
	background-color: #000040 !important;
}
/* text of story */
.card {
	background: #000040 !important;
	color: #FFC000 !important; 
}
.card .card-section h4 {
 
	color: #4080FF;
}

/* text of hyperlinked headlines */
a {
	line-height: inherit;
	color: #0080C0; 
}

/* submenu bar at top */
.top-bar-container .affinity-menu {
	background-color: #004080 !important; 
}
 
/*side topstories background*/
.top-stories .large-5 .grid-upper {
    background-color: #000040 !important; 
    color: #80ABA0 !important;
}


/* the reader poll heading text */
.module-reader-poll h3 {
	color: #0080C0 !important; 
}

/* Poll Box */
#PDI_container10084098 .pds-box {  
	background: #0080C0 !important;
} 

/*Poll Vote button*/
#PDI_container10084098 .pds-vote-button { 

	background: #0080C0;
}


/*this is for the centered title sections like News, etc */
h6[class*="category-"] a { 
	background: #004080 !important;
    color: #00C0FF !important;
}

/*body {
	color: #C08040 !important;
}*/

/* Numbered List of Most Read */
.module-trending h4.count::before, .module-commented > ul > li > h4.count::before { 
	background: #4080FF !important;
    color: #000040 !important;
}

/* specific article title*/
article.main-content header h1.entry-title { 
	font-size: 2.625rem;
	text-align: left;
    color: #80C0FF !important;
}

/*specific article text */
article.main-content .entry-content { 
	color: #00C0FF !important;

}

/*background of article Tags*/
.label.category-meta, .label.category-meta-entertainment, .label.category-meta-letters, .label.category-meta-obituaries, .label.category-meta-nation-world, .label.category-meta-northwest, .label.category-meta-politics, .label.category-meta-crime, .label.category-meta-opinion, .label.category-meta-life, .label.category-meta-sports, .label.category-meta-news, .label.category-meta-business, .label.category-meta-marketplace, .label.category-meta-tag {
	background: #004080 !important;
    background: #00C0FF !important;
}

/* topic header on specific article*/
.landing h1.category, .landing h1[class*="category-"], .landing h1[class*="tag-"] { 
	background: #004080 !important;
    color: #00C0FF !important;
}

/*footer stuff */
#footer-container {
	background: #000040 !important;

}

#footer .textwidget {
    color: #00C0FF !important;
}

/*Peninsula Daily link/text in footer */
#footer .textwidget a { 
	color: #00C0FF;
}

/*link text in bottom of footer*/
#menu-footer li a { 
	color: #00C0FF;
}
}
