css:
@-moz-document domain("mangadex.org") {

    /* COLORS */

    /** DESCRIPTION : set the colors **/
    :root {
        --body-bg: #1F1F21 !important;
        --btn-bg: #2F2E33 !important;
        --card-bg: #2B2C30 !important;
        --card-bg-2: #222326 !important;
        --header-bg: #202124 !important;
        --header-bg-2: #171719 !important;
        --border-color: #1D1D1E !important;
        --border-color-2: #111 !important;
        --primary-color: #FFF !important;
        --invert: invert(1) !important;
    }

    /* GENERAL */

    /** DESCRIPTION : change the elements of all page **/
    *:not(span) {
        font-family: "Roboto", "ぼくたちのゴシック２ボールド" !important;
    }

    *::-moz-focus-inner {
        border: 0;
    }

    select {
        -webkit-appearance: none;
        -moz-appearance: none;
        text-indent: 0px;
        text-overflow: ellipsis;
        white-space: nowrap;
    }

    body {
        background: var(--body-bg);
    }

    #content {
        margin-top: 30px !important;
    }

    hr {
        border-top: 1px solid var(--border-color);
    }

    .btn.dropdown-toggle.btn-light:active,
    .show > .btn-light.dropdown-toggle {
        background: var(--card-bg-2) !important;
    }

    .alert {

        /*** ALERT POPUP ***/
        background-image: none;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .alert,
    .alert * {
        color: #FFF;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .alert.alert-warning {
        background: #ED4C67;
    }

    .alert.alert-success {
        background: #10AC84;
        padding-top: 8px !important;
    }

    #announcement,
    #announcement * {
        color: #FFF !important;
    }

    .dropdown-menu {

        /*** DROPDOWN MENU ***/
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding-top: 15px !important;
        padding-bottom: 18px !important;
    }

    .dropdown-item.active,
    .dropdown-item:hover,
    .dropdown-item:focus {
        background: var(--card-bg);
        color: var(--primary-color);
    }

    .dropdown-item {
        color: var(--primary-color) !important;
        font-size: 14pt;
        font-stretch: condensed;
    }

    .dropdown-menu * {
        border-color: var(--border-color) !important;
    }

    .btn .fas::before {

        /*** ICONS ***/
        color: #FFF !important;
    }

    .fa-star::before {
        color: #F4D03F !important;
    }

    .fa-book,
    .fa-book::before {
        color: #2980B9 !important;
    }

    .fa-bookmark::before {
        color: #2ECC71;
    }

    .fa-exclamation-circle::before,
    .fa-info::before,
    .fa-info-circle::before,
    .fa-question-circle::before {
        color: #8395A7 !important;
    }

    .fa-file::before {
        color: #BEBEBE !important;
    }

    .fa-user::before,
    .fa-users::before,
    .fa-user-friends::before {
        color: #ABB7B7 !important;
    }

    .fa-clock::before {
        color: #6C7A89 !important;
    }

    .fa-sync::before {
        color: #218C74 !important;
    }

    .fa-search::before {
        color: #EAB543;
    }

    .fa-tv::before {
        color: #808E9B !important;
    }

    .fa-plus-circle::before {
        color: #596275 !important;
    }

    .fa-university::before {
        color: #B33771 !important;
    }

    .fa-discord::before {
        color: #7388D9 !important;
    }

    .fa-twitter::before {
        color: #00ACED !important;
    }

    .fa-reddit::before {
        color: #FF4500 !important;
    }

    .fa-hashtag::before {
        color: #8A3AB9 !important;
    }

    .fa-list::before {
        color: #34ACE0;
    }

    .fa-code::before {
        color: #218C74;
    }

    .fa-thumbtack::before {
        color: #E66767;
    }

    .fa-lock::before {
        color: #F5CD79;
    }

    .fa-envelope::before {
        color: #227093;
    }

    .fa-pencil-alt::before {
        color: #FFB142;
    }

    .fa-trash::before {
        color: #84817A;
    }

    .fa-check::before {
        color: #2ED573;
    }

    .fa-pause::before {
        color: #ED4C67;
    }

    .fa-calendar-alt::before {
        color: #6BC6E1;
    }

    .fa-eye::before {
        color: #006266;
    }

    .fa-clipboard-list::before {
        color: #747D8C;
    }

    .fa-dollar-sign::before {
        color: #2ED573;
    }

    a[style*="#c00"] {

        /*** USERS COLORS ***/
        color: #ED4C67 !important;
    }

    a[style*="#36f"],
    a[style*="#06f"] {
        color: #686DE0 !important;
    }

    a[style*="#090"] {
        color: #A3CB38 !important;
    }

    .pagination {

        /*** PAGINATION ***/
        margin-bottom: 20px !important;
    }

    .pagination .page-item {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .pagination .page-item a {
        background: var(--card-bg) !important;
        border: none !important;
    }

    .pagination .page-item a:focus {
        box-shadow: none;
    }

    .pagination .page-item.active a {
        background: var(--card-bg-2) !important;
    }

    .btn.btn-sm.btn-secondary.next_chapter_alt,
    .btn.btn-sm.btn-secondary.prev_chapter_alt,
    .btn.btn-sm.btn-secondary.next_page_alt,
    .btn.btn-sm.btn-secondary.prev_page_alt {
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .alert-info,
    .btn.btn-secondary.disabled,
    #comment-button .comment-amount,
    .dropdown-menu.dropdown-menu-right.show a[id="3"],
    .footer,
    #hide-header-button,
    .reader-page-bar .track,
    #reader-controls-collapser,
    .reader-controls-footer,
    .reader-controls-groups,
    .reader-controls-pages,
    .reader-controls-mode,
    .reader-load-icon,
    #report-button,
    #search_manga .badge.badge-secondary,
    #search_manga .btn-group,
    #search_manga .p-1.col.text-center.text-primary,
    .border-bottom .btn-group,
    .border-bottom .p-1.col.text-center.text-primary,
    #settings-button .d-lg-inline {
        display: none !important;
    }

    /* HEADER */

    /** DESCRIPTION : change the elements of the header **/
    .navbar {
        background: var(--header-bg);
        border: none !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        padding-top: 10px !important;
        padding-bottom: 14px !important;
        position: absolute;
        top: 0px !important;
        display: block !important;
    }

    .navbar .container {

        /*** HEADER CONTAINER ***/
        max-width: 100% !important;
        padding-right: 10px !important;
        padding-left: 0px !important;
    }

    .navbar-brand {

        /*** HEADER MASCOT ***/ 
        margin-left: -5px !important;
    }

    .navbar .container .d-lg-none {

        /*** HEADER TITLE ***/
        font-size: 14pt !important;
        position: relative !important;
        top: 1px !important;
        left: 6px;
    }

    .navbar .container .nav-item {

        /*** HEADER SECTION ***/
        margin-right: 10px !important;
    }

    .navbar .container .nav-item .fas {
        margin-right: 5px;
    }

    .navbar .container .navbar-nav:last-child .nav-item {
        margin-right: 5px !important;
    }

    .navbar .container .navbar-nav:last-child .nav-item .fas {
        margin-right: 0px !important;
    }

    .navbar .container a {
        color: var(--primary-color) !important;
        font-size: 14pt;
        font-stretch: condensed;
        text-shadow: none;
    }

    .navbar .container .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 10px !important;
        width: 10px !important;
        position: absolute;
        top: 20px;
        right: -15px;
        filter: brightness(0) var(--invert) !important;
    }

    .navbar .container .input-group {

        /*** HEADER SEARCH ***/
        border: none !important;
    }

    .navbar .container .input-group #quick_search_type {
        background: var(--btn-bg) !important;
        border: none !important;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        margin-top: 1px;
        margin-right: 20px !important;
    }

    .navbar .container .input-group #quick_search_input {
        background: transparent !important;
        border: none;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
        text-indent: -12px !important;
    }

    .navbar .container .input-group #quick_search_type:focus,
    .navbar .container .input-group #quick_search_input:focus {
        box-shadow: none !important;
    }

    .navbar .container .input-group #quick_search_button {
        background: transparent !important;
        border: none;
        box-shadow: none;
        font-size: 14pt;
    }

    #quick_search_button .fas::before {
        color: var(--primary-color) !important;
    }

    .navbar .container .navbar-toggler {
        background: var(--btn-bg) !important;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .navbar .container .navbar-toggler span {
        filter: brightness(0) var(--invert) !important;
    }

    /* LOGIN */

    /** DESCRIPTION : change the elements of the login page **/
    #login_container input,
    #reset_email,
    #reset_code {
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    #login_container .custom-checkbox .custom-control-label::before {
        background: var(--card-bg);
        border: 1px solid var(--border-color);
        border-radius: 2px;
        margin-top: 9px;
    }

    #login_container .custom-checkbox .custom-control-input:checked ~ .custom-control-label::after {
        filter: brightness(0) var(--invert);
        margin-top: 3px;
    }

    #login_button,
    #forgot_button,
    #reset_button {
        background-image: none;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: #FFF !important;
        text-shadow: none;
    }

    /* MODAL */

    /** DESCRIPTION : change the elements of the modal popup **/
    .modal-content {
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .modal-content * {
        border-color: var(--border-color);
        color: var(--primary-color);
        font-stretch: condensed;
    }

    .modal-content .modal-header * {
        font-size: 16pt;
    }

    .modal-content .modal-body * {
        font-size: 14pt;
    }

    .modal-content .modal-body button,
    .modal-content .modal-body input,
    .modal-content .modal-body select,
    .modal-content .modal-body textarea {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .modal-content .modal-body button:focus {
        background: var(--card-bg-2) !important;
        box-shadow: none !important;
    }

    .modal-content .flag {
        position: relative;
        top: -3px !important;
    }

    .modal-content .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 15px !important;
        width: 15px !important;
        position: relative !important;
        top: 5px !important;
        filter: brightness(0) var(--invert) !important;
    }

    .modal-content .bs-actionsbox {
        background: var(--card-bg) !important;
        border-bottom: 1px solid var(--border-color);
        padding-top: 10px;
        padding-bottom: 10px !important;
        margin-top: -15px;
    }

    .dropdown-menu > li > a:hover,
    .dropdown-menu > li > a:focus {
        background: var(--card-bg);
    }

    .modal-footer .btn {
        background: #446CB3;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
        color: #FFF !important;
        font-size: 14pt;
        font-weight: bold;
        padding-bottom: 9px;
    }

    .close span {
        font-size: 18pt !important;
        font-stretch: normal !important;
        margin-right: 3px !important;
    }

    /* SETTINGS */

    /** DESCRIPTION : change the elements of the settings page **/
    #site_settings_form button,
    #site_settings_form input,
    #change_profile_form button,
    #change_profile_form input,
    #change_profile_form textarea,
    #change_password_form input,
    #upload_settings_form button {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    #site_settings_form .dropdown-toggle::after,
    #change_profile_form .dropdown-toggle::after,
    #upload_settings_form .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 15px !important;
        width: 15px !important;
        position: relative !important;
        top: 5px !important;
        filter: brightness(0) var(--invert) !important;
    }

    .form-group .custom-checkbox .custom-control-input:checked ~ .custom-control-label::before {
        background: var(--card-bg);
        border: 1px solid var(--border-color);
        border-radius: 2px;
        margin-top: 10px;
    }

    .form-group .custom-checkbox .custom-control-input:checked ~ .custom-control-label::after {
        filter: brightness(0) var(--invert);
        margin-top: 5px;
    }

    /* HOME */

    /** DESCRIPTION : change the elements of the home page **/
    .nav-pills .nav-link.active,
    .nav-pills .show > .nav-link {
        border-radius: 2px;
        color: #FFF !important;
        opacity: 1 !important;
    }

    .rounded {
        border-radius: 2px !important;
    }

    .rounded a img {
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
    }

    #content .mb-3 .d-inline {
        color: var(--primary-color);
        font-size: 16pt;
        font-stretch: condensed;
        font-weight: bold;
    }

    .tab-content #latest_update .row .flag,
    .tab-content #follows_update .row .flag {
        position: relative;
        top: -3px !important;
    }

    .tab-content #latest_update .border-bottom .fas.fa-book,
    .tab-content #follows_update .border-bottom .fas.fa-book,
    .list-group .border-bottom .fas.fa-book {
        margin-left: -5px !important;
    }

    .float-right.btn-group {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .float-right.btn-group button {
        background: var(--card-bg) !important;
        border: none !important;
        box-shadow: none !important;
        color: var(--primary-color) !important;
    }

    #forum_posts .list-group.list-group-flush a {
        color: #2980B9 !important;
    }

    #hled_titles_owl_play,
    #hled_titles_owl_stop,
    #new_titles_owl_play,
    #new_titles_owl_stop {
        background: var(--card-bg-2) !important;
    }

    .float-right.btn-group button .fas::before {
        color: var(--primary-color) !important;
    }

    .large_logo {
        height: 280px !important;
    }

    .large_logo.rounded {
        border-radius: 2px !important;
    }

    .owl-carousel .owl-item img.owl-lazy {
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .car-caption.px-2.py-1 {
        background: var(--card-bg);
        border-bottom-right-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        height: 65px !important;
    }

    .car-caption.px-2.py-1 * {
        font-size: 12pt;
        font-stretch: condensed;
    }

    /* FOLLOW */

    /** DESCRIPTION : change the elements of the follow page **/
    #content .nav.nav-tabs {
        padding-bottom: 8px;
    }

    #content .nav.nav-tabs .nav-item .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 10px !important;
        width: 10px !important;
        position: absolute !important;
        top: 19px !important;
        right: 0px;
        filter: brightness(0) var(--invert) !important;
    }

    .nav-tabs .nav-link.active,
    .nav-tabs .nav-item.show .nav-link {
        background: transparent !important;
        border: 1px solid transparent !important;
        color: var(--primary-color) !important;
    }

    .nav-link.active {
        opacity: .5;
    }

    #content *,
    #chapters *,
    .tab-content * {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .tab-content #chapters #import_form .form-control {
        background: var(--card-bg) !important;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .social-media-btns > div > a {
        color: transparent !important;
    }

    /* MANGA */

    /** DESCRIPTION : change the elements of the manga page **/
    .card {

        /*** MANGA CARD ***/
        background: var(--card-bg);
        border: none;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    .list-group .list-group-item {
        background: var(--card-bg);
        border-bottom: 1px solid var(--border-color);
        padding-top: 10px !important;
    }

    .card .card-header {

        /*** MANGA CARD HEADER ***/
        background: var(--header-bg-2);
        border-color: var(--border-color-2) !important;
        border-top-right-radius: 2px;
        border-top-left-radius: 2px;
        font-size: 15pt;
        font-stretch: condensed;
        padding: 15px !important;
    }

    .card-header.p-0 {
        border-radius: 1px !important;
    }

    .card .card-header .rounded {

        /*** MANGA CARD FLAG ICON ***/
        position: relative;
        top: -4px !important;
        margin-left: 5px !important;
    }

    .card .card-header .fas {

        /*** MANGA CARD RSS ICON ***/
        margin-top: 2px !important;
        margin-right: 5px !important;
    }

    .nav.nav-tabs .fa-rss {
        margin-top: 14px !important;
        margin-right: 20px;
    }

    .card .card-header .fa-rss::before,
    .nav.nav-tabs .fa-rss::before {
        color: #F0932B !important;
        font-size: 14pt;
    }

    .card *,
    .list-group .list-group-item * {

        /*** MANGA CARD CONTENT ***/
        border-color: var(--border-color);
        color: var(--primary-color);
    }

    .list-group .list-group-item * {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .card .card-body .rounded {

        /*** MANGA CARD CONTENT COVER ART ***/
        margin: 10px;
    }

    .card .card-body .col-xl-9.col-lg-8.col-md-7 {

        /*** MANGA CARD CONTENT DATA ***/
        margin-top: 10px !important;
        margin-left: -5px !important;
    }

    .card .card-body .col-xl-9.col-lg-8.col-md-7 div {
        margin-top: 2px;
        margin-bottom: 5px;
    }

    .card .card-body .col-xl-9.col-lg-8.col-md-7 div * {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .card .card-body .badge {

        /*** MANGA CARD CONTENT DATA TAG ***/
        background: #F2784B;
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: #FFF !important;
        font-size: 14pt;
        font-stretch: condensed;
        padding: 8px 10px 10px 10px !important;
        margin-top: 3px;
        margin-right: 2px;
        margin-bottom: 2px;
    }

    .card .card-body .badge * {
        color: #FFF !important;
    }

    .card .card-body .list-inline .list-inline-item img {

        /*** MANGA CARD CONTENT DATA LINKS ***/
        border-radius: 50% !important;
        margin-top: -4px !important;
        margin-right: 4px !important;
    }

    .card .card-body .btn,
    .card .card-body #upload_button {

        /*** MANGA CARD CONTENT DATA BUTTON ***/
        background-image: none !important;
        border: none;
        border-radius: 2px !important;
        box-shadow: none !important;
        color: #FFF !important;
        margin-right: 3px;
    }

    .card .card-body .btn * {
        color: #FFF;
    }

    .card .card-body #upload_button {
        background: #10AC84 !important;
        margin-top: -3px !important;
    }

    .card .card-body .btn.manga_follow_button,
    .card .card-body .btn.dropdown-toggle-split {
        background: #EB974E;
    }

    .card .card-body .btn.dropdown-toggle-split {
        border-top-left-radius: 1px !important;
        border-bottom-left-radius: 1px !important;
        margin-left: -14px;
    }

    .card .card-body .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 10px !important;
        width: 10px !important;
        position: relative;
        top: 3px;
        filter: brightness(0) invert(1) !important;
    }

    .card .card-body .btn-warning {
        background: #FF6B6B !important;
    }

    #histogram_toggle {
        background: #00b894 !important;
    }

    .nav-tabs {

        /*** MANGA CHAPTER SECTION ***/
        background: var(--card-bg);
        border-color: var(--border-color);
        border-top-right-radius: 2px;
        border-top-left-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding-top: 5px;
        padding-bottom: 7px;
    }

    .nav-tabs * {
        font-size: 14pt;
        font-stretch: condensed;
    }

    .nav-tabs .nav-link.active {
        background: transparent;
        border: none;
        border-top-right-radius: 2px;
        border-top-left-radius: 2px;
        color: var(--primary-color);
        margin-bottom: -1px !important;
    }

    .nav-tabs .nav-link:hover,
    .nav-tabs .nav-link:focus {
        border-color: transparent !important;
    }

    .chapter-container {
        background: var(--card-bg);
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
    }

    .chapter-container * {
        border-color: var(--border-color);
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    .chapter-container > .row:nth-last-of-type(1) * {
        border: none !important;
    }

    .chapter-container .row .badge,
    .nav-link .badge {
        border-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        padding: 5px 8px;
    }

    .chapter-container .row .badge {
        background: #2C3E50;
    }

    .nav-link .badge {
        background: #446CB3;
        font-size: 14pt !important;
        font-stretch: condensed !important;
        margin-left: 10px;
    }

    .chapter-container .row .badge,
    .chapter-container .row .badge *,
    .nav-link .badge {
        color: #FFF !important;
        font-size: 12pt !important;
        font-stretch: condensed;
    }

    .edit.tab-content {

        /*** MANGA MISSING SECTION ***/
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .card .card-body #text {

        /*** MANGA COMMENT SECTION ***/
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .card .card-body #text:focus {
        box-shadow: none;
    }

    .card .card-body .form-group button {
        background: var(--card-bg-2);
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color) !important;
        margin-bottom: 7px;
        margin-left: 3px !important;
    }

    .card .card-body .form-group button .fas::before {
        color: var(--primary-color) !important;
    }

    .card .card-body #post_reply_button {
        background: #019875 !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: bold;
    }

    .card .card-body #post_reply_button * {
        color: #FFF;
    }

    .card .card-body .row > div > .btn,
    #back_button {
        background: #EB974E;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: bold;
    }

    #content .table {
        border-collapse: initial;
        border: 2px solid var(--card-bg);
        border-radius: 2px;
        border-spacing: 0;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    #content .table tbody tr {
        background: var(--card-bg);
    }

    #content .table-striped tbody tr:nth-of-type(2n+1) {
        background: var(--card-bg-2);
    }

    #content .table tbody tr td {
        border: none;
    }

    #content .table tbody tr td * {
        border-color: var(--border-color);
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    #content .table tbody tr .avatar {
        background: var(--card-bg-2);
        border-radius: 1px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    #content .table tbody tr a[href*="user"] {
        color: #446CB3 !important;
    }

    #content .table tbody tr .well {
        background: var(--header-bg);
        border: 1px solid var(--border-color-2);
        box-shadow: none;
        padding: 5px 8px !important;
    }

    #content .table tbody tr .post_edit_button,
    .btn.btn-sm.btn-warning.btn-spoiler {
        background-image: none;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: bold;
        text-shadow: none;
    }

    #content .table tbody tr .post_edit_button *,
    .btn.btn-sm.btn-warning.btn-spoiler {
        color: #FFF !important;
    }

    #content .text-center .btn {
        background: #446CB3;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: bold;
        padding-bottom: 7px !important;
    }

    #content .text-center .btn * {
        color: #FFF;
    }

    .report-button.btn.btn-sm.btn-secondary.text-warning {
        background: #FF6B6B !important;
        padding-bottom: 7px !important;
    }

    /* MANGA READER */

    /** DESCRIPTION : change the elements of the manga reader page **/
    .reader-controls-wrapper {

        /*** MANGA READER SIDEBAR ***/
        background: var(--card-bg) !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        height: 104px !important;
        width: 100% !important;
        padding-top: 10px !important;
        position: absolute !important;
        top: 62px !important;
        left: 0px !important;
    }

    .reader-controls-wrapper * {
        border: none !important;
    }

    .reader-controls-wrapper .reader-controls-title {
        max-width: 70%;
        line-height: 25pt;
        padding-left: 25px !important;
        text-align: left !important;
    }

    .reader-controls-wrapper .reader-controls-title .manga-link {
        font-size: 16pt !important;
    }

    .reader-controls-wrapper .reader-controls-title .lang-flag {
        margin-top: -4px;
        margin-right: 4px;
    }

    .reader-controls-wrapper .reader-controls-title .d-none.d-lg-block {
        font-stretch: condensed;
        display: block !important;
        width: 50%;
    }

    .reader-controls-wrapper .reader-controls-title .chapter-title {
        color: var(--primary-color);
    }

    .reader-controls-chapters {
        width: 400px !important;
        position: absolute !important;
        top: 7px !important;
        right: 15px !important;
    }

    .reader-controls-chapters .d-lg-none {
        display: block !important;
    }

    #jump-chapter,
    #jump-page,
    #jump_chapter,
    #jump_page,
    #jump_group {
        background: var(--card-bg-2);
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
        color: var(--primary-color);
        font-size: 14pt !important;
        font-stretch: condensed;
    }

    #jump_chapter,
    #jump_page,
    #jump_group {
        border: none;
        height: 45px;
    }

    .reader-controls-chapters #jump-chapter:focus,
    .reader-controls-chapters #jump-page:focus {
        box-shadow: none !important;
    }

    #comment-button,
    #settings-button {
        background-image: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        color: #FFF !important;
        font-size: 14pt !important;
        width: 50px !important;
        position: fixed !important;
        right: 10px !important;
    }

    #comment-button,
    .btn.btn-success.comment_button {
        background: #019875 !important;
        bottom: 10px !important;
    }

    #comment-button * {
        color: #FFF !important;
    }

    #settings-button {
        background: #446CB3;
        bottom: 60px !important;
    }

    .modal-container .custom-checkbox .custom-control-label::before {
        background: var(--card-bg-2);
        border: 1px solid var(--border-color);
        border-radius: 2px !important;
        margin-top: 3px !important;
    }

    .modal-content .container .form-group .btn.btn-default.btn-secondary.col.px-2.active {
        background: var(--card-bg-2);
        opacity: .5;
    }

    .reader-images h2 {
        font-size: 18pt;
    }

    .reader-main {

        /*** MANGA READER CONTAINER ***/
        max-width: 75% !important;
        margin: auto;
        padding: 0px !important;
        margin-top: 120px;
    }

    .reader.layout-horizontal .reader-images {
        padding-right: 0px !important;
    }

    .reader-main img {
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
    }

    /* QUICK SEARCH */

    /** DESCRIPTION : change the elements of the search page **/
    .col-form-label {
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .form-control {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: var(--primary-color);
    }

    .form-group.row .btn-group a {
        background: var(--card-bg-2) !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        color: var(--primary-color);
    }

    .form-group.row .btn-secondary:not(:disabled):not(.disabled).active {
        opacity: .5;
    }

    #group_name,
    #username {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .table-responsive {
        background: var(--card-bg);
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2) !important;
        border-bottom-right-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
        margin-bottom: 10px;
    }

    .table-responsive .table {
        box-shadow: none !important;
    }

    .table.table-striped.table-hover.table-sm * {
        border: none;
        box-shadow: none;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    .table.table-striped.table-hover.table-sm .badge,
    .table.table-striped.table-hover.table-sm .badge * {
        border-radius: 2px !important;
        color: #FFF !important;
    }

    .table.table-striped.table-hover.table-sm .badge[title*="comment"] {
        background: #2C3E50;
    }

    #search_manga {
        background: var(--card-bg);
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        padding-bottom: 1px !important;
    }

    #search_manga .border-bottom {
        border-color: var(--border-color);
        box-shadow: none !important;
        padding: 5px !important;
    }

    #search_manga .text-center .btn,
    #search_manga .text-center .btn * {
        color: #FFF !important;
    }

    /* TITLES */

    /** DESCRIPTION : change the elements of the titles page **/
    .nav-item.dropdown.ml-auto {
        margin-right: 20px;
    }

    .clearfix.mb-3,
    .row.mt-1.mx-0 {
        background: var(--card-bg);
        border-bottom-right-radius: 2px !important;
        border-bottom-left-radius: 2px !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        padding: 10px 12px;
        margin-top: 0px !important;
    }

    .clearfix.mb-3 {
        text-align: center !important;
    }

    #content .border-bottom {
        background: var(--card-bg);
        border-color: var(--border-color) !important;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        padding: 5px;
    }

    .border-bottom.d-flex.align-items-center,
    .chapter-container .border-bottom,
    .col-lg-6.border-bottom.pl-0.my-1,
    .col-md-6.border-bottom.p-2,
    .reader-images .border-bottom,
    .text-truncate.border-bottom {
        box-shadow: none !important;
    }

    #content .border-bottom .badge {
        background: #2C3E50;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
    }

    #content .border-bottom .badge,
    #content .border-bottom .badge * {
        color: #FFF !important;
        font-size: 12pt;
    }

    .row.mt-1.mx-0 *,
    #content .border-bottom.d-flex * {
        border-color: var(--border-color) !important;
        color: var(--primary-color);
        font-size: 14pt;
        font-stretch: condensed;
    }

    /* SEARCH */

    /** DESCRIPTION : change the elements of the search page **/
    #search_titles_form input {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
    }

    .dropdown.bootstrap-select.form-control {
        background: transparent !important;
    }

    #search_titles_form .custom-checkbox .custom-control-label::before,
    #manga_add_form .custom-checkbox .custom-control-label::before {
        background: var(--card-bg-2);
        border: 1px solid var(--border-color);
        border-radius: 2px !important;
        margin-top: 10px !important;
    }

    #search_titles_form .custom-checkbox .custom-control-input:checked ~ .custom-control-label::after,
    #manga_add_form .custom-checkbox .custom-control-input:checked ~ .custom-control-label::after {
        color: var(--primary-color) !important;
        margin-top: 10px !important;
    }

    #content .large_logo.rounded.position-relative.mx-1.my-2 .car-caption.px-2.py-1 {
        background: var(--card-bg-2);
        box-shadow: none;
        height: 110px !important;
    }

    .btn-group.btn-group-xs.dropup button {
        background-color: var(--card-bg);
        background-image: none;
        border: none !important;
        border-radius: 2px !important;
        box-shadow: none;
        padding: 5px;
        margin-top: 4px;
    }

    .btn-group.btn-group-xs.dropup button * {
        color: var(--primary-color) !important;
        text-shadow: none !important;
    }

    .btn-group.btn-group-xs.dropup button .fas {
        filter: brightness(0) var(--invert);
    }

    .btn-group.btn-group-xs.dropup .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 15px !important;
        width: 15px !important;
        position: relative;
        top: 6px;
        filter: brightness(0) var(--invert) !important;
    }

    .btn-group.btn-group-xs.dropup .btn.btn-secondary.dropdown-toggle.dropdown-toggle-split:focus {
        background: var(--card-bg) !important;
        box-shadow: none !important;
    }

    /* ADD */

    /** DESCRIPTION : change the elements of the add page **/
    form[id*="_add_form"] input,
    form[id*="_add_form"] textarea {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
    }

    .form-control:focus {
        background: var(--card-bg-2);
        box-shadow: none;
    }

    /* FORUM */

    /** DESCRIPTION : change the elements of the forum **/
    .breadcrumb {
        background: var(--card-bg);
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        padding-top: 10px;
    }

    .btn.btn-secondary.new_thread_button {
        background: #019875 !important;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .2);
        font-weight: bold;
        padding-bottom: 8px;
    }

    .btn.btn-secondary.new_thread_button * {
        color: #FFF !important;
    }

    #subject {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1);
    }

    #start_thread_button {
        background: #019875 !important;
    }

    .table thead th {
        border: none;
    }

    #content .table.table-md.edit tr:nth-of-type(2n) {
        background: var(--card-bg-2) !important;
    }

    .edit tr[id*="post_edit"] .form-group button {
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: var(--primary-color) !important;
        margin-bottom: 7px;
        margin-left: 3px !important;
    }

    .edit #text {
        background: var(--card-bg);
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
    }

    .btn.btn-secondary.post_reply_button {
        background: #019875 !important;
        border: none;
        border-radius: 2px;
        box-shadow: 0px 1px 2px rgba(0, 0, 0, .1) !important;
        color: #FFF !important;
        font-size: 14pt;
        font-stretch: condensed;
        font-weight: bold;
    }

    /* GROUPS */

    /** DESCRIPTION : change the elements of the groups page **/
    #content .card .table.table-sm {
        box-shadow: none !important;
    }

    #content .card .table.table-sm th {
        border: none !important;
    }

    /* USERS */

    /** DESCRIPTION : change the elements of the users page **/
    .card .card-body .btn.btn-secondary[href*="message"] {
        background: #5CB85C;
    }

    #friend_add_button {
        background: #337AB7;
    }

    #user_block_button {
        background: #FF6B6B;
    }

    /* MDLIST */

    /** DESCRIPTION : change the elements of the mdlist page **/
    .nav-tabs .dropdown.ml-auto .dropdown-toggle::after {
        background: no-repeat url("https://i.imgur.com/OygFMxF.png") center;
        background-size: cover;
        border: none;
        height: 10px !important;
        width: 10px !important;
        position: absolute;
        top: 18px;
        right: 4px;
        filter: brightness(0) var(--invert) !important;
    }

    /* MESSAGE */

    /** DESCRIPTION : change the elements of the message page **/
    .table.table-striped.table-md {
        border-top-right-radius: 1px !important;
        border-top-left-radius: 1px !important;
    }

    .table.table-striped.table-md tr:nth-of-type(1) {
        background: var(--card-bg);
    }

    #msg_send_form input {
        background: var(--card-bg-2);
        border: none;
        border-radius: 2px;
    }

    #msg_send_form input:focus {
        box-shadow: none;
    }

    /* INFO */

    /** DESCRIPTION : change the elements of the info page **/
    #content h3 {
        font-size: 16pt;
    }

}

@-moz-document regexp("https://mangadex.org/updates.*") {

    /* UPDATES */

    /** DESCRIPTION : change the elements of the updates page **/
    .table thead th:nth-of-type(1) {
        padding-left: 10px;
    }

    .table td {
        padding: 0px !important;
    }

}
