css:
@-moz-document domain(bootcampspot-v2.com) {
	:root {
		--tech-gold: #b3a369;
		--buzz-gold: #eaaa00;
		--buzz-gold-light: #f5d580;
		--tech-blue: #003057;
		--tech-blue-link: #004f9f; /* TODO: Replace #4275bd */
		--tech-blue-link-hover: #1879db;
		--dark-gray: #343434;
		--medium-gray: #808080;
		--darkest-gray: #181818;
		--light-gray: #c0c0c0;
	}

	[data-component="login"] button,
	[data-component="login"] p a {
		color: var(--tech-gold) !important;
		border-color: var(--tech-gold);
	}
	[data-component="login"] button:hover {
		border-color: var(--tech-gold);
		background-color: rgba(179, 163, 105, 0.15) !important;
	}
	#main-content {
		background-image: url("data:image/png;base64,") !important;
	}
	body {
		background-image: url("data:image/png;base64,") !important;
	}
	html
		body
		div#root.page
		div
		section.page
		div.row.max-width-none
		div.col-xs-12.col-sm-9.col-md-6.col-lg-4.padding-6.min-height-100 {
		background-color: #181818;
		background-image: url("data:image/png;base64,") !important;
	}
	.title {
		color: #c0c0c0;
	}
	.university-logo {
		background-color: var(--tech-gold);
	}
	.bcs-navigation {
		box-shadow: none !important;
		background-color: #181818 !important;
		border-right: 1px solid #343434;
		color: var(--tech-gold) !important;
	}
	a:focus {
		outline: none !important;
	}
	.course-list nav ul li a {
		color: var(--tech-gold) !important;
		text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000,
			0.1px 0.1px 0 #000;
	}
        .course-list nav ul li a:hover {
		color: var(--buzz-gold-light) !important;
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.row.card-group
		div.col-xs-12
		div.card-with-title.card-wide
		div.card-title
		p.margin-b-1
		span.card-title-month.text-primary {
		color: var(--tech-gold);
	}
	.course-list nav ul li .active {
                color: var(--buzz-gold-light) !important;
		background-color: #000 !important;
		border-right-color: var(--buzz-gold) !important;
		border-right-width: 20px;
	}
	header {
		background-color: #181818;
	}
	header.bcs-header {
		color: #c0c0c0 !important;
		background-color: #181818 !important;
		border-bottom: 1px solid #343434 !important;
	}
	[data-component="header"] {
		color: #c0c0c0 !important;
	}
	html body div#app header.row {
		background-color: #181818 !important;
		box-shadow: none !important;
		border-bottom: 1px solid #343434;
	}
	.bcs-app-content {
		background-image: url("data:image/png;base64,") !important;
		color: #c0c0c0 !important;
	}
	.card-wide {
		background-color: #181818;
		border-color: #343434 !important;
		box-shadow: none;
	}
	.bcs-calendar {
		background-color: #181818;
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		header.bcs-header
		nav.header-nav
		ul
		li.header-notifications
		i.fas.fa-bell.fa-fw.notification-icon.link-with-unread {
		color: var(--tech-gold) !important;
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		header.bcs-header
		nav.header-nav
		ul
		li.hide-xs.show-md
		a {
		color: var(--tech-gold) !important;
	}
	html
		body
		div#app
		header.row
		nav.col-xs-9.col-no-gutter.text-right
		ul
		li
		a.text-white {
		margin-right: 100px;
	}
	select {
		background-color: #181818 !important;
		color: var(--tech-gold) !important;
		border-color: var(--darkest-gray);
		height: 37px;
	}
	select:focus {
		border-color: var(--tech-gold) !important;
	}
    #liveagent_online {
		color: var(--tech-gold) !important;
		background-color: var(--darkest-gray);
	}
    #liveagent_online:hover {
		color: var(--buzz-gold-light) !important;
		background-color: var(--darkest-gray);
	}
    #liveagent_offline {
		color: var(--tech-gold) !important;
		background-color: var(--darkest-gray);
        border: 1px solid var(--dark-gray);
        box-shadow: none;
	}
    #liveagent_offline:hover {
		color: var(--buzz-gold-light) !important;
		background-color: var(--darkest-gray);
        border: 1px solid var(--dark-gray);
        box-shadow: none;
	}
	.chat-close .fas {
		color: var(--tech-gold);
	}
	.chat-close .fas:hover {
		color: var(--buzz-gold-light);
	}
	
	html
		body
		div#root.page
		div
		section.bcs-application
		div#sf-liveagent-widget
		div#liveagent_offline.live-chat.unavailable.active
		div.chat-panel
		a
		button.btn-small {
		background-color: var(--tech-gold) !important;
		color: var(--tech-blue) !important;
		border-color: var(--tech-blue);
	}
	html
		body
		div#root.page
		div
		section.bcs-application
		div#sf-liveagent-widget
		div#liveagent_offline.live-chat.unavailable.active
		div.chat-panel
		a
		button.btn-small:hover {
		background-color: var(--buzz-gold-light) !important;
		color: var(--darkest-gray) !important;
		border: 1px solid var(--dark-gray);
	}
	time .card-title-month,
	.card-title-date {
		color: var(--tech-gold);
	}
	.bcs-footer {
		color: var(--darkest-gray);
		background-color: #111111 !important;
		border-top: 1px solid #343434 !important;
		box-shadow: none;
	}
	.card {
		background-color: #181818;
		border-color: #343434 !important;
		box-shadow: none;
	}
	.card-skinny {
		background-color: #181818;
		border-color: #343434 !important;
		box-shadow: none;
	}
	.card-with-title {
		background-color: #181818;
		border-color: #343434 !important;
		box-shadow: none;
	}
	.card-footer a {
		color: var(--tech-gold);
	}
	.card-footer a:hover {
		color: var(--buzz-gold-light) !important;
		border: none;
	}
	.card-footer ul li a {
		border: none;
		color: var(--tech-gold) !important;
	}
	.card-footer ul li a:hover {
        border: none;
	}
	.card-title-month {
		color: var(--tech-gold);
	}
	ol li:before {
		color: #181818;
		background-color: var(--tech-gold);
	}
	li.calendar-event-session a {
		color: #fff !important;
		background-color: var(--tech-blue) !important;
	}
	li.calendar-event-due a {
		color: #fff !important;
		background-color: var(--tech-gold) !important;
	}
	li.calendar-event-assigned a {
		color: #fff !important;
		background-color: var(--buzz-gold) !important;
	}
	.header-nav ul li a,
	.header-nav ul li i {
		color: #c0c0c0 !important;
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.row
		div.col-xs-12.col-md-4.first-xs.last-md.col-no-gutter.padding-l-2-md
		div.row
		div.col-xs-12
		div.card-with-title.card-skinny
		div.card-title
		p.card-title-month {
		color: var(--tech-gold);
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.row
		div.col-xs-12.col-md-4
		ul
		li
		a {
		color: var(--tech-gold);
		border-bottom: 1px solid var(--tech-gold);
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.student-grade-info
		table.bcs-table
		tbody
		tr.font-sm
		td
		a.btn.btn-small.btn-save {
		border-color: var(--tech-gold);
		color: var(--tech-gold);
		-moz-box-shadow: none;
		-webkit-box-shadow: none;
		box-shadow: none;
	}
	html
		body
		div#root.page
		main
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.row
		div.row
		div.col-xs-12.col-md-8
		div.card
		ol
		li:before {
		background-color: var(--tech-gold);
		color: #181818;
	}
	h2 {
		color: #c0c0c0;
	}
	h1 {
		color: var(--tech-gold);
		text-shadow: 3px 3px 0 #000, -1px -1px 0 #000, 1px -1px 0 #000,
			-1px 1px 0 #000, 1px 1px 0 #000;
	}
	[data-component="alert"] {
		color: var(--tech-gold);
		border-color: var(--tech-gold);
	}
	.context-warn {
		color: var(--tech-gold);
		border: 1px solid var(--tech-gold);
	}
	thead {
		background-color: transparent;
	}
	td {
		background-color: #181818;
		border: none;
	}
	.btn {
		background-color: #181818;
		color: #c0c0c0;
		-webkit-box-shadow: none;
		box-shadow: none;
	}
	.btn-sm i.fas:before {
		color: #c0c0c0;
	}
	.btn-primary {
		background-color: #4275bd;
	}
	.calendar-event-session a {
		background-color: #4275bd;
	}
	input {
		background-color: #181818 !important;
		color: #c0c0c0 !important;
	}
	input:focus {
		border-color: var(--tech-gold);
		background-color: #181818 !important;
		color: #c0c0c0 !important;
	}
	textarea {
		background-color: #181818 !important;
		color: #c0c0c0 !important;
	}
	textarea:focus {
		border-color: var(--tech-gold) !important;
	}
	button {
		background-color: #181818 !important;
		color: #c0c0c0 !important;
		box-shadow: none;
		text-decoration: none !important;
	}
	a.btn:hover {
		background-color: #222222 !important;
	}
	.btn-delete {
		color: #ad324a !important;
		border: 1px solid #ad324a !important;
	}
	.btn-delete:hover {
		color: #ff0000 !important;
		border: 1px solid #ff0000 !important;
	}
	.request-absence-link {
		color: #fff !important;
		padding: 0.3rem 1.1rem;
		border-radius: 3px;
		border: 1px solid var(--tech-gold);
		outline: none;
		max-width: 186px;
	}
	.request-absence-link a {
		color: var(--tech-gold) !important;
	}
	.request-absence-link:hover {
		background-color: rgba(179, 163, 105, 0.15);
	}
	[data-component="career-services"] .col-md-4:last-child {
		background-color: #181818;
		border: 1px solid #343434;
		border-radius: 3px;
		margin-left: 17px;
	}
	li a {
		color: var(--tech-gold) !important;
		border-color: var(--tech-gold) !important;
	}
	html
		body
		div#app
		div.bcs-app-content
		div.page
		div
		div
		div.row.center-md
		div.col-xs-12.col-md-10
		div.col-xs-12.col-md-6.col-no-gutter
		select {
		height: auto;
	}
	html
		body
		div#root.page
		div
		section.bcs-application
		main#main-content.bcs-app-content
		div.bcs-app-content-inner
		section.page
		div.row
		div.col-xs-12.col-md-8.col-no-gutter.padding-r-2-md
		div
		div#notifications.padding-t-6-xs.padding-t-0-md
		div.col-xs-12
		div.row
		div.col-xs-12.col-md-6.col-no-gutter
		select {
		margin-top: 8px;
	}}
