css:
@-moz-document domain("www.fimfiction.net") {
\\new stories list div.front_page .new_stories_outer {
    position: relative
}
div.front_page .new_stories_outer .story-card-list {
    overflow-y: scroll !important;
    overflow-x: hidden;
    height: 800px;
    max-height: none;
    scrollbar-width: thin;
}
div.front_page .new_stories_outer .story-card-list::-webkit-scrollbar-track {
    background: transparent;
    box-shadow: none
}
div.front_page .new_stories_outer .story-card-list::-webkit-scrollbar-thumb {
    border-radius: 5px
}
div.front_page .new_stories_outer .story-card-list:hover {
    overflow: auto;
}
div.front_page .new_stories_outer a.styled_button {
    z-index: 5
}

\\updated stories list div.front_page .latest_stories_outer {
    position: relative
}
div.front_page .latest_stories_outer .story-card-list {
    overflow-y: scroll !important;
    overflow-x: hidden;
    height: 800px;
    max-height: none;
    scrollbar-width: thin;
}
div.front_page .latest_stories_outer .story-card-list::-webkit-scrollbar-track {
    background: transparent;
    box-shadow: none
}
div.front_page .latest_stories_outer .story-card-list::-webkit-scrollbar-thumb {
    border-radius: 5px
}
div.front_page .latest_stories_outer .story-card-list:hover {
    overflow: auto;
}
div.front_page .latest_stories_outer a.styled_button {
    z-index: 5
}

\\popular stories list div.front_page .popular-stories-container {
    position: relative
}
div.front_page .popular-stories-container .story-card-list {
    overflow-y: scroll !important;
    overflow-x: hidden;
    height: 800px;
    max-height: none;
    scrollbar-width: thin;
}
div.front_page .popular-stories-container .story-card-list::-webkit-scrollbar-track {
    background: transparent;
    box-shadow: none
}
div.front_page .popular-stories-container .story-card-list::-webkit-scrollbar-thumb {
    border-radius: 5px
}
div.front_page .popular-stories-container .story-card-list:hover {
    overflow-y: scroll;
    overflow-x: hidden;
}

\\related story lists div.tab {
    position: relative;
    padding: 1px;
}
div.tab {
    overflow-y: scroll !important;
    overflow-x: hidden;
    height: 800px;
    max-height: none;
    z-index: 1;
    scrollbar-width: thin;
}
div.tab::-webkit-scrollbar-track {
    background: transparent;
    box-shadow: none
}
div.tab::-webkit-scrollbar-thumb {
    border-radius: 5px
}
\\chapter list scroller ul.chapters {
    position: relative;
    padding: 1px;
}
ul.chapters {
    overflow-y: scroll !important;
    overflow-x: hidden;
    min-height: 45px;
    max-height: 300px;
    z-index: 1;
    scrollbar-width: thin;
}
ul.chapters::-webkit-scrollbar-track {
    background: transparent;
    box-shadow: none
}
ul.chapters::-webkit-scrollbar-thumb {
    border-radius: 5px
}
}
