css:
@-moz-document url-prefix("https://mybroadband.co.za/") {
.p-body-inner {
    max-width: 100% !important;
}


.p-footer,
.p-navSticky {
    max-width: 100% !important;
}

.p-sectionLinks {
    width: 100%;
    margin: auto;
}

.p-pageWrapper,
body {
    max-width: 100% !important;
    background-color: #ccc !important;
}

#top_menu {
    background: #ccc;
}

#mainNav {
    background: #ccc none;
    border-bottom: solid 1px #ccc;
    border-top: solid 1px #ccc;
}

.button.button--cta, a.button.button--cta {
    -moz-filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    filter: gray;
    /* IE6-9 */
    filter: grayscale(100%);
}

.message-newIndicator {
    -moz-filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    filter: gray;
    /* IE6-9 */
    filter: grayscale(100%);
}

.structItemContainer {
background: #eee;
}

.block--messages .message, .block--messages .block-row {
    background: #eee;
}

.message-cell.message-cell--user, .message-cell.message-cell--action {
background: #e4e3e3;
}

.block-container {
    background: #eae8e8;
}

.bbCodeBlock {
    border: 1px solid #dfdfdf;
    border-left: 3px solid #747474;
}

.bbCodeBlock-title {
    color: #656565;
    background: #fafafa;
}

.bbCodeBlock-expandLink a {
    color: #656565;
}

.p-nav-scroller {
    margin-right: auto;
    max-width: 100%;
}

.pageNavWrapper {
    -moz-filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    filter: gray;
    /* IE6-9 */
    filter: grayscale(100%);
}
.p-nav-inner {
    max-width: 100%;
}

.p-sectionLinks-inner {
    max-width: 100%;
    background: #ddd;
}

.p-description {
    color: #000;
}

.pageNav-page.pageNav-page--current {
    background: #fff;
    color: #000;
    border: 1px solid #6c6c6c;
}

.p-navSticky,
.p-sectionLinks {
    width: 100%;
    background: #ccc none;
}

.p-footer-inner {
    max-width: 1020px;
    background: #ccc none;
}

.p-footer-row {
    margin-bottom: -10px;
    background: #888 none;
    padding: 5px 5px 0px 5px;
}

.p-footer {
    background: #888;
}

.p-nav {
    background: #ccc;
}

.site-footer {
    width: 95% !important;
    margin: auto;
}

.avatar.avatar--m {
    width: 45px;
    height: 45px;
    font-size: 25px;
}

.actionBar-set.actionBar-set--internal {
    float: right;
    margin-left: -3px;
}
}
