css:
@-moz-document domain("rt.com") {
body {
	background: #111;
	color: #eee;
}

.layout__wrapper {
	background-color: #000;
}

.header__section_gray {
	background-color: #aaa;
}

.seach-form__wrapper-input-text {
	border: .1em solid #323232;
	background: #000;
}
.seach-form__input-text {
	color: #fff;
}


.main-promobox__link {
	color: #fff;
}
.main-promobox__item:first-child .main-promobox__link {
	color: #fa6161;
}
.main-promobox:hover .main-promobox__item:first-child .main-promobox__link {
	color: #fff;
}
.main-promobox__heading:hover .main-promobox__link {
	color: #fa6161 !important;
}
.main-promobox__wrapper {
	border-top: 1px solid #181818;
}
.button__link {
	border: 1px solid #3e3e3e;
	color: #fff;
	text-shadow: 0 1px 0 #000;
	background: #131313;
	background: -moz-linear-gradient(top, #131313 0%, #222222 59%, #232323 100%);
	background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #131313), color-stop(59%, #222222), color-stop(100%, #232323));
	background: -webkit-linear-gradient(top, #131313 0%, #222222 59%, #232323 100%);
	background: -o-linear-gradient(top, #131313 0%, #222222 59%, #232323 100%);
	background: -ms-linear-gradient(top, #131313 0%, #222222 59%, #232323 100%);
	background: linear-gradient(to bottom, #131313 0%, #222222 59%, #232323 100%);
}


.news-line {
	border-top: 1px solid #1f1f1f;
	border-bottom: 1px solid #1f1f1f;
}
.news-line__link {
	color: #fff;
}
.news-line__controls button {
	background-color: #000;
}
.news-line__controls button::after {
	color: #D6D6D6;
	border: 1px solid #292929;
}

.main-viralbox {
	border: .1em solid #1f1f1f;
}
.main-viralbox__heading {
	color: #fff;
}


.card__title {
	color: #fff;
}
.card-rows__content_border-retreat {
	border: 1px solid #000;
	border-bottom: 0;
}
.card__summary {
	color: #fff;
}
.card__header_color_044faa {
	color: #56a0fb;
}
.card__header_color_A00505 {
	color: #fa6161;
}
.card__header_top-news {
	color: #f94d4d;
}

.breadcrumbs__links {
	color: #56a0fb;
}
.short-url__link {
	color: #56a0fb;
	border-bottom: 0.07692308em dashed #56a0fb;
}


.columns__separator {
	background: #323232;
	background: -moz-linear-gradient(left, #323232 0%, #131313 5%, #010101 100%);
	background: -webkit-gradient(linear, left top, right top, color-stop(0%, #323232), color-stop(5%, #131313), color-stop(100%, #010101));
	background: -webkit-linear-gradient(left, #323232 0%, #131313 5%, #010101 100%);
	background: -o-linear-gradient(left, #323232 0%, #131313 5%, #010101 100%);
	background: -ms-linear-gradient(left, #323232 0%, #131313 5%, #010101 100%);
	background: linear-gradient(to right, #323232 0%, #131313 5%, #010101 100%);
}

.columns--mainpage .row__wrapper {
	background: #132735;
	background: -webkit-linear-gradient(top,#132735 0,#000 40%,#000 100%);
	background: -webkit-gradient(linear,left top,left bottom,from(#132735),color-stop(40%,#000),to(#000));
	background: -o-linear-gradient(top,#132735 0,#000 40%,#000 100%);
	background: linear-gradient(to bottom,#132735 0,#000 40%,#000 100%);
}

.columns--mainpage .article-card__title > a {
	color: #fff;
}



.subscribe__field-text {
	background: #000;
	border: 1px solid #000;
	color: #999;
}


#container_92026 #header_92026,
#container_92027 #header_92027 {
	color: #fff;
}
#container_92026 .list-container-item .title_92026,
#container_92027 .list-container-item .title_92027 {
	color: #fff;
}



.read-more {
	background: #000;
	border: 1px solid #333;
}
.text a, .summary a {
	color: #56a0fb;
}

.text blockquote p strong:first-of-type {
	color: #fff;
}

.follows-channel_youtube {
	background-color: #290000;
}
.follows-channel a {
	color: #56a0fb;
}

.read-more-big {
	border: 1px solid #333;
}
.read-more-big, .read-more-big span {
	color: #fff !important;
}

.link_hover:hover {
	color: #f53232;
}

.decorline-verticall {
	background-color: #1a1a1a;
	border-right: 1px solid #4e4e4e;
}
.decorline-verticall_corners::before {
	background-image: none;
}
.decorline-verticall_corners::after, .decorline-verticall_corners::before {
	background-color: #000;
}
.decorline-verticall_corners::after {
	background-image: none;
}


#spcv_conversation#spcv_conversation .spcv_simple-editor {
	color: #bbc2c9;
	background-color: #000;
}
#spcv_conversation#spcv_conversation .spcv_rich-editor .spcv_rich-editor-container .spcv_input-wrapper {
	background-color: #000;
}
#spcv_conversation#spcv_conversation .spcv_rich-bar {
	background-color: #000;
	border: solid #27292a;
}
#spcv_conversation#spcv_conversation .spcv_rich-editor {
	color: #bbc2c9;
}
#spcv_conversation#spcv_conversation .spcv_rich-editor .spcv_panel {
	background-color: #080808;
}
#spcv_conversation#spcv_conversation .spcv_rich-editor .spcv_panel .spcv_buttons {
	border-top: 1px solid #27292a;
}
#spcv_conversation#spcv_conversation .spcv_rich-editor .spcv_rich-editor-container {
	border: 1px solid #27292a;
}
#spcv_conversation#spcv_conversation[data-spotim-app="conversation"] {
	color: #bbc2c9;
}
#spcv_conversation#spcv_conversation a, #spcv_conversation#spcv_conversation a:link a:focus {
	color: #bbc2c9;
}
#spcv_conversation#spcv_conversation .spcv_message-details .spcv_user-link .spcv_username {
	color: #bbc2c9;
}
#spcv_conversation#spcv_conversation .spcv_show-more-replies .spcv_show-more-replies-button {
	background-color: #000;
	box-shadow: 0 0 0 1px #27292a;
}
#spcv_conversation#spcv_conversation .spcv_show-more-replies .spcv_show-more-replies-button:hover {
	box-shadow: 0 0 0 2px #27292a;
	background-color: #000;
}
#spcv_conversation#spcv_conversation .spcv_show-more-replies.spcv_last-message::after {
	background-color: #191a1a;
}

}

@-moz-document domain("spot.im") {
body {
	color: #bbc2c9;
}

.sppre_conversation-header {
	border-bottom: 1px solid #1a1919;
}

.sppre_rich-editor {
	color: #bbc2c9;
}
.sppre_rich-editor .sppre_rich-editor-container {
	border: 1px solid #404345;
}
.sppre_rich-editor .sppre_rich-editor-container .sppre_input-wrapper {
	background-color: #000;
}
.sppre_conversation-typing .sppre_details {
	background-color: #0f0e0e;
	color: #000;
}
[data-spot-im-direction]:not([data-spot-im-direction="rtl"]) .sppre_conversation-typing .sppre_details::before {
	border-right: 6px solid #0f0e0e;
}
.sppre_message-details .sppre_user-link .sppre_username {
	color: #bbc2c9;
}
.sppre_show-more-replies .sppre_show-more-replies-button {
	background-color: #000;
	box-shadow: 0 0 0 1px #27292a;
}
.sppre_show-more-replies.sppre_last-message::after {
	background-color: #1a1919;
}
.sppre_show-more-replies .sppre_show-more-replies-button:hover {
	box-shadow: 0 0 0 2px #27292a;
	background-color: #000;
}

}
