@-moz-document domain("shoreexcursioneer.com") {
/*__________________________________________________________________________*/
* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

.highlights {
	position: relative!important;
	display: block!important;
}

.add2fav,
.booknow {
	text-transform: capitalize!important;
}


/*___ Fonts ________________________________________________________________*/
#head,
#navi li,
#tabPort .Nexcursions,
.blockright ul,
.exc_description,
.excursion_title a,
.Gtext,
.highlights li,
.labelgraph,
.linkother,
.name_port span,
.Quotes,
.textcol,
body,
font,
h2,
html {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

#ul_style li,
.Descriptiontexttab,
.excursion_title a {
	font-size: 21px!important;
}

#navi li,
.blockright ul,
.coupon_code,
.descriptiontextprices,
.descriptiontitleprices,
.exc_description,
.Gtext,
.highlights li,
.linkother,
.otherstyle,
.regular_prices,
.selectp,
.table_prices .foot td,
.table_prices .z1 td,
.table_prices .z2 td,
.table_prices th,
.textcol,
.week_table,
font {
	font-size: 20px!important;
	line-height: 27px!important;
}

.content_deals,
.name_port span,
[style="color:#666;font-size:8pt;"] {
	font-size: 18px!important;
	line-height: 27px!important;
}


/*___ Remove Items _________________________________________________________*/
.imghiglights {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}


/*___ Left/Right/Top/Bottom ________________________________________________*/
.middleDiv {
	margin-left: 21rem!important;
}

.gallery {
	margin-left: -21rem!important;
}

.slideshow {
	margin-left: 32rem!important;
}

.GetStarted {
	margin-left: -25rem!important;
}

.gallery_thumbs {
	margin-top: 2rem!important;
	margin-left: 58rem!important;
}

.middleDiv div[style="position:absolute;z-index:1;margin-left:400px;margin-top:276px;"] {
	margin-left: 30rem!important;
	margin-top: 1rem!important;
}

.content_deals {
	margin-left: 19rem!important;
	margin-top: -9rem!important;
}

[style="float:left;width:60px;font-size:12pt;text-align:right;padding-right:5px;padding-top:10px;font-weight: bold;color: #137E92;"] {
	margin-left: 19rem!important;
}

.highlights {
	margin-bottom: 2rem!important;
}

.img-listing div:hover {
	top: 0!important;
}


/*___ Margins ______________________________________________________________*/
.contentleftExcursion,
.contentrightExcursion,
section {
	margin: 12px!important;
}

.blockright {
	margin: 0 auto!important;
}


/*___ Paddings _____________________________________________________________*/
.add2fav,
.booknow,
.content_excursion,
.contentleftExcursion,
.coupon_code,
.dealshome {
	padding: 12px!important;
}


/*___ Heights _____________________________________________________________*/
selector {
	Max-height: none!important;
}

#IdHomePorts .location,
.deals_link span,
.dealshome,
.img-listing,
.sareviewscontent {
	height: auto!important;
}

.gallery_thumbs img {
	height: 100%!important;
}

.gallery_heros {
	height: 75%!important;
}

.dealshome {
	min-height: 0!important;
}


/*___ Widths ______________________________________________________________*/
.highlights {
	max-width: none!important;
}

#IdHomePorts .location,
.img-listing {
	width: auto!important;
}

.blockright {
	width: 100%!important;
}

#head,
#IdHomePorts,
.contentother,
.gallery,
.slideshow,
[style="width:1000px;height:400px;"],
section,
table {
	width: 98%!important;
}

.content_excursion {
	width: 94rem!important;
}

.highlights {
	width: 93%!important;
}

#rightContent,
.contentleftExcursion {
	width: 75%!important;
}

._excursion_description,
[style*="background-size: 1000px 300px;"] {
	width: 73% !important;
}

.gallery_thumbs {
	width: 53%!important;
}

.gallery_heros,
[style="float:left;width:450px;padding:10px"] {
	width: 48%!important;
}

#menu {
	width: 47%!important;
}


.gallery_thumbs img,
.middleDiv {
	width: 30%!important;
}

.dealshome {
	width: 29%!important;
}

.otheractivities {
	width: 22%!important;
}

.contentrightExcursion {
	width: 16%!important;
}

#navi li {
	width: 11%!important;
}
}

@-moz-document domain("shoreexcursioneer.com") {
/*________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
#navi li:hover,
.coupon_code,
.rating,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
.coupon_code b,
.descriptiontextprices,
.exc_description,
.form_column2,
.GetStartedFoot,
.Gtext,
.sareview_review,
.table_prices .z2 td,
.textcol,
li,
p {
	color: #fff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Khaki ________________________________________________________________*/
#tabPort .Nexcursions,
.blockrightTitle,
.excursion_title a,
.sareview_col3 b,
.StartedTitle,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Burlywood ____________________________________________________________*/
#IdHomePorts .location span,
.assistence2,
.descriptiontitleprices,
.form_column2 b,
.labelgraph,
.Quotes,
.regular_prices,
.table_prices th,
.week_table th,
[style="color:#666;font-size:8pt;"],
h3,
h4,
p b {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Sandy Brown __________________________________________________________*/
.exc_description b,
.label_f,
.table_prices .foot td,
font[style="font-size:7pt;"],
h5,
h6 {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Blue ________________________________________________________________*/
.sareview_stars i {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Green _______________________________________________________________*/
.bs-price,
.otherstyle,
.prices_content,
.table_prices .foot td b,
.table_prices .z1 td,
[style="float:left;width:60px;font-size:12pt;text-align:right;padding-right:5px;padding-top:10px;font-weight: bold;color: #137E92;"] {
	color: #0f0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Red _________________________________________________________________*/
.regular_prices span {
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}


/*________________________________________________________________________*/
::selection {
	background: #ff9!important;
	color: #000!important;
}

pre::selection {
	background: #ff9!important;
	color: #fff!important;
}

.assistence2 b {
	color: #9cf!important;
}
}

@-moz-document domain("shoreexcursioneer.com") {
/*________________________________________________________________________*/
html,
body {
	background: #000 url(http://image.ibb.co/hYKqvw/noise_background.png)!important;
	background-attachment: fixed!important;
}


/*___ Background Gray _____________________________________________________*/
#navi {
	background-color: #060505!important;
}


/*___ Background 0000 _____________________________________________________*/
#ContentFilters,
#GetStarted1,
#GetStarted2,
#tabPort,
.add2fav,
.blockright,
.booknow,
.experiences_item,
.GetStartedFoot,
.items_port,
.sort,
.StartedTitle,
.styled-select,
.TabForm,
.TabForm2,
.table_prices .z1 td,
.table_prices th,
.week_table th {
	background: rgba(0, 0, 0, 0)!important;
}


/*___ Background-color 0000 _______________________________________________*/
#navi li,
.subtitle {
	background-color: rgba(0, 0, 0, 0)!important;
}


/*___ Highlight Background _______________________________________________*/
selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}


/*___ Faded Background bottom (Usually expand paragraph) _________________*/
selector {
	background: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(120, 224, 240, 0.13))!important;
}


/*________________________________________________________________________*/
selector {
	background-image: none!important;
}


/*________________________________________________________________________*/
pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}


/*__ Blue __________________________________________________________________*/
Selector {
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Red ____________________________________________________________________*/
Selector {
	background-image: linear-gradient(to bottom right, #7f0505, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #8f1616, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Yellow ________________________________________________________________*/
Selector {
	background-image: linear-gradient(to bottom right, #956101, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #a67212, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Light Blue ___________________________________________________________*/
.add2fav {
	background-image: linear-gradient(to bottom right, #4d87bc, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.add2fav:hover {
	background-image: linear-gradient(to bottom right, #5e98cd, #000)!important;
	color: #ff0!important;
}

.add2fav:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Orange___________________________________________________________*/
.booknow {
	background-image: linear-gradient(to bottom right, #e35121, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.booknow:hover {
	background-image: linear-gradient(to bottom right, #f46232, #000)!important;
	color: #ff0!important;
}

.booknow:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Plain Black _________________________________________________________*/
#leftContent,
.contentleftExcursion,
.contentrightExcursion,
section {
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#leftContent:hover,
.contentleftExcursion:hover,
.contentrightExcursion:hover,
section:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}


/*__ Plain ______________________________________________________________*/
#bntotal,
.content_excursion,
.dealshome,
.GetStarted,
.highlights,
.otheractivities,
.sareviewscontent {
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#bntotal:hover,
.content_excursion:hover,
.dealshome:hover,
.GetStarted:hover,
.highlights:hover,
.otheractivities:hover,
.sareviewscontent:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}


/*__ Noise _____________________________________________________________*/
header,
Footer {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

header:hover,
Footer:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}


/*__ Red Gradient ______________________________________________________*/
Selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}


/*__ Blue Gradient _____________________________________________________*/
selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}


/*__ Green Gradient ____________________________________________________*/
.coupon_code {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	text-shadow: 1px 1px #555!important;
}


/*__ Yellow Gradient ___________________________________________________*/
selector {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #ff9 0%, #000 100%)!important;
	text-shadow: 1px 1px #555!important;
}


/*__ Shadow ____________________________________________________________*/
Selector {
	box-shadow: 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("shoreexcursioneer.com") {
/*________________________________________________________________________*/
#IdHomePorts .location,
.assistence2,
.content_excursion,
.contentcruiseline,
.dealshome,
.otheractivities {
	filter: brightness(65%)!important;
}


/*________________________________________________________________________*/
#IdHomePorts .location:hover,
.assistence2:hover,
.content_excursion:hover,
.contentcruiseline:hover,
.dealshome:hover,
.otheractivities:hover {
	filter: brightness(100%)!important;
}


/*________________________________________________________________________*/
img {
	border-radius: 22px!important;
}


/*________________________________________________________________________*/
selector,
selector:first-child {
	border-top-left-radius: 22px!important;
	border-top-right-radius: 22px!important;
}

.GetStartedFoot,
selector:last-child {
	border-bottom-left-radius: 22px!important;
	border-bottom-right-radius: 22px!important;
}


/*________________________________________________________________________*/
.highlights li,
font,
li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-out, color .5s ease-out, filter .5s ease-out, filter .8s ease-out, box-shadow .125s ease-out, box-shadow .5s ease-out!important;
}

#bntotal tr,
.highlights li,
.table_prices .foot td,
.week_table tr p:hover,
font:hover,
li:hover,
p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-in, filter .5s ease-in, filter .8s ease-in, box-shadow .125s ease-in, box-shadow .5s ease-in!important;
}
}
