css:
@-moz-document url-prefix("https://www.ezeelinux.com/talk/") 
{
body {
    background: #333 !important;
    color: #fff !important;
}
#container {
    background: #333 !important;
    color: #fff !important;
}
a:link {
    color: #ffbf00 !important;
}
a:visited {
    color: #b98b00 !important;
}
a:hover,
a:active {
    color: #ffbf00 !important;
}
#container {
    color: #fff !important;
}
#logo {
    background: #333 !important;
    border-bottom: 1px solid #263c30 !important;
}
#content {
    background: #333 !important;
}
#panel .upper {
    background: #111111 !important;
    color: #fff !important;
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #000 !important;
    clear: both;
}
#panel .upper a:link,
#panel .upper a:visited,
#panel .upper a:hover,
#panel .upper a:active {
    color: #ffbf00 !important;
}
#panel .lower {
    background: #222 !important;
    color: #fff !important;
    border-top: 1px solid #333 !important;
    border-bottom: 1px solid #111 !important;
}
#panel .lower a:link,
#panel .lower a:visited,
#panel .lower a:hover,
#panel .lower a:active {
    color: #ffbf00 !important;
}
#search input.button,
#search input.textbox {
    border-color: #333 !important;
}
#search input.button {
    background: #444;
    color: #fff !important;
    border-color: #000 !important;
}
#search input.button:hover,
#search input.button:hover {
    color: #ffbf00 !important;
    border-color: #aaa !important;
}
#search input {
    margin: -3px 0;
}
#footer .upper {
    background: #111 !important;
    border-top: 1px solid #999;
    border-bottom: 1px solid #999;
    padding: 6px;
}
#footer a:link,
#footer a:visited,
#footer a:hover,
#footer a:active {
    color: #ffbf00 !important;
}
#footer .upper .language select {
    border-color: #999;
}
#footer .upper .theme select {
    border-color: #999;
}
#footer .lower {
    color: #999 !important;
}
#footer .lower a:link,
#footer .lower a:visited {
    color: #ffbf00 !important;
}
#footer .lower a:hover,
#footer .lower a:active {
    color: #ffbf00 !important;
    text-decoration: underline;
}
#footer .lower #current_time {
    color: #999 !important;
}
table {
    color: #fff !important;
    font-size: 13px !important;
}
.tborder {
    background: #111;
    border: 1px solid #111;
}
.thead {
    background: #222 !important;
    color: #ffffff !important;
    border-bottom: 1px solid #263c30;
    padding: 8px;
}
.thead a:link {
    color: #ffffff;
    text-decoration: none;
}
.thead a:visited {
    color: #ffffff;
    text-decoration: none;
}
.thead a:hover,
.thead a:active {
    color: #ffffff;
    text-decoration: underline;
}
.tcat {
    background: #222 !important;
    color: #fff !important;
    border-top: 1px solid #444 !important;
    border-bottom: 1px solid #000 !important;
}
.tcat a:link {
    color: #fff;
}
.tcat a:visited {
    color: #fff;
}
.tcat a:hover,
.tcat a:active {
    color: #fff;
}
.trow1 {
    background: #444 !important;
    color: #fff !important;
    border: 1px solid !important;
    border-color: #222 #222 #222 #222 !important;
}
.trow2 {
    background: #444 !important;
    color: #fff !important;
    border: 1px solid !important;
    border-color: #222 #222 #222 #222 !important;
}
.trow_shaded {
    background: #ffdde0 !important;
    border: 1px solid !important;
    border-color: #fff #ffb8be #ffb8be #fff !important;
}
.post.unapproved_post {
    background: #ffdde0 !important;
}
.post.unapproved_post .post_author {
    border-bottom-color: #ffb8be !important;
}
.post.classic.unapproved_post .post_author {
    border-color: #ffb8be !important;
}
.post.unapproved_post .post_controls {
    border-top-color: #ffb8be !important;
}
.trow_deleted,
.post.deleted_post {
    background: #E8DEFF !important;
}
.trow_selected,
tr.trow_selected td {
    background: #FFFBD9 !important;
    color: #333 !important;
    border-right-color: #F7E86A !important;
    border-bottom-color: #F7E86A !important;
}
.trow_selected a:link,
.trow_selected a:visited,
.trow_selected a:hover,
.trow_selected a:active {
    color: #333 !important;
}
.trow_sep {
    background: #555 !important;
    color: #fff !important;
    border-bottom: 1px solid #000 !important;
}
.tfoot {
    border-top: 1px solid #111 !important;
    background: #222 !important;
    color: #666 !important;
}
.tfoot a:link {
    color: #ffbf00 !important;
    text-decoration: none !important;
}
.tfoot a:visited {
    color: #ffbf00 !important;
    text-decoration: none !important;
}
.tfoot a:hover {
    color: #ffbf00 !important;
    text-decoration: underline !important;
}
.tfoot a:active {
    color: #ffbf00 !important;
    text-decoration: none !important;
}
.thead input.textbox,
.thead select {
    border: 1px solid #263c30;
}
.bottommenu {
    background: #efefef;
    color: #333;
    border: 1px solid #4874a3;
    padding: 10px;
}
.navigation {
    color: #fff;
    font-size: 12px;
}
.navigation a:link {
    text-decoration: none;
}
.navigation a:visited {
    text-decoration: none;
}
.navigation a:hover,
.navigation a:active {
    text-decoration: underline;
}
.navigation .active {
    color: #fff;
    font-size: small;
    font-weight: bold;
}
.smalltext {
    color: #fff !important;
    font-size: 11px !important;
}
.largetext {
    color: #fff !important;
    font-size: 16px !important;
    font-weight: bold !important;
}
fieldset.trow1,
fieldset.trow2 {
    border-color: #bbb;
}
input.textbox {
    background: #222;
    color: #fff;
    border: 1px solid #111;
}
input.textbox.portal_search {
    width: 68%;
}
textarea {
    background: #222 !important;
    color: #fff !important;
    border: 1px solid #000 !important;
}
select {
    background: #444 !important;
    color: #fff !important;
    border: 1px solid #111 !important;
}
button,
input.button {
    background: #444 !important;
    color: #fff !important;
    border-color: #000 !important;
}
button:hover,
input.button:hover {
    color: #ffbf00 !important;
    border-color: #aaa !important;
}
form {
    margin: 0;
    padding: 0;
}
input.error,
textarea.error,
select.error {
    border: 1px solid #f30;
    color: #f30;
}
input.valid,
textarea.valid,
select.valid {
    border: 1px solid #0c0;
}
label.error {
    color: #f30;
    margin: 5px;
    padding: 0px;
    display: block;
    font-weight: bold;
    font-size: 11px;
}
form #message {
    width: 500px;
}
.editor {
    background: #f1f1f1;
    border: 1px solid #ccc;
}
.editor_control_bar {
    background: #fff;
    border: 1px solid #ccc;
}
.post .editor_control_bar {
    background: #f5f5f5;
}
.popup_menu {
    background: #444 !important;
    border: 1px solid #ccc !important;
}
.popup_menu .popup_item {
    background: #444;
    color: #fff;
}
.popup_menu .popup_item:hover {
    background: #666;
    color: #fff;
}
.trow_reputation_positive {
    background: #ccffcc;
}
.trow_reputation_negative {
    background: #ffcccc;
}
.reputation_positive {
    color: green;
}
.reputation_neutral {
    color: #444;
}
.reputation_negative {
    color: red;
}
.repbox {
    font-size: 16px;
    font-weight: bold;
    padding: 5px 7px 5px 7px;
}
._neutral {
    background-color: #FAFAFA;
    color: #999999;
    border: 1px solid #CCCCCC;
}
._minus {
    background-color: #FDD2D1;
    color: #CB0200;
    border: 1px solid #980201;
}
._plus {
    background-color: #E8FCDC;
    color: #008800;
    border: 1px solid #008800;
}
img {
    border: none;
}
img.attachment {
    border: 1px solid #E9E5D7;
    padding: 2px;
}
hr {
    background-color: #fff !important;
    color: #fff !important;
    height: 1px !important;
    border: 0px !important;
}
.selectall {
    background: #FFFBD9;
    border-bottom: 1px solid #F7E86A;
    color: #333;
    text-align: center;
}
blockquote {
    background: #444 !important;
    color: #aaa !important;
    border: 1px solid #999 !important;
    margin: 0 !important;
}
blockquote cite {
    font-weight: bold;
    border-bottom: 1px solid #ccc;
}
blockquote cite > span {
    color: #666;
}
blockquote cite span.highlight {
    float: none;
    font-weight: bold;
    padding-bottom: 0;
}
.codeblock {
    background: #111 !important;
    color: #fff !important;
    border: 1px solid #000 !important;
}
.codeblock .title {
    border-bottom: 1px solid #ccc;
}
.codeblock code {
    font-family: Monaco, Consolas, Courier, monospace;
    font-size: 13px;
}
.smilie {
    vertical-align: middle;
}
.smilie_pointer {
    cursor: pointer;
}
.separator {
    margin: 5px;
    padding: 0;
    height: 0px;
    font-size: 1px;
    list-style-type: none;
}
.highlight {
    background: #FFFFCC;
}
.pm_alert {
    background: #FFF6BF;
    border: 1px solid #FFD324;
}
.red_alert {
    background: #FBE3E4;
    border: 1px solid #A5161A;
    color: #A5161A;
    text-align: center;
}
.red_alert a:link,
.red_alert a:visited,
.red_alert a:hover,
.red_alert a:active {
    color: #A5161A;
}
.high_warning {
    color: #CC0000;
}
.moderate_warning {
    color: #F3611B;
}
.low_warning {
    color: #AE5700;
}
.imminent_banned {
    color: #880000;
}
.high_banned {
    color: #FF0000;
}
.moderate_banned {
    color: #FF6600;
}
.low_banned {
    color: #008000;
}
.online {
    color: #15A018;
}
.offline {
    color: #C7C7C7;
}
div.error {
    border: 2px solid #FFD324;
    background: #FFF6BF;
}
div.error p {
    color: #333;
}
div.error p em {
    color: #C00;
}
.pagination a {
    background: #444 !important;
    color: #fff !important;
    border: 1px solid #000 !important;
}
.pagination .pagination_current {
    background: none;
    color: #aaa !important;
    border: none !important;
}
.pagination a:hover {
    background: #444 !important;
    color: #ffbf00 !important;
    border-color: #fff !important;
    text-decoration: none !important;
}
.drop_go_page {
    background: #f5f5f5;
}
.pagination_breadcrumb {
    background-color: #efefef;
    border: 1px solid #fff;
    outline: 1px solid #ccc;
}
.success_message {
    color: #00b200;
}
.error_message {
    color: #C00;
    font-weight: bold;
}
.ignored_post {
    border-top: 3px solid #333;
}
.deleted_post_hidden {
    border-top: 2px solid #ccc;
}
.deleted_post_collapsed {
    border-top: 3px solid #333;
}
.pmspace_unused {
    background: #444 !important;
    color: #fff !important;
}
.post .post_author {
    background: #222 !important;
    border-bottom: 1px solid #333 !important;
    border-top: 2px solid #333 !important;
    padding: 30px 10px 10px 10px
}
.post.classic .post_author {
    border: 1px solid #ddd;
}
.post .post_author div.author_avatar img {
    border: 1px solid #ddd;
    background: #fff;
}
.post .post_author div.author_statistics {
    float: right;
    color: #666;
}
.post.classic .post_author div.author_statistics {
    border-top: 1px dotted #ccc;
}
.post .post_head {
    background: #444 !important;
    font-size: 11px !important;
    border-bottom: 1px dotted #ddd !important;
}
.post .post_head span.post_date {
    color: #aaa;
}
.post .post_head span.edited_post {
    color: #aaa;
}
.post .post_head span.edited_post a {
    color: #aaa;
}
.post_content {
    background: #444 !important;
    padding: 9px 10px 5px 10px !important;
    ;
}
.post_content .signature {
    color: #aaa !important;
    border-top: 1px dotted #ddd !important;
}
.post .post_meta {
    color: #999;
}
.post .post_meta a:link,
.post .post_meta a:visited {
    color: #777;
}
.post .post_meta a:hover,
.post .post_meta a:active {
    color: #777;
}
.post_controls {
    clear: both;
    background: #222 !important;
    border-bottom: 1px solid #ccc !important;
}
.post_body {
    background: #444 !important;
    color: #fff !important;
    font-size: 13px !important;
}
.postbit_buttons > a:link,
.postbit_buttons > a:hover,
.postbit_buttons > a:visited,
.postbit_buttons > a:active {
    background: #444 !important;
    border: 1px solid #000 !important;
    color: #fff !important;
}
.postbit_buttons > a:visited {
    background: #444 !important;
    border: 1px solid #000 !important;
    color: #fff !important;
}
.postbit_buttons > a:hover {
    background: #444 !important;
    color: #ffbf00 !important;
    border: 1px solid #fff !important;
}
a.button:link,
a.button:hover,
a.button:visited,
a.button:active {
    background: #0f0f0f;
    color: #fff;
    border: 1px solid #000;
}
.pollbar {
    border: 1px solid #3f3f3f;
    height: 10px;
}

/** jGrowl Styling **/
.jGrowl {
    color: #ffffff;
}

/** Modal Start **/
.blocker {
    background-color: rgb(0, 0, 0);
    background-color: rgba(0, 0, 0, 0.75);
}
.modal {
    background: #fff;
    -webkit-box-shadow: 0 0 10px #000;
    -moz-box-shadow: 0 0 10px #000;
    -o-box-shadow: 0 0 10px #000;
    -ms-box-shadow: 0 0 10px #000;
    box-shadow: 0 0 10px #000;
}

/** Modal End **/

