css:
@-moz-document domain("canvas.uccs.edu") {
/* Dark canvas */
:root {
    --background: #091e2d;
    --background-light: #113047;
    --text-main: #dbdbdb;
    --text-secondary: #cfcfcf;
    --button-background: var(--background-light);
    --background-lighter: #465c6d;
    --link-color: #5fc2ff;
    --background-even-lighter: #b3b5be;
}

a[title="Starfish"] {
    display: none !important;
}

body,
.ic-app-header,
#breadcrumbs {
    background: var(--background);
    color: var(--text-main);
}

.ic-Dashboard-header__layout {
    background: var(--background-light);
    color: var(--text-main);
    opacity: 0.95;
}

[name=IconMore] {
    color: var(--text-secondary);
}

a {
    color: var(--link-color);
}

input, select, .ac-input-box {
    color: var(--text-main) !important;
    background: var(--background-lighter) !important;
}

/* Left nav */
.menu-item.ic-app-header__menu-list-item,
#global_nav_help_link {
    background: var(--background);
}
.ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-app-header__menu-list-link {
    background: var(--background-light);
}
.ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-icon-svg {
    fill: var(--text-secondary);
}
.primary-nav-expanded .menu-item__text {
    color: var(--text-secondary) !important;
}



/* Cards on dashboard */
.ic-DashboardCard,
.ic-DashboardCard__header_content {
    background: var(--background-light);
}
.ic-DashboardCard__action {
    color: var(--text-secondary);
}
.ic-DashboardCard__header-subtitle.ellipsis,
.ic-DashboardCard__header-term.ellipsis {
    color: var(--text-secondary);
}

/* Color is set on the element itself, can't override, so just remove. */
.ic-DashboardCard__header-title {
    display: none;
}


/* Dashboard side column */
.Button,
button,
.btn {
    background: var(--button-background) !important;
    color: var(--text-main) !important;
}
.ToDoSidebarItem {
    color: var(--text-secondary);
}



/* Do we really need to see the branding? */
.ic-app-footer {
    display: none;
}



/* In a course */
.mini_calendar .day.today, .mini_calendar .day.has_event {
    background: var(--background-lighter);
}
.mini_calendar .day,
.mini-cal-month-and-year {
    color: var(--text-main);
}

.list-view > ul > li > a,
.list-view > nav > ul > li > a {
    background: var(--background-light);
    color: var(--text-secondary);
}
.list-view a.active {
    background: var(--background-lighter);
    color: var(--text-main)
}
.list-view>ul>li>a:hover, .list-view>ul>li>a:focus, .list-view>nav>ul>li>a:hover, .list-view>nav>ul>li>a:focus {
    background: var(--background-lighter);
}
#syllabus tr.date.related td, #syllabus tr.date.related th, #syllabus tr.date.selected td, #syllabus tr.date.selected th {
    background: var(--background-lighter);
}

table.summary td, table.summary tbody th {
    background: var(--background-light);
    color: var(--text-secondary);
}

/* Files in course */
.ef-item-row:hover {
    background: var(--background-lighter);
}

#breadcrumbs a, .ef-plain-link, .ef-plain-link:hover {
    color: var(--text-main) !important;
}
.tree i[class*=icon-], .tree i[class^=icon-] {
    color: var(--text-secondary);
}

/** Assignments */

/* Show by */
._1clzBoF { color: var(--text-secondary); }

.header-bar,.item-group-container {
    background: var(--background);
}
.accessible-toggler, .ig-list .ig-row a.ig-title {
    color: var(--text-main);
}
.ig-list .ig-row:hover {
    background: var(--background-light);
}
.ig-list .ig-row {
    color: var(--text-secondary);
}
.item-group-condensed .ig-row {
    background: var(--background);
}
.ag-header-controls .tooltip_link {
    color: var(--text-secondary);
}

/* Grades */
._2xB3_kf, legend._2xB3_kf, label._2xB3_kf {
    color: var(--text-main);
}
#grades_summary th.title .context {
    color: var(--text-secondary);
}
.ic-Table.ic-Table--hover-row tbody tr:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-neutral:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-success:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-alert:hover, .ic-Table.ic-Table--hover-row tbody tr.ic-Table__row--bg-danger:hover {
    background: var(--background-lighter);
}

/* People */

.ui-tabs .ui-tabs-panel {
    background: var(--background);
}
.ic-Table.ic-Table--striped tbody tr:nth-child(odd) {
    background: var(--background-light);
}
.ic-Table.ic-Table--striped tbody tr:hover {
    background: var(--background-lighter);
}
.ui-tabs .ui-tabs-nav li.ui-tabs-active a {
    color: var(--text-main);
    background: var(--background-light);
}
.ui-tabs .ui-tabs-nav li.ui-state-hover, .ui-tabs .ui-tabs-nav li:hover {
    background: var(--background-lighter);
}

/* Help */
._22OAa8u {
    background: var(--background);
}
._3jLMUSh._2QIthAo._3F3lDiw._2WOMSfA {
    color: var(--text-secondary);
}


#right-side .events_list .event-details:after, #right-side .events_list .todo-details:after, #right-side .to-do-list .event-details:after, #right-side .to-do-list .todo-details:after {
    background: linear-gradient(to right, rgba(255,255,255,0) 0%, var(--background-light) 80%)
}

/* Calendar */

#calendar-app .fc-month-view .fc-body, #calendar-drag-and-drop-container .fc-month-view .fc-body, #minical {
    background-color: var(--background-light);
}
#calendar-app .fc-month-view .fc-today, #calendar-drag-and-drop-container .fc-month-view .fc-today {
    background-color: var(--background-lighter);
}
#calendar-app .fc-event, #calendar-drag-and-drop-container .fc-event {
    background-color: var(--background-even-lighter);
}
#minical .fc-widget-content {
    border-color: var(--background-lighter);
    color: var(--text-main);
}
#context-list-holder, #undated-events, #undated-events .event {
    background: var(--background);
    color: var(--text-main);
}

/* Inbox */

.message-list .messages>li {
    background: var(--background-light);
}
.message-list .author, .message-list .subject {
    color: var(--text-main);
}
.message-list .date, .message-list .summary {
    color: var(--text-secondary);
}
.message-list .messages>li.active {
    background: var(--background-lighter);
}
.conversations .panel {
    background: var(--background-light)
}

/* Notifications */
thead th {
    background: var(--background-light);
}

/* Settings */
.profile_table .data_description, #right-side p.email_channel, .feature-flags {
    color: var(--text-secondary);
}
.feature-flag .element_toggler, .feature-flag .element_toggler:hover {
    color: var(--text-secondary);
}
}
