css:
@-moz-document domain("bbs.18wos.org") {
/*Modern Style Declaration*/
* {
	box-shadow: none !important;
	word-break: keep-all;
	word-spacing: normal;
	word-wrap: normal;
}
img {
	border: 0px;
}
button {
	outline: none !important;
}
	button:focus {
		outline: none !important;
	}
cite {
	font-weight: normal !important;
}

html[xmlns*="xhtml"] > body {
	margin: 0px;
	margin-bottom: 36px;
	border: 0px;
	padding: 0px;
	background: rgb(64, 64, 64);
	background-attachment: fixed;
	background-position: center top;
	background-repeat: no-repeat;
	background-size: auto auto;
	font: 10pt Verdana, Helvetica, sans-serif;
}

/*Logo*/
	/*VeFans*/
		/*Top*/
		html[xmlns*="xhtml"] a[href="/"] img[src*="logo"] {
			width: 218px;
			height: 56px;
		}
		html[xmlns*="xhtml"] img[src*="logo_tm"] {
			display: block;
			content: "";
			width: 218px;
			height: 56px;
			background: url(http://bbs.18wos.org/images/NewArk/logo.png);
			background-position: center top;
			background-repeat: no-repeat;
			background-size: 218px 56px;
		}
		/*Bottom*/ 
		img[src*="logo3.gif"] {
			display: none;
		}
	/*Chungtsai*/
	a[href="https://weibo.com/chungtsai"] {
		display: none;
	}

/*Navigation Bar*/
html[xmlns*="xhtml"] > body > div > a[href="/"][title="模載聯合支援站"] {
	display: block;
	position: fixed;
	z-index: 26;
	margin: 0px;
	border: 0px;
	padding: 0px;
	top: 3px;
	left: 0px;
	width: 218px;
	height: 56px;
}
#topside.topside {
	display: block;
	box-sizing: border-box;
	position: sticky;
	z-index: 25;
	margin: 0px;
	margin-bottom: 8px;
	border: 0px;
	padding: 0px;
	padding-left: 218px;
	width: 100%;
	height: 64px !important;
	top: 0px;
	background: rgba(107, 192, 13, 0.9);
	color: rgb(255, 255, 255);
	font-weight: normal;
}
#topside.topside .backindex {
	z-index: 26;
	margin: 0px;
	line-height: normal;
}
#topside.topside .backindex img[src*="bindex.gif"] {
	display: none;
}
#topside.topside .backindex:nth-child(1) {
	display: block;
	position: fixed;
	top: 4px;
	left: 218px;
}
#topside.topside .backindex:nth-child(2) {
	display: none;
}
#topside.topside > div:nth-child(1) {
	overflow-x: hidden;
	overflow-y: hidden;
}
#topside.topside > div:nth-child(2) {
	padding-top: 0px !important;
	height: 44px !important;
}
#topside.topside a, #topside.topside li a {
	color: rgb(255, 255, 255);
	font-weight: normal;
}
#topside.topside #menu ul {
	margin: 0px;
	margin-right: 2px;
	padding: 0px;
}
#topside.topside #menu ul li a {
	margin: 0px;
	padding: 4px 6px;
}
#topside.topside #menu ul li cite a[href^="space.html"] {
	display: none;
}
#topside.topside #menu li.hover, #topside.topside #menu li.current {
	border: 0px;
	background-color: rgb(72, 128, 9);
	color: rgb(255, 255, 255);
}
#topside.topside #menu ul li.hover a {
	padding: 4px 6px;
	background-color: rgb(72, 128, 9);
}
#topside.topside #nav a[href="/"] {
	display: none;
}
#topside.topside #userinfo {
	margin: 0px;
	margin-left: 5px;
	margin-top: 6px;
	padding: 0px;
	color: rgb(255, 255, 255);
}
#topside.topside #userinfo #nav {
	margin: 0px;
}
#topside.topside #userinfo p {
	display: none;
}
#topside.topside #userinfo form#loginform {
	margin-top: -10px;
}
#topside.topside #userinfo form#loginform input {
	border: 0px;
	width: 128px;
	height: 20px;
}
#topside.topside .searchbar {
	margin: 0px;
	margin-top: -6px !important;
	margin-right: 8px;
	padding: 0px;
	height: auto;
}
#topside.topside .searchbar input#srchtxt {
	border: 0px;
	box-sizing: border-box;
	height: 28px;
}
#topside.topside .searchbar button {
	margin: 0px;
	border: 0px;
	padding: 0px;
	box-sizing: border-box;
	height: 28px;
}

/*Body*/
.wrap {
	width: 98%;
}

/*Footer*/
.blq-gvl-3 {
	display: block;
	box-sizing: border-box;
	position: fixed;
	z-index: 25;
	margin: 0px;
	border: 0px;
	padding: 2px 4px;
	width: 100% !important;
	height: 38px;
	bottom: 0px;
	background: rgba(107, 192, 13, 0.9);
	color: rgb(255, 255, 255);
	font-weight: normal !important;
	line-height: normal !important;
}
.wrap .blq-gvl-3 {
	margin-left: -1%;
}
.blq-gvl-3 #blq-container-inner, .blq-gvl-3 #blq-acc, .blq-gvl-3 #blq-mast-h {
	height: auto;
}
#footer {
	margin: 0px;
	padding: 0px;
	width: auto;
	height: auto;
}
#footer #footlinks, #footer #footlinks p, #footer #copyright {
	margin: 0px;
	padding: 0px;
	font: 10pt Verdana, Helvetica, sans-serif;
	line-height: normal;
}

/*Button*/
button[type=submit] {
	border: 0px;
	background: rgb(224, 224, 224);
	font-weight: normal;
}
button[name] {
	border: 0px;
	background: rgb(224, 224, 224) !important;
	font-weight: normal;
}
button[name="searchsubmit"] {
	width: 44px;
	height: 33px;
}
button[name="topicsubmit"], button#postsubmit {
	width: auto;
	height: 33px;
	color: rgb(0,0,0);
}
button[name="userlogin"], #loginform button {
	margin: 0px !important;
	padding: 0px !important;
	width: 50px !important;
	height: 28px !important;
	vertical-align: middle !important;
	font-size: 0px;
}
button[name="userlogin"]::after, #loginform button::after {
	display: inline-block;
	margin: 0px !important;
	padding: 0px !important;
	content: "登錄";
	width: 50px !important;
	height: 28px !important;
	color: rgb(0, 0, 0);
	text-align: center;
	vertical-align: middle !important;
	font-size: 10pt;
	line-height: 2em;
}

/*Popup*/
.popupmenu_popup {
	position: relative;
	background-color: rgb(72, 128, 9);
	color: rgb(255, 255, 255) !important;
}
.popupmenu_popup a {
	color: rgb(255, 255, 255);
}
.headermenu_popup li, .popupmenu_popup li, .newspecialmenu a {
	border: 0px !important;
}

.legend {
	border: 0px;
	padding: 16px 0px;
	width: 540px;
	line-height: normal;
}
.mainbox h1, .mainbox h3, .mainbox h6, .box h4, .message h1 {
	background: rgba(102, 102, 102, 1);
}

/*Forum Trending*/
#category_HS > td:nth-child(1) {
	text-align: center;
}

/*Forum List*/
.forumlist tbody th, .forumlist tbody th.new {
	background: none;
}
.forumlist th {
	padding-left: 15px !important;
}
.forumlist thead tr th, .forumlist thead tr td.nums {
	display: none;
}

/*Forum Information*/
#foruminfo {
	margin: 0px;
	padding: 0px;
	color: rgb(255, 255, 255);
	font-weight: normal;
}
#foruminfo a, #foruminfo em {
	color: rgb(255, 255, 255);
	font-weight: normal;
}
#foruminfo #announcement {
	margin: 0px;
	height: auto;
}
.forumsole img {
	outline: none;
}

/*Block*/
a[href*="londontour.18wos.org"] {
	display: none;
}
	/*Translate*/
	a#StranLink {
		display: none;
	}
	/*VeFans Social Network Service*/
	#topside.topside .weibo, #topside.topside .fb {
		display: none;
	}
	/*Hong Kong News Display Board*/
	img[src*="hkbrda"] {
		display: none;
	}
	/*Post Editor*/
	#newpost > thead:nth-child(5) {
		display: none;
	}
	tbody#adv {
		display: table-row-group !important;
	}
	/*Post Content*/
	td.postcontent, td.postcontent > div.postinfo {
		background: none !important;
	}
.bottombutton {
	display: none;
}
	/*Forum Debug*/
	#footer #debuginfo {
		display: none;
	}

td.postauthor p img[src*=common] {
	width: 28px;
	height: 28px;
}

/*Responsive*/
@media screen and (max-width: 1920px) {
	#specialvotes {
		display: block;
	}
	#weinewsblock {
		display: block;
	}
	.weinewsleft {
		float: left;
		width: 80%;
		margin-bottom: 6px;
	}
}
@media screen and (max-width: 1200px) {
	#specialvotes {
		display: none;
	}
	#weinewsblock {
		display: none;
	}
	#menu #plugin {
		display: none;
	}
	.weinewsleft {
		float: none;
		width: 100%;
		margin-bottom: 6px;
	}
	.threadpm {
		display: none;
	}
	#announcement {
		display: block;
	}
	#topside > div:nth-child(1) {
		overflow: visible;
	}
	#menu a[href^="member.html"], #menu a[href^="search.html"], #menu a[href*="viewthread.php?tid=6837"] {
		display: none;
	}
}
@media screen and (max-width: 1000px) {
	#specialvotes {
		display: none;
	}
	#weinewsblock {
		display: none;
	}
	#menu #plugin {
		display: none;
	}
	.weinewsleft {
		float: none;
		width: 100%;
		margin-bottom: 6px;
	}
	.backindex {
		display: none;
	}
	.threadpm {
		display: none;
	}
	#announcement {
		display: block;
	}
	#topside > div:nth-child(1) {
		overflow: visible;
	}
	#menu a[href^="member.html"], #menu a[href^="search.html"], #menu a[href*="viewthread.php?tid=6837"] {
		display: none;
	}
}
@media screen and (max-width: 640px) {
	#specialvotes {
		display: none;
	}
	#weinewsblock {
		display: none;
	}
	#menu #plugin {
		display: none;
	}
	.weinewsleft {
		float: none;
		width: 100%;
		margin-bottom: 6px;
	}
	.backindex {
		display: none;
	}
	.threadpm {
		display: none;
	}
	.signatures img {
		display: none;
	}
	#announcement {
		display: block;
	}
	#topside > div:nth-child(1) {
		overflow: visible;
	}
	#menu a[href^="member.html"], #menu a[href^="search.html"], #menu a[href*="viewthread.php?tid=6837"] {
		display: none;
	}
}
}
