/* ==UserStyle==
@name           egais-diagnostic
@namespace      github.com/openstyles/stylus
@version        0.1.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document regexp(".*cloud.sbis.ru/egais-diagnostics.*") {
    [name="Fsrar"] {
        width: 170px !important;
    }

    [name="SearchString"] {
        min-width: 350px !important;
    }

    [name="DocType"] {
        width: 480px;
    }

    [data-component="Lib/Control/TemplatedArea/TemplatedArea"] {
        margin-top: 7px;
    }

    .controls-InformationPopup {
        height: 100vh !important;
    }

    .controls-SubmitPopup__footer {
        position: fixed;
        bottom: 0px;
        width: 908px;
        padding: 3px;
    }

    .controls-SubmitPopup__footer:after {
        content: "Можно закрыть на ESC";
        margin-left: calc(50% - 50px);
        font-size: 10px;
    }

    .controls-SubmitPopup__body {
        padding-top: 5px;
        padding-left: 5px;
        padding-right: 5px;
        padding-bottom: 15px
    }

    .controls-SubmitPopup__message-centered {
        text-align: left;
        font-family: monospace;
        font-size: 14px;
    }

    .ws-field {
        border: 1px solid;
        border-color: #959595;
        border-bottom-color: #aab0c1;
    }

    [name="UserId"] {
        min-width: 300px;
    }

    div[name="UserId"] {
        margin-left: 16px;
    }

    [name="TransitionList"] {
        min-width: 500px;
    }

    [name="Console"] {
        margin-top: 10px;
        padding-right: 10px;
        width: 100% !important;
        min-height: calc(100vh - 170px);
    }

    [name="Console"] > div {
        min-height: calc(100vh - 170px);
    }

    [name="Console"] > div > div {
        min-height: calc(100vh - 170px);
    }

    [name="Console"] > div > div > textarea {
        min-height: 100%;
    }

    [name="DocumentStage"],
    [name="DocumentAction"] {
        border: 1px solid;
        border-color: #959595;
    }

    [name="DocumentStage"] {
        margin-left: 35px;
        max-width: 345px;
    }
}
