css:
@-moz-document domain(scryfall.com) {
.form-input 
{
	border: 1px solid #AEAEAE;
        height: 25px;
        font-size: 15px;
        padding: 0 !important;
}

.form-row-label svg
{
	display:none;
}

.form-row {
	padding: 0 !important;
}

.advanced-search-checkbox input[type=checkbox]
{
	width: 10px !important;
    height: 10px !important;
    -moz-appearance: none;
    -ms-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    background-color: #FEFEFE;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    border: 1px solid #AEAEAE;
    color: #343242;
    font-size: 20px;
    color: #5822AC;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: center;
    -webkit-justify-content: center;
    -ms-flex-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    margin: 0 8px 0 0;
    -webkit-transition: background-color 60ms linear, border-color 60ms linear, -webkit-box-shadow 60ms linear;
    transition: background-color 60ms linear, border-color 60ms linear, -webkit-box-shadow 60ms linear;
    -o-transition: background-color 60ms linear, border-color 60ms linear, box-shadow 60ms linear;
    transition: background-color 60ms linear, border-color 60ms linear, box-shadow 60ms linear;
    transition: background-color 60ms linear, border-color 60ms linear, box-shadow 60ms linear, -webkit-box-shadow 60ms linear;
}



.form-row-tip {
	display:none;
}

.form-layout .select2 {
	border: 1px solid #AEAEAE;
        padding: 0 !important;
        font-size: 15px;
        min-height: 0px;
}


.button-n.submit-n, .submit-n.select-n
{
	font-size: 15px;
    padding: 0 12px;
    height: 36px;
    margin: 18px 0 0 0;
	position: absolute;
    top: -15px;
    left: 800px;
}
}
