@-moz-document domain("imageshack.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

selector
{
	text-transform: capitalize!important;
}

selector,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
      text-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

selector
{
	font-size: 31px!important;
}

selector
{
	font-size: 21px!important;
}

header.main .logged-out a.login
{
	font-size: 19px!important;
}

selector
{
	line-height: 28px!important;
}

selector
{
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

selector
{
    top: 10px!important;
}

/*___ Margins ______________________________________________________________________*/

selector
{
	margin: 12px!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

selector
{
	padding: 12px!important;
}

header.main .logged-out a.login
{
	padding: 15px 26px!important;
}

/*___ Heights ______________________________________________________________________*/

selector
{
	max-height: none!important;
}

selector
{
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

selector
{
	max-width: none!important;
}

selector
{
	width: auto!important;
}

selector
{
	width: 98%!important;
}

selector
{
	width: 45%!important;
}

}

@-moz-document domain("imageshack.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

header.main .logged-out a.login,
body,
li,
p
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("imageshack.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/*___ Background Gray ______________________________________________________________*/

header.main
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/*___ Background 0000 ______________________________________________________________*/

selector
{
	background: #0000!important;
	background-color: #0000!important;
}

/*__ Blue __________________________________________________________________________*/

#signup-new section.page form section.bottom-btns .login-btn,

.button-large-blue,
.button-large-facebook,

header.main .logged-out nav.dropdown .login-button
{	
	background: radial-gradient(circle, #01748b 0%, #012654 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

#signup-new section.page form section.bottom-btns .login-btn::before,

.button-large-blue::before,
.button-large-facebook::before,

header.main .logged-out nav.dropdown .login-button::before
{
	background: radial-gradient(circle, #012654 0%, #01748b 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

#signup-new section.page form section.bottom-btns .login-btn:hover::before,

.button-large-blue:hover::before,
.button-large-facebook:hover::before,

header.main .logged-out nav.dropdown .login-button:hover::before {
  opacity: 1!important;
}

/*__ Yellow ________________________________________________________________________*/

#signup-new section.page form .yellow-btn
{	
	background: radial-gradient(circle, #f5bc22 0%, #2f2304 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
