	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

#signup-new section.page form .yellow-btn:hover::before
{
  opacity: 1!important;
}

/*__ Orange ________________________________________________________________________*/

.button-large-yellow
{	
	background: radial-gradient(circle, #651c03 0%, #e1410b 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem!important;
	position: relative!important;
	z-index: 1!important;
}

.button-large-yellow::before
{
	background: radial-gradient(circle, #e1410b 0%, #651c03 100%)!important;
