css:
@-moz-document domain("redux.js.org") {
*::-webkit-scrollbar {
    display: none;
}

ol, ul {
    color: #946CD0 !important;
}

table *{
    border: none !important;
}
tr, tr:nth-child(1) td {
    background: #111 !important;
    border: #222 solid 1px !important;
}
td {
    border: #222 solid 1px !important;
    background: #333 !important;
}

._lgcoiik {
    background-color: #333 !important;
}

/* Item Hover*/
._qcj0gqw,
._rgo1ysp,
._1d1cb8qj,
._ze9j219,
._1lqa3kn2 {
    border: none;
    color: #fff !important;
    font-weight: bold !important;
    background-color: #444 !important;
}
._hnaeki0 {
    box-shadow: none !important;
}
/* HEADER */

#root > div > div > div:nth-child(1) > div  {
    background-color: #111 !important;
    color: #ddd !important;
    border-bottom: 1px solid #111;
}
#root > div > div > div:nth-child(1) > div * {
    color: #ddd !important;

}


#root > div > div > div:nth-child(1) > div > div > div > div > div > div {
    background-color: #222 !important;
    border: 1px solid #222;
}

/* BODY - LEFT */
#root > div > div > div:nth-child(2) > div > div > div:nth-child(2) {
    border-right: 1px solid #222;
    background-color: #333;
}
#root > div > div > div:nth-child(2) > div > div > div:nth-child(2) svg  {
    color: #eee !important;
}
#root > div > div > div:nth-child(2) > div > div > div:nth-child(2) div  {
    background-color: #333;
    color: #ddd !important;
    
}
a[style="color: rgb(116, 76, 188);"] span {
    font-weight: bold;
    color: #946CD0;
}
/* BODY - RIGHT */
#root > div > div > div:nth-child(2) > div > div > div:nth-child(3) {
    background: #222;
}
#root > div > div > div:nth-child(2) > div > div > div:nth-child(3) a {
    background-color: #222;
    color: #946CD0;
    
}
#root > div > div > div:nth-child(2) > div > div > div:nth-child(3) div  {
    background-color: #222;
    color: #ddd !important;
}
#root > div > div > div:nth-child(2) > div > div > div:nth-child(3) h1::before {
    background-color: #333;
}


code {
     background-color: #111 !important;
}

div[contenteditable=false] div,
div[contenteditable=true] div {
     background-color: #111 !important;   
     opacity: 1;
}
pre,
pre *{
     background-color: #111 !important;
}

blockquote {
    border-color: #8CA4B8 !important;
}
blockquote * {
    color: #979FA6 !important;
}

div[role=menuitem] {
    background-color: rgba(0, 0, 0, .3) !important;
}
div[role=menuitem]:hover {
    background-color: rgba(0, 0, 0, .8) !important;
}

div[role=menuitem] span > span > span, ._nv1as6 {
    background-color: rgba(90,55,142, .4);
}
._1r8nzaup:before {
    background: linear-gradient(to bottom, rgba(245,247,249,0) 0%, #444 100%) !important;
}
}
