css:
@-moz-document domain("rbx-forum.com") {
/*Originally created by BladefuryDev - Edited by 5andtest*/
/*----------------------------------------------------*/


.forum-content-background, .forum-table-row {
    background-color: #6950b4;
}


body .forum-table-header, body .forum-table-header th, body .forum-table-footer {
    background-color: #35285b;
}


body {
    background-color: #9673ff;
    margin: 0;
}


#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    width: 970px;
    background-color: #9673ff;
}


a.linkSmallBold:visited, a.linkMenuSink:visited {
    font-weight: normal;
    color: #472b99;
}


.CenterColumn a.btn-control {
    margin-right: 5px;
    color: #262424;
    box-shadow: 0px 1px 2px 0px black;
}


.CenterColumn input.btn-control, .CenterColumn select {
    margin-right: 0px;
    box-shadow: 0 1px 2px black;
}


input[type=text] {
    border: 1px solid #3b218a;
    padding-left: 5px;
    box-shadow: 0 1px 2px #000;
}


.normalTextSmall, .normalTextSmallBold, .normalTextSmaller {
    color: #000000;
    text-shadow: 2px 4px 20px #4000ff;
}

element.style {
    box-shadow: 0 1px 7px black;
}


a.post-list-subject, a.post-list-subject:hover, a.post-list-author, a.post-list-author:hover {
    text-decoration: none;
    color: #000000;
    text-shadow: 0px 6px 20px black;
}


a.forum-summary, a.forum-summary:hover {
    text-decoration: none;
    color: #000000;
    text-shadow: 0 1px 20px black;
}




h2, h2 a:link, h2 a:visited, h2 a:active, h2 .text {
    font-size: 30px;
    font-weight: normal;
    color: #000000;
    letter-spacing: -1px;
    text-decoration: none;
    text-shadow: 0px 4px 20px #555;
}

table#ctl00_cphRoblox_PostView1_ctl00_PostList {
    box-shadow: 0 1px 13px black;
}

table#ctl00_cphRoblox_ThreadView1_ctl00_ThreadList {
    box-shadow: 0px 1px 13px black;
}

table.table {
    border-top: none;
    box-shadow: 0 1px 13px black;
}

.forumTitle {
    font-size: 1.0em;
    font-weight: bold;
    color: #191919;
}
}
