css:
@-moz-document domain("central.carleton.ca") {
body {
    margin-left: 5px;
    margin-top: 5px;
    margin-right: 5px;
    margin-bottom: 5px;
    padding: 0;
    background-color: #191818 !important;
    font-family: Arial, Helvetica, sans-serif;
}

h1 {
    font-family: Verdana, Geneva, sans-serif !important;
    font-size: 16px;
    font-weight: bold;
    color: #bf0603;
    line-height: normal;
    margin-bottom: 2px;
    margin-top: 8px;
}

p {
    font-family: Arial, Helvetica, sans-serif;
    line-height: 125%;
    color: #EAF2EF;
    font-size: 12px;
    margin: 0 0 15px;
}

ul {
    list-style-type: square;
    margin: 0;
    padding: 0 0 0 16px;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
    line-height: 1.5;
    text-shadow: 0 0 0px black;
    -webkit-text-fill-color: #fff !important;
}

a:visited {
    font-size: 12px;
    color: #fff !important;
    text-decoration: none;
}

table td.pldefault {
    font-family: verdana, arial narrow, helvetica, sans-serif;
    font-weight: normal;
    font-size: 90%;
    background-color: #191818 !important;
}

table td {
    vertical-align: top;
    color: white !important;
}

table td.mpdefault {
    color: #fff !important;
    font-family: verdana, arial narrow, helvetica, sans-serif;
    font-weight: bolder !important;
    font-size: 120% !important;
    font-style: initial;
    text-align: left !important;
    vertical-align: baseline;
}
 
element.style{
    visibility:collapse !important;
}

img {
    margin-left: 0px;
    margin-right: 0px;
    margin-top: 0px;
    margin-bottom: 0px;
    border-top-width: 0px;
    border-right-width: 0px;
    border-bottom-width: 0px;
    border-left-width: 0px;
    border-top-style: solid;
    border-right-style: solid;
    border-bottom-style: solid;
    border-left-style: solid;
    height: 0px;
    width: 53px;
}

a.submenulinktext2 {
    font-weight: normal;
    font-size: 100%;
    color: #ffffff !important;
    text-decoration: none;
}

/* Bullets: Change to white. */
.menulinkdesctext {
    color: white !important;
    font-family: verdana, arial narrow, helvetica, sans-serif;
    font-weight: normal;
    font-size: 70%;
    font-style: normal;
    text-align: left;
}

/* Change header background to dark grey */
.pageheaderlinks {
    position: absolute;
    top: 75px;
    right: 20px;
    display: block;
    padding: 5px;
    color: #000000;
    background-color: #191818 !important;
    font-family: verdana,arial narrow, helvetica, sans-serif;
    font-weight: normal;
    font-style: normal;
    font-size: 90%;
    text-align: right;
}
div.pageheaderdiv1 {
    background-color: #191818;
    width: 100%;
    text-align: left;
    margin-top: 90px;
    margin-left: 0px;
    margin-right: 0px;
    border-left: 0px solid;
    border-right: 0px solid;
    border-top: 0px solid;
}
.pageheaderlinks {
    position: absolute;
    top: 75px;
    right: 20px;
    display: block;
    padding: 5px;
    color: #ffffff !important;
    background-color: #191818 !important;
    font-family: verdana,arial narrow, helvetica, sans-serif;
    font-weight: normal;
    font-style: normal;
    font-size: 90%;
    text-align: right;
}

/* Update h1 */
h1 {
    font-family: Verdana, Geneva, sans-serif !important;
    font-size: 20px;
    font-weight: bold;
    color: #ffffff !important;
    line-height: normal;
    margin-bottom: 2px;
    margin-top: 8px;
}

}
