css:
@-moz-document url-prefix(https://trello.com) {

p, span, div, a, input, textarea, button, select, label, 
h1, h2, h3, h4, h5, h6, .cLANqf, .jwdbeE, .XjOka, .kqidEQ,
.button, .iconButton {
text-decoration: none !important;
font-family: Muli;}

/* Fit Background Image */ 

.body-board-view {
    background-size: 100% auto;
    background-position: center;}

/* Rounder Edges */

._2ZNy4w8Nfa58d1, 
._3Z6i0FBUukKNYK ._1CyMivLdH2a8dA,
.board-header-btn,
.board-menu-navigation-item-link,
.board-menu-section-header,
.show-more,
.button-link,
.button, 
.iframe-input, 
button, 
input:not([type="file"]), 
textarea,
.card-label.mod-selectable,
.card-label-edit-button,
.pop-over-emoji-list li a, 
.pop-over-member-list li a,
.comment-box-options-item,
.bHF9FML4QxCHFM,
._3n2XNd416DYt5r,
._3LO8BCnYMEeBzC,
._1k52pNENP7tpiK .khihAkM9Ux6EHW,
.list-header-extras-menu,
.list-wrapper.mod-add,
.Y34HN84mGuwAaM,
.board-tile, .board-tile-fade,
.pop-over-list li > a,
.pika-single .pika-next, 
.pika-single .pika-prev,
.pika-single .pika-label,
.label-list-item-link,
.pop-over-list.inset li > a,
textarea.mod-list-name,
._2nlCooRQipe5Hi,
.card-detail-item-add-button,
.card-label.mod-card-detail,
.icon-lg.dark-background-hover:hover,
.card-detail-badge,
.quiet-button,
.edit-controls .option,
.checklist-add-controls-option,
.comment-frame,
.action-comment {
border-radius: 6px;}

.DXzhA_dcFCwcUx  {
border-radius: 6px 0 0 6px;}

/* All Icons - a bit bigger */
.icon-sm {
font-size: 18px;}

/* Menu Back Buttons */

.pop-over-header-back-btn {
left: 11px; top: 9px;
padding: 2px;
border-radius: 20px;
background: #c2c8d2;
transition: all 0.1s;}
.pop-over-header-back-btn:hover {
color: #fff;
background: #6b778c;}

/* Red Round Close Buttons */

.pop-over-header-close-btn {
right: 11px; top: 9px;
padding: 2px;
border-radius: 20px;
color: #c00;
background: #ffb8b8;
transition: all 0.1s;}
.pop-over-header-close-btn:hover {
color: #fff;      
background: #c00;}

.board-menu-header-close-button {
font-size: 25px;
position: absolute;
top: 8px; right: 12px;
color: #c00;}

.dialog-close-button {
right: 6px; top: 6px;
padding: 2px;
color: #c00;
background: #ffb8b8;
opacity: 0.5;
transition: all 0.1s;}
.dialog-close-button:hover {
color: #fff;      
background: #c00;
opacity: 0.8;}

/* Popup Menus  */

.pop-over-header-title {
font-weight: 900;
font-size: 17px;
padding-top: 2px;}
                    
.pop-over {
animation: pop 0.3s ease forwards;
background: rgba(255,255,255,0.98);
box-shadow: 0 0 20px rgba(0,0,0,0.2);
border-radius: 8px;
border: 0;}

.pop-over-content {
padding: 0 14px 12px;}
.pop-over-list li > a {
margin: 0 -5px;}
   
.js-close-list,
.js-delete {
color: #e93a00 !important;}
.js-close-list:hover,
.js-delete:hover,
.js-archive-card:hover {
color: #c00 !important;}
.js-archive-card:hover .icon-sm {
color: #c00 !important;}

/*  Right Board Menu */

.board-menu-tab-content {
border-radius: 15px 0 0 0;
box-shadow: -10px 0 10px rgba(0,0,0,0.2);}

.board-menu-content {
padding: 12px 12px 12px 12px;}

.board-menu-header-back-button {
color: #999;
font-size: 25px;
top: 8px; left: 8px;}


/* ALL CARDS */

div.list {
border-radius: 8px;
background:rgba(255,255,255,0.6);}
.open-card-composer {
border-radius: 0 0 8px 8px;}

textarea.mod-list-name {
border-radius: 6px;
font-weight: 700;}

.list-card {
background-color: rgba(255,255,255,0.8);
box-shadow: 0 0 6px rgba(0,0,0,0.2);
border-bottom: none;
border-radius: 6px;}
    
.list-card:hover {
background-color: rgba(255,255,255,1.0);
box-shadow: 0 0 6px rgba(0,0,0,0.4);}
    
/* Card Titles */

.list-card-title.js-card-name,
textarea.list-card-composer-textarea,
textarea.list-card-edit-title {
font-size: 15px;
line-height: 19px;}
   
textarea.list-card-edit-title {
height: 57px;}

/* Card Check Lists */

.badge.is-complete {
background-color: rgba(0,0,0,0.2);}


/* Card Titles - Quick Edit */

.list-card-operation {
background-color: rgba(0,0,0,0.01);
box-shadow: inset 0 0 5px rgba(0,0,0,0.1);
border-radius: 15px;
padding: 3px;
right: 3px; top: 3px;}

.quick-card-editor {
animation: pop 0.3s ease forwards;}

.quick-card-editor-buttons-item,
.quick-card-editor-buttons-item:hover {
border-radius: 6px;
transform: none;
box-shadow: inset 0 0 8px rgba(0,0,0,0.7);}
    
.quick-card-editor-buttons-item.js-archive,
.quick-card-editor-buttons-item.js-archive .icon-sm {
color: #c00;}
.quick-card-editor-buttons-item.js-archive:hover,
.quick-card-editor-buttons-item.js-archive:hover .icon-sm {
color: #ff3c3c;}
   
.list-card-quick-edit.list-card {
background-color: rgba(255,255,255,1.0);}


/*  Add Card  */

.open-card-composer .icon-add,
.open-card-composer {
color: #333 !important;}
   


/* CARD WINDOW */

.window-overlay {
 background-color: rgba(0,0,0,0.7);}
   
.window {
width: 900px;
box-shadow: 0 0 50px rgba(0,0,0,0.5);
margin-top: 0px;
padding: 0;
min-height: 100%;
border-radius: 0;
animation: pop 0.3s ease forwards;}
   
@keyframes pop {
  0% {opacity: 0;}
  100% {opacity: 1;}}

.shortcut-list {
    padding: 0 140px 0 10px;}
    
/* Cover */

.window-cover {
height: 300px !important;
margin-top: 0px !important;
margin-bottom: -45px !important;
background-color: #111}
.window-cover.is-covered:hover {
opacity: 1.0;}

/* Sidebar Buttons */
.window-sidebar {
padding: 10px 30px 8px 8px;}

.window-sidebar h3 {
margin-bottom: 0px;}

.button-link,
.quiet-button {
color: #172b4d;
background-color: #f9f9f9;
box-shadow: 0 0 10px rgba(0,0,0,0.1);}
.button-link:hover,
.quiet-button:hover {
color: #2e5eb1;
background-color: #fff;
box-shadow: 0 0 10px rgba(0,0,0,0.15);}
.button-link:hover .icon-sm,
.quiet-button:hover .icon-sm {
color: #2e5eb1;}

/* Title */
  
textarea.mod-card-back-title {
    text-align: center;
    font-weight: 900;
    font-size: 32px;
    line-height: 100%;
    margin: 0px 0px 10px 0px;
    padding: 14px 2% 10px 2%;
    transition: all 0.3s ease;
    color: #44526e;
    letter-spacing: -1px;
    background: #fff;
    border-radius: 6px;
    white-space: nowrap;}

textarea.mod-card-back-title:disabled {
    line-height: 110%;
    white-space: normal;}

.mod-card-back-title:focus {
    color: #1c5dad;
    background: #fff !important;}
	
.window h3 {
font-size: 18px;
letter-spacing: 0;
text-transform: none;
font-weight: 900;
color: #44526e;}


/* DESCRIPTION */

.window-main-col {
    width: 650px;}

.description-content-fade-button-text {
background: #fff;
border-radius: 6px;
box-shadow: 0 0 10px #ccc;
padding: 5px 10px 5px;
margin-top: -10px;}

.description-content .markeddown {
    padding: 16px 20px 10px 20px;
    font-size: 18px;
    font-weight: 400;
    line-height: 1.4em;
    background: #fafafa !important;
    border-radius: 8px;
    color:#48505f;
    text-shadow: 0 0 2px #ddd;}
   
.markeddown h1 {
font-size: 24px !important;}
.markeddown p {
margin-bottom: 1.4em;}
.markeddown ul {
margin: 1.8em 0 1.5em 0 !important;}
.markeddown ul > li {
list-style: disc;
margin: -8px 8px 8px 20px;}
.markeddown a {
text-decoration: none;}
   
/* Editing Textarea */

.description-content .edit textarea.field {
    padding: 16px 20px 10px 20px;
    font-size: 18px;
    font-weight: 400;
    line-height: 1.4em;
    background: #333 !important;
    box-shadow: inset 0 0 20px #000 !important;
    border-radius: 8px;
    color: #eee;}
  
/* Note List */
  
a.js-open-move-from-header {
  background: #E2E4E6;
  padding: 3px 8px;
  border-radius: 4px;
  text-decoration: none;
  font-weight: bold;
  transition: all 0.3s ease !important;}
a.js-open-move-from-header:hover {
  background: #D6DADC;} 
  
   
/* CHECKLIST */

/* Box & Header */

.checklist {
background: #fafafa;
border-radius: 8px;
margin: 30px 0px 0px 40px;
padding: 10px 30px 10px 20px;}

.checklist-title 
textarea.field.full.single-line {
background: #fafafa;
font-size: 18px; 
height: 28px !important;
font-weight: 700;
padding: 6px 0px 0px 0px;
border: none;
box-shadow: none;}

.checklist .quiet {
    margin-top: -10px !important;
    padding: 4px 12px !important;}

/* Progress Bars */ 

.checklist-progress-percentage {
    color: #838c91;
    font-size: 15px;
    line-height: 14px;
    top: 0px;}
    
.checklist-progress-bar {
    border-radius: 6px;
    height: 12px;
    margin: 0 5px 10px 40px;}

.checklist-progress-bar-current {
  background: -moz-linear-gradient(
  135deg,
#00995e 25%, #00804f 25%,
#00804f 50%, #00995e 50%,
#00995e 75%, #00804f 75%);
  width: 80%;
  height: 30px;
  background-size: 50px 50px;
  -webkit-animation: bar-animation 3s linear infinite;}

@-webkit-keyframes bar-animation {
  0% {background-position: 0 0;}
  100% {background-position: 50px 50px;}}

/* Checklist Items */

.checklist-item {
background: #fafafa !important;
padding: 1px 0 1px 37px;}

.checklist-item-checkbox {
margin: 7px 8px 8px;}

.checklist .markeddown {
font-size: 19px;
font-weight: 400;
color: #555;
text-shadow: 0 0 2px #ddd;}
   
.checklist-item-state-complete 
.checklist-item-details-text {
color: #838c91 !important;
text-decoration: line-through !important;}
.checklist-item:hover span {
color: #00995e !important;}

/* Checklist Item - Input Editing */

textarea.js-checkitem-input,
textarea.checklist-new-item-text {
background: #fafafa !important;
height: 23px !important;
font-size: 19px;
border: none;
box-shadow: none !important;
margin-top: 4px;
color: #00995e !important;
text-shadow: 0 0 2px #ddd;}

textarea.js-checkitem-input {
padding: 0px 0px 0px 0px;
margin: 6px 0px 0px 0px;}

textarea.checklist-new-item-text {
padding: 10px 10px 10px 10px;
height: 40px !important;}
 

  
/* Links */
  
.current a,
.comment-container a {
  color: #0d70d5;}

a.known-service-link {
    background: rgba(0,0,0,0);}
a.known-service-link:hover {
    color: #2aa456 !important;
    background: rgba(0,0,0,0);}
  
.current a:hover,
.comment-container a:hover  {
  color: #429dfb;}

/* Card Labels */

.body-card-label-text-on .card-label.mod-card-front, 
.body-card-label-text .card-label.mod-card-front {
height: 18px;
line-height: 17px;
padding: 0 6px;
min-width: 20px;}

.card-label.mod-card-detail {
font-size: 14px;
font-weight: 700;
height: 28px;
line-height: 28px;
padding: 0 10px;}

.card-detail-item-add-button .icon-sm {
padding: 6px;}

/* Smaller Labels */

.card-label.mod-selectable {
padding: 5px 10px;}
.card-label-edit-button {
padding: 5px 7px;}

.card-label.mod-edit-label {
margin: 0 4px 4px 0;
width: 51px;}
.card-label {
border-radius: 6px;}

.card-label.mod-selectable .card-label-selectable-icon {
top: 4px;}

p.u-bottom {
margin-top: 2px;}
p.u-bottom.quiet {
font-size: 11px;
line-height: 1em;}

/* Custom Fields */

.field-color.badge {
border-radius: 6px;
padding: 1px 2px 1px 4px;
margin-top: 2px;}
.custom-field-detail-badge {
border-radius: 6px;
height: 18px;
line-height: 18px;}
    

   
} 
/* i really want this to be global */
