css:
@-moz-document url-prefix("http://ragial.org") {
/* Body and Background */
.main, .mkt_left
{
    background-color: #444 !important;
}

body, .footer_out
{
    background-color: #111e21
}

.head_main img
{
    display: none !important;
}

#avgtable
{
    margin-top: 0px !important;
}

#selltable #date, #selltable #pc
{
    width: 15% !important;
}

.head_main
{
    background-image: url('https://i.imgur.com/2vPPTeR.png');
    background-repeat: no-repeat !important;
    background-color: #59a0af !important;
    background-blend-mode: multiply !important;
    background-position: 10px center !important;
}

.mkt_left h2
{
    margin-top: 8px !important;
}

#selltable #amt
{
    padding-right: 5px !important;
}

#selltable
{
    width: 100% !important;
}

.head_main .server
{
    width: 220px !important;
}

.mkt_left h1 img
{
    margin-top: 3px !important;
    ;
    margin-right: 3px !important;
    ;
}

.mkt_rb, .mkt_left h1, .mkt_left h4
{
    margin-top: 27px !important;
}

#rnav_server, div#ragial_tip
{
    margin-bottom: 20px !important;
}

.mkt_rb
{
    margin-bottom: 0px !important;
}


/* Border Accent Dark */
.mkt_left h1, .mkt_left h4, .mkt_left .ilist td, #poplist td, .mkt_rb h2, #avgtable #cnt, #avgtable #hed, #avgtable #num, #selltable td
{
    border-bottom: 1px solid #59a0af !important;
}


/* White Text */
.ch, .head_tc ul li:hover .head_tc_drop a, .head_top a:hover, .head_top a:visited:hover, .mkt_left h1 a, .mkt_left h2 a, #vend_info dt, .head_main .server, .footer
{
    color: #ffffff !important;
}


/* Black Text */
.head_top a, .head_top a:visited, .mkt_left h1, .mkt_left h4, .mkt_left .ilist .name a, a:link, a:visited
{
    color: #383838 !important;
}


/* Accent Text */

/*
{
    color: #78c1d0 !important;
}*/

/* Accent Dark Background Color */
.mkt_left .ilist .price, .mkt_left .ilist tr:hover, #poplist .price, .mkt_rb h2, #avgtable .odd, #avgtable th #hed, .mkt_left h1, .mkt_left h4, #selltable #date, .mkt_left h2, #vend_info > dl > dd:nth-child(3), #vend_info > dl > dd:nth-child(5), #vend_info > dl > dd:nth-child(7), #avgtable #hed, #avgtable .gb, #selltable > thead
{
    background: #59a0af !important;
}


/* Accent Background Color */
.mkt_left .ilist tr, .mkt_rb, .ilist #poplist, .head_tc, .head_top, .sh_search .search_input, #avgtable th, #selltable #pc, #avgtable tbody, #selltable #amt, #selltable td
{
    background: #78c1d0 !important;
}

#rnav_server, div#ragial_tip, .mkt_rb, .main, .mkt_left h1, #selltable td, #selltable tbody tr, .head_main
{
    border-style: solid!important;
    border-width: 1px!important;
    border-color: #78c1d0 !important;
}

.mkt_left h1, .mkt_left .ilist, .mkt_rb, .sh_search .search_input
{
    box-shadow: 5px 5px 0px #111e21!important;
}

::-webkit-scrollbar
{
    background: -webkit-linear-gradient(40deg, #000000, #1e1e1e, #000000 100%)!important;
    height: 12px!important;
    width: 12px!important;
}

::-webkit-scrollbar-thumb
{
    background-color: #454545!important;
    border-radius: 0px!important;
    border-style: solid!important;
    border-width: 1px!important;
}
}
