css:
@-moz-document domain("ragial.org") {
/* Body and Background */
.main, .mkt_left {
    background-color: #444 !important; }

body, .footer_out {
    background-color: #111e21 }

.head_main img {
    display: none !important; }

#avgtable {
    margin-top: 0px !important; }

#selltable #date, #selltable #pc {
    width: 15% !important; }

.head_main {
    background-image: url('https://i.imgur.com/2vPPTeR.png');
    background-repeat: no-repeat !important;
    background-color: /*[[dark]]*/ !important;
    background-blend-mode: multiply !important;
    background-position: 10px center !important; }

div.mkt_left > div > ul > li:nth-child(1), div.mkt_left > div > ul > li:nth-child(2), div.mkt_left > div > ul > li:nth-child(3), div.mkt_left > div > ul > li:nth-child(4) {
    text-align: center !important; }

.mkt_left h2, div.mkt_left > div > ul > li:nth-child(1), div.mkt_left > div > ul > li:nth-child(2), div.mkt_left > div > ul > li:nth-child(3), div.mkt_left > div > ul > li:nth-child(4) {
    margin-top: 8px !important; }

#selltable #amt {
    padding-right: 5px !important; }

#poplist .shop_item a.itemdb {
    width: 36px !important; }

#selltable {
    width: 100% !important; }

.head_main .server {
    width: 220px !important; }

.mkt_left .ilist img, .mkt_left .ilist .shopitem img {
    margin-left: 5px !important;
    margin-right: 5px !important; }

.mkt_left .ilist .shopitem img, .mkt_left .ilist table img, .mkt_left .ilist .name a {
    margin-right: 5px !important; }

.mkt_left h1 img {
    margin-top: 3px !important;
    margin-right: 3px !important; }

.mkt_rb, .mkt_left h1, .mkt_left h4 {
    margin-top: 27px !important; }

#rnav_server, div#ragial_tip {
    margin-bottom: 20px !important; }

.mkt_rb {
    margin-bottom: 0px !important; }


/* Border Accent Dark */
.mkt_left h1, .mkt_left h4, .mkt_left .ilist td, #poplist td, .mkt_rb h2, #avgtable #cnt, #avgtable #hed, #avgtable #num, #selltable td {
    border-bottom: 1px solid /*[[dark]]*/ !important; }


/* Bold*/
.ch, .head_tc, #avgtable #num {
    font-weight: bold !important; }


/* White Text */
.ch, .head_tc ul li:hover .head_tc_drop a, .head_top a:hover, .head_top a:visited:hover, .mkt_left h1 a, .mkt_left h2 a, #vend_info dt, .head_main .server, .footer, div.mkt_left > div > ul > li:nth-child(1) > a, div.mkt_left > div > ul > li:nth-child(2) > a, div.mkt_left > div > ul > li:nth-child(3) > a, div.mkt_left > div > ul > li:nth-child(4) > a {
    color: #ffffff !important; }

a
/* Black Text */
.head_top a, .head_top a:visited, .mkt_left h1, .mkt_left h4, .mkt_left .ilist .name a, a:link, a:visited {
    color: #383838 !important; }


/* Accent Text */

/*
{
    color: #78c1d0 !important;
}*/

/* Accent Dark Background Color */
.mkt_left .ilist .price, .mkt_left .ilist tr:hover, #poplist .price, .mkt_rb h2, #avgtable .odd, #avgtable th #hed, .mkt_left h1, .mkt_left h4, #selltable #date, .mkt_left h2, #vend_info > dl > dd:nth-child(3), #vend_info > dl > dd:nth-child(5), #vend_info > dl > dd:nth-child(7), #avgtable #hed, #avgtable .gb, #selltable > thead, div.main > div.mkt_left > div > ul > li:nth-child(1), body > div.main > div.mkt_left > div > ul > li:nth-child(2), body > div.main > div.mkt_left > div > ul > li:nth-child(3), body > div.main > div.mkt_left > div > ul > li:nth-child(4) {
    background: /*[[dark]]*/ !important; }


/* Accent Background Color */
.mkt_left .ilist tr, .mkt_rb, .ilist #poplist, .head_tc, .head_top, .sh_search .search_input, #avgtable th, #selltable #pc, #avgtable tbody, #selltable #amt, #selltable td {
    background: /*[[light]]*/ !important; }

#rnav_server, div#ragial_tip, .mkt_rb, .main, .mkt_left h1, #selltable td, #selltable tbody tr, .head_main, .ch .head_top a:hover, .head_top a:visited:hover, .mkt_left h2 a, .head_main .server a, div.mkt_left > div > ul > li:nth-child(1), div.mkt_left > div > ul > li:nth-child(2), div.mkt_left > div > ul > li:nth-child(3), div.mkt_left > div > ul > li:nth-child(4) {
    border-style: solid!important;
    border-width: 1px!important;
    border-color: /*[[light]]*/ !important; }

td.name > a.itemdb > img {
    border-style: solid!important;
    border-width: 0px!important; }

.mkt_left h1, .mkt_left .ilist, .mkt_rb, .sh_search .search_input, #avgtable, #selltable {
    box-shadow: 5px 5px 0px #111e21!important; }

::-webkit-scrollbar {
    background: -webkit-linear-gradient(40deg, #000000, #1e1e1e, #000000 100%)!important;
    height: 12px!important;
    width: 12px!important; }

::-webkit-scrollbar-thumb {
    background-color: #454545!important;
    border-radius: 0px!important;
    border-style: solid!important;
    border-width: 1px!important; }
}
