@-moz-document url-prefix("https://contacts.google.com/") {
:root {

--color-search-background: #111111;
--color-background: #191919;
--color-button: #353535;  
--color-text-inactive: #222222; 
--color-text-menus: #666666;
--color-text: #EEEEEE;
--color-borders: #777777;
--color-id: chartreuse;
    
}

/* ------------------------------------------------------------------------------------------------ */
/* UI styling */
/* ------------------------------------------------------------------------------------------------ */

/* -------------------------------- */
/* general */
/* -------------------------------- */

/* body, header bar, left side bar */
body, header, .k831u {
	background: var(--color-background) !important;
}



/* -------------------------------- */
/* header */
/* -------------------------------- */

/* hide Google colored circle segments around user picture */
#gb > div > div > div > div > div > a > div > svg > path
{
  	display: none !important;  
}


/* -------------------------------- */
/* search bar*/
/* -------------------------------- */

/* unfocused search bar */
.gb_oe.gb_la, .jBmls {
  background: var(--color-backgroundn) !important;
  color: var(--color-text) !important;
}

/* focused search bar */
.Ax4B8 {
  color: var(--color-text) !important;
}

/* search results list */
.VOEIyf .jBmls, .VOEIyf, .VOEIyf .jBmls, .oKubKe {
  background-color: var(--color-background) !important;
  color: var(--color-text) !important;
}

/* list mouseover */
.oKubKe[aria-selected="true"] {
  background-color: var(--color-button) !important;
}


