css:
@-moz-document url-prefix("http://ragial.org"), url-prefix("https://ropd.info/") {
/* ropd */

.pl, .n td pre, td.cnt
	{border: 0px dotted #111;
	 color: #c3c3f6 !important;}

/* eq txt */
.qdr, .qdl
	{color: #5B4E3E;}


/* .pl.s7g26195, .pl.s7g37372  guild pink
	{background-color: #DBADAF !important;} */


/* ragial */
html, h1, h2 , h3 , h4, body, .main, tr, .ilist, .footer, .mkt_nav, .head_top, .head_tc, .head_main, .drop_server, .mkt_left, .mkt_rb, .spacer, .footer_out, .head_main img
    {background-color: #33302D !important;
     color: #c3c3f6 !important;}

a
	{color: #B5C7DD !important;}

.mkt_left .ilist .date
	{color: #c3c3f6 !important;}

.small_gray
	{color: #9B9BC1 !important;}

/* logo replacement */
.head_main img 
  {display: block;
  box-sizing: border-box;
  background: url(data:image/png;base64,) no-repeat;
  width: 200px; /* Width of new image */
  height: 45px; /* Height of new image */
  padding-left: 200px; /* Equal to width of new image */
  	/* original img values */
    margin-left: 12px;
  /*  padding-top: 22px; */
	margin-top: 22px;  /* turned padding value to margin */
    padding-bottom: 16px;}

td.price.ch /* price low */
	{background-color: #29352F !important;}

td.price.ex /* price high */
	{background-color: #492828 !important;}

td.price.nm, td#date.nm, .mkt_left th, .mkt_left td /* price */
	{background-color: #292929 !important;}

a:link, a:visited, a .tag, h2 a:active
    {color: #B5C7DD !important;}

* 
	{border-color: #888 !important;}


.ilist
	{max-width: 604px;}	

.mkt_left
	{max-width: 615px;}


input.search_input#search_input
	{background-color: #222;
     color: #ddd;}

.flr
	{z-index: 12;}

/* scrollbar */

/* width */
::-webkit-scrollbar
    {width: 10px;}

/* Track */
::-webkit-scrollbar-track
    {background: #2e2e2e;}
 
/* Handle/bar */
::-webkit-scrollbar-thumb 
	{background: #555;}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover 
	{background: #888;}

/* extras */

table.hundo tbody tr:nth-child(1):before
{ content: "1 　";}

table.hundo tbody tr:nth-child(2):before
{ content: "2 　";}

table.hundo tbody tr:nth-child(3):before
{ content: "3 　";}

table.hundo tbody tr:nth-child(4):before
{ content: "4 　";}

table.hundo tbody tr:nth-child(5):before
{ content: "5 　";}

table.hundo tbody tr:nth-child(6):before
{ content: "6 　";}

table.hundo tbody tr:nth-child(7):before
{ content: "7 　";}

table.hundo tbody tr:nth-child(8):before
{ content: "8 　";}

table.hundo tbody tr:nth-child(9):before
{ content: "9 　";}

table.hundo tbody tr:nth-child(10):before
{ content: "10 　";}

table.hundo tbody tr:nth-child(11):before
{ content: "11 　";}

table.hundo tbody tr:nth-child(12):before
{ content: "12 　";}

table.hundo tbody tr:nth-child(13):before
{ content: "13 　";}

table.hundo tbody tr:nth-child(14):before
{ content: "14 　";}

table.hundo tbody tr:nth-child(15):before
{ content: "15 　";}

table.hundo tbody tr:nth-child(16):before
{ content: "16 　";}


/* image replacement vend shop*/
img[src*="shop_vend.png"]
  {display: inline-block;
  box-sizing: border-box;
  background: url(data:image/png;base64,) no-repeat;
  width: 24px; /* Width of new image */
  height: 24px; /* Height of new image */
  padding-left: 24px; /* Equal to width of new image */
  opacity: .96;}

/* image replacement buy shop*/
img[src*="shop_buy.png"]
  {display: inline-block;
  box-sizing: border-box;
  background: url(data:image/png;base64,) no-repeat;
  width: 24px; /* Width of new image */
  height: 24px; /* Height of new image */
  padding-left: 24px; /* Equal to width of new image */
  opacity: .96;}

/* tag dimming */
td.tag a img
{opacity: .96;}

.ilist #poplist {
    padding-left: 12px;
    background: #FFDEBD4A;}

tr a:hover
    {font-weight: bolder;}

dl dt input
{background-color: #eee;}

form input
{background-color: #333;}
}
