css:
@-moz-document url-prefix("https://catwar.su/") {
div#logo {
display: none;
}
div#menu_div {
background: #736080;
}
div#site_table {
background: #736080;    
color: #9370DB;
}
div#branch {
background: #736080;
color: #9370DB;
}
div#footer {
background: #736080;
color:#9370DB;
}
body {
    background-image: none;
    color: #9370DB;
}
body {
    font-size: .9em;
    font-family: Verdana;
    margin: 0;
    padding: 0;
    background-attachment: fixed;
    background-color: #2D2433;
}
a:link {
color:#9370DB;
}
a:visited {
color:#9370DB;
}
#tr_chat {
    background-color: #4D3D57;
    padding: 0px;
}
#chat_msg {
    background-color: #4D3D57;
    border: 0px;
}
#tr_tos {
    background: #4D3D57;
color:#9370DB;
}
#tr_sky {
    display: none;
}
#tr_actions {
    background-color: #4D3D57;
    border: 0px;
}
.ui-slider .ui-slider-handle {
    background: #3A1254;
    border: 0.5px solid #282828;
}
.ui-slider {
    background: #1B032B;
    border: 0.5px solid #282828;
    margin-right: 50px;
}
hr {
    border: 0.5px solid #1B032B;
}
div#thdey {
    margin-left: 10px;
}
h2 {
background: #4D3D57;
}
div#background {
background:#4D3D57;
}
a#parameters.toggle {
    display: none;
}
#family {
    background-color: #4D3D57;
}
div#tir {
    background:#4D3D57;
    color: #9370DB;
}
.title {
    background:#4D3D57;
    color: #9370DB;
}
a#history_clean {
    margin-left: 5px;
}
tr#tr_mouth {
background:#4D3D57;
}
p#error {
    top: 310px;
    left: 25px;
    background-color: #4D3D57;
    border: 0.5px solid #282828;
}
#fightPanel {
    background-color: #4D3D57;
    border: 0.5px solid #2d2d2d;
}
input {
    background-color: #111111;
    color: #9370DB;
    border: 0.5px solid #282828;
    border-radius: 5px;
}
input:focus {
    background-color: #383838;
}
.hotkey {
    background-color: #111111;
    color: #9370DB;
    border: 0.5px solid #282828;
    border-radius: 5px;
}
#mit {
    background-color: #111111;
    color: #9370DB;
    border: 0.5px solid #282828;
    border-radius: 5px;
}
.move_name {
    color: #000000;
}
.nick {
    color: #b2b2b2;
}
.adopt-id {
    color: #fff;
}
.cat_tooltip {
background: #191919;
}
#history {
    background-color: #4D3D57;
    width: 100%;
    margin-left: 10px;
    color:#9370DB;
}
a#history.toggle {
    color: #9370DB;
    font-size: 16px;
    margin-left: 10px;
    margin-bottom: 5px;
}
#parameter {
    position: fixed;
    width: 185px;
    top: 60px;
    right: 220px;
    border-radius: 10px;
    text-align: left;
    padding: 14px;
background:#2D2433;
}
span#location {
    color: #838383 !important;
    position: fixed;
    width: 170px;
    top: 12px;
    right: 240px;
    background-color: #4D3D57;
    border: 0.5px solid #282828;
    border-radius: 15px;
    text-align: center;
    padding: 15px;
    font-weight: bold;
}
div#history_block {
 background-color: transparent;   
    }
}
