css:
@-moz-document url-prefix("https://catwar.su/") {

div#logo {
display: none;
}
div#menu_div {
background: #FFB841;
}
div#site_table {
background: #FFB841;    
color: #D76E00;
}
div#branch {
background: #FFB841;
color: #D76E00;
}
div#footer {
background: #FFB841;
color:#D76E00;
}
body {
    background-image: none;
    color: #D76E00;
}
body {
    font-size: .9em;
    font-family: Verdana;
    margin: 0;
    padding: 0;
    background-attachment: fixed;
    background-color: #FFDF84;
}
a:link {
color:#D76E00;
}
a:visited {
color:#D76E00;
}
#tr_chat {
    background-color: #FFB841;
    padding: 0px;
}
#chat_msg {
    background-color: #FFB841;
    border: 0px;
}
#tr_tos {
    background: #FFB841;
color:#D76E00;
}
#tr_sky {
    display: none;
}
#tr_actions {
    background-color: #FFB841;
    border: 0px;
}
span#location {
    color: #D76E00 ;
}
.ui-slider .ui-slider-handle {
    background: #C35831	;
    border: 0.5px solid #282828;
}
.ui-slider {
    background: #EFA94A;
    border: 0.5px solid #282828;
    margin-right: 50px;
}
hr {
    border: 0.5px solid #FFB841;
}
input {
    background-color: #EFA94A;
    color: #D76E00;
    border: 0.5px solid #282828;
    border-radius: 5px;
}
div#history_block div {
    background: #FFB841;
}
div#thdey {
    margin-left: 10px;
}
#history div {
    background:#FFB841;
    color: #D76E00;
}
div#background {
background:#FFB841;
}
#parameter {
background:#FFB841;
}
a#parameters.toggle {
    display: none;
}
#family {
    background-color: #FFB841;
}
div#tir {
    background:#FFB841;
    color: #D76E00;
}
.title {
    background:#FFB841;
    color: #D76E00;
}
a#history_clean {
    margin-left: 5px;
}
td#history {
background:#FFB841;
}
tr#tr_mouth {
background:#FFB841;
}
p#error {
    top: 310px;
    left: 25px;
    background-color: #FFB841;
    border: 0.5px solid #282828;
}
#fightPanel {
    background-color: #FFB841;
    border: 0.5px solid #2d2d2d;
}
input {
    background-color: #EFA94A;
    color: #D76E00;
    border: 0.5px solid #282828;
    border-radius: 5px;
}
input:focus {
    background-color: #FFB841;
}
.hotkey {
    background-color: #FFB841;
