css:
@-moz-document domain("www.volleyscores.be") {
/* Algemeen */
body {
    color: #eee;
    background-color: #1f1f1f;
}

.volleyscoreBanner {
    filter: invert(100%);
    box-shadow: none;
}

.form-control {
    color: #eee;
    background-color: #000;
    border-color: #eee;
    border-radius: 0;
}

.form-control:focus {
    border-color: #eee;
    box-shadow: none;
}

.btn {
    border-radius: 0;
}

.panel {
    background-color: transparent;
    border: 2px solid #000;
    border-radius: 0;
}

.panel-heading {
    border: none;
    border-radius: 0;
}

.panel-heading h4 {
    font-weight: 600;
}

.panel-default>.panel-heading {
    color: #eee;
    background-color: #000;
}

.table-bordered {
    border-color: #000;
}

.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border-color: #000;
}

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #444;
}

.nav-tabs {
    border-color: #000;
}

.nav-tabs>li {
    margin-bottom: 0;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #eee;
    background-color: #076DA0;
    border-color: #076DA0;
    border-radius: 0;
}

.nav-tabs>li>a:hover {
    color: #eee;
    border-color: #333;
    background-color: #333;
    border-radius: 0;
}

.select2-drop {
    color: #eee;
    background-color: #000;
    border-color: #eee;
    border-radius: 0;
}

.select2-drop-active {
    border-color: #eee;
}

.select2-search {
    padding: 0;
}

.select2-search input {
    border-radius: 0;
}

.select2-results {
    padding: 0;
    margin: 0;
}

.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
    color: #A00;
    background-color: #000;
    text-align: center;
}

.select2-container .select2-choices .select2-search-field input, .select2-container .select2-choice, .select2-container .select2-choices {
    color: #eee;
    background-color: #000;
    border-color: #eee;
    border-radius: 0;
}

.select2-container-active .select2-choice, .select2-container-multi.select2-container-active .select2-choices {
    border-color: #eee;
    box-shadow: none;
}

a {
    color: #076DA0;
}

a:hover {
    color: #eee;
    text-decoration: none;
}

a:focus {
    outline: none;
}

a img {
    opacity: 0.6;
    box-shadow: none !important;
}

a:hover img {
    opacity: 1;
}

h4 {
    color: #eee !important;
}

/* Zijbalk */
#volleyscoreMenu {
    background-color: #000;
}

#volleyscoreMenu .panel {
    background-color: transparent;
    border: 2px solid #222;
    border-radius: 0;
}

#volleyscoreMenu .panel-heading {
    border: none;
    border-radius: 0;
}

#volleyscoreMenu .panel-heading h4 {
    font-weight: 600;
}

#volleyscoreMenu .panel-default>.panel-heading {
    color: #eee;
    background-color: #222;
}

#volleyscoreMenu .panel-body[style="text-align: center"] {
    background-color: #076DA0;
}

a.favorite, a.favorite:visited, a.delfav, a.delfav:visited {
    color: #aaa;
}

a.favorite:hover, a.delfav:hover {
    color: #eee;
    background-color: transparent;
}

.volleyscoreMnu .lblLevel1 {
    color: #076DA0;
}

.volleyscoreMnu .lblLevel2 {
    color: #aaa;
}

.volleyscoreMnu .lblLevel1:hover, .volleyscoreMnu .lblLevel2:hover {
    color: #eee;
}

.volleyscoreMnu .mnuLink {
    color: #aaa;
}

.volleyscoreMnu .mnuLink:hover {
    color: #eee;
    background-color: transparent;
    text-decoration: none;
}

/* Content */
#volleyscoreContainer {
    background-color: #1f1f1f;
}

#reqNavExtra > a, #reqNavExtra > a:visited {
    background-color: #000;
}

#reqNavExtra > a:hover {
    background-color: #076DA0;
}

/* Team */
.teamtitle {
    color: #eee;
    border-color: #eee;
}

.teamsubtitle {
    color: #eee;
    text-decoration: none;
}

.scoreSerieTitle {
    background-color: #000;
}

.matchchanged {
    background-color: #909156;
}

.team:hover, .table-striped > tbody > tr:nth-child(odd) > td.team:hover, .sporthall:hover, .table-striped > tbody > tr:nth-child(odd) > td.sporthall:hover {
    background-color: #076DA0;
    text-decoration: none;
}
}
