css:
@-moz-document domain("www.hln.be") {
body, .container {
    color: #eee;
    background-color: #1f1f1f;
}

a, a:active, a:focus, a:hover, a:visited {
    color: #eee;
}

a:hover {
    color: #FF0002;
}

.page-logo {
    filter: grayscale(100%);
}

.menu__top-container {
    background-color: #000;
}

.menu__list--secondary {
    background-color: #111;
}

.menu__list--secondary .menu__link, .menu__list--secondary .menu__link:active, .menu__list--secondary .menu__link:focus, .menu__list--secondary .menu__link:hover, .menu__list--secondary .menu__link:visited {
    color: #eee;
}

a.teaser-link.teaser-link--color.hftn-list-teaser__link {
    color: #eee;;
}

.ktsjing-row--leader, .ktsjing-row, .side-menu--social, .integration--trackuity, .social-widget {
    display: none;
}

.weather-teaser__temperature, .weather-teaser__temperature:active, .weather-teaser__temperature:focus, .weather-teaser__temperature:hover, .weather-teaser__temperature:visited {
    color: #eee;
}

.weather-teaser__button {
    background: none;
}

.flow-large-vertical-teaser__intro-link, .flow-large-vertical-teaser__intro-link:active, .flow-large-vertical-teaser__intro-link:focus, .flow-large-vertical-teaser__intro-link:hover, .flow-large-vertical-teaser__intro-link:visited, .flow-medium-vertical-teaser__intro-link, .flow-medium-vertical-teaser__intro-link:active, .flow-medium-vertical-teaser__intro-link:focus, .flow-medium-vertical-teaser__intro-link:hover, .flow-medium-vertical-teaser__intro-link:visited {
    color: #bbb;
}

.hftn-large-teaser__intro-link, .hftn-large-teaser__intro-link:active, .hftn-large-teaser__intro-link:focus, .hftn-large-teaser__intro-link:hover, .hftn-large-teaser__intro-link:visited {
    color: #eee;
}

.teaser-list, .bakskes, .page-section--stand-out, .popular-online__teasers, .page-footer {
    background-color: #111;
}

.navigation-teaser-list {
    background-color: transparent;
}

.trending-navigations__link, .trending-navigations__link:active, .trending-navigations__link:focus, .trending-navigations__link:hover, .trending-navigations__link:visited, .component-title, .teaser-label, .sticky-back-to-top {
    background-color: #000;
}

.dossier-carousel-vertical-teaser--plus .dossier-carousel-vertical-teaser__title, .dossier-carousel-horizontal-teaser--plus .dossier-carousel-horizontal-teaser__title, .dossier-large-vertical-teaser--plus .dossier-large-vertical-teaser__title, .flow-large-vertical-teaser--plus .flow-large-vertical-teaser__title, .flow-medium-vertical-teaser--plus .flow-medium-vertical-teaser__title,.navigation-section-carousel-horizontal-teaser--plus .navigation-section-carousel-horizontal-teaser__title,.bakskes-horizontal-teaser--plus .bakskes-horizontal-teaser__title, .top-navigation-vertical-teaser--plus .top-navigation-vertical-teaser__title {
    background-color: #000;
        box-shadow: -4px 0 0 0 #000, 4px 0 0 0 #000;;
}
}
