css:
@-moz-document domain("adoc.app.fft.fr") {
.tpl-home .home-box strong {
 color: black;
}
#fft-header .fft-header-content #fft-profile-nav > ul > li > span, #fft-header .fft-header-content #fft-profile-nav > ul > li > a {
 font-weight: bold;
 color: black;
}
#fft-header .fft-header-content #fft-profile-nav li.submenu ul li a {
 font-weight: bold;
 color: black;
}
#fft-main #fft-breadcrumb li {
 font-weight: bold;
 color: black;
}
#fft-main #fft-tabs li a {
 font-weight: bold;
 color: black;
}
.pagebanner {
 font-weight: bold;
 color: black;
}
:not(.oldie) #fft-main .fft-form .fft-form-line label, :not(.oldie) #fft-main .fft-form .fft-form-line-right label, :not(.oldie) #fft-main .fft-form .fft-form-line-right-plus label {
 color: black;
 font-size: 14px;
 font-weight: bold;
}
.pagination .pagination-page a {
 font-weight: bold;
 color: black;
}
:not(.oldie) input:not([type="radio"]):not([type="checkbox"]):not([type="submit"]):not([type="button"]), :not(.oldie) select, :not(.oldie) textarea {
 color: black;
}
div.dispo {
 background-color:#7cc481;
}
#fft-main #fft-tabs li.current a {
 font-size: 20px;
}
:not(.oldie) #fft-main .fft-form .fieldset .legend, :not(.oldie) #fft-main .fft-form .fieldset legend, :not(.oldie) #fft-main .fft-form fieldset .legend, :not(.oldie) #fft-main .fft-form fieldset legend {
 font-weight: bold;
}
.btn2 {
 background-color: #06AED3;
 color: white;
 font-weight: bold;
 font-size: 14px;
}
#scrollDonneesTableau {
 font-size: 14px;
}
div.vide, div.enteteCourtJour {
 font-size: 11px;
}
#heures div {
 font-size: 14px;
}
}
