css:
@-moz-document domain("doodly.io")
{
#m {
    background-color: #383838;
    color: #f2f2f2;
}

#messages li {
    background-color: #595959;
    color: #c4ecff;
}

.borderThick {
    border: 1;
    border-color: #141414;
    border-radius: 0;
}

.borderThin {
    border-color: darkred;
}

.overlayRules .content {
    -webkit-text-fill-color: white;
    font-family: "Libre Franklin";
    font-size: 1rem;
    font-style: normal;
    font-weight: normal;
    height: 100%;
    padding: 5px;
    position: relative;
    text-shadow: none;
}

::-webkit-scrollbar {
    display: none;
}

body {
    background-color: black;
    background-image: url(https://i.imgur.com/cHeRBvl.png);
    background-size: 2%;
}

body.vsc-initialized {
    color: #2bf0a1;
}

button.btn.btn-block.lobbyitem {
    background-color: #736f73;
    color: #e8e8e8;
}

div.avatarGame avatarHat {
}

div.avatarSelectors {
    color: #ffffff;
}

div.borderThin {
}

div.containerChatMessages {
    background-color: #524f52;
}

div.containerGame.borderThick {
    background-color: #424242;
    color: #492dd4;
}

div.containerLobbyList {
    background-color: #1c1c1c;
    border-color: #000000;
    border-style: solid;
    border-width: 2px;
    height: 105%;
    padding-right: 3px;
    padding-top: 10px;
    width: 101%;
}

div.containerLobbyMenu.borderThick {
    background-color: #606a94;
    border-style: groove;
}

div.containerLobbyUser.borderThick {
    background-color: #424242;
    color: #a35ba3;
}

div.containerWelcome.borderThick {
    background-color: #444c59;
    font-family: Helvetica;
}

div.contentPadding.playerNameLobby {
    color: #ff0000;
    text-shadow: none;
}

div.d-block.content.gradLobby.h-100 {
    background-color: #444c59;
    border-color: #786f78;
    color: #f7eff7;
    font-family: Roboto;
}

div.gameStatus {
    background-color: #474747;
    color: #c27019;
}

div.gradLobby {
    background: none;
}

div.information.h-100 {
    background-color: #444c59;
    color: #e82ee8;
}

div.information.h-100 img {
    margin-top: 50px;
}

div.information.h-100 p {
    color: #d18cd1;
    font-family: Roboto;
    font-size: 16px;
}

div.modal-body {
    color: #8f008f;
}

div.modal-body p span {
    color: #000000;
}

div.modal.modal2.fade.show {
    border-color: #ffffff;
}

div.overlay.overlayRules.borderThick.open {
    background-color: #444c59;
    font-family: Lato;
}

div.playerName {
    border-color: #eb59eb;
    border-style: none;
    color: #99c3eb;
    font-family: monospace;
    text-shadow: none;
}

div.playerRank {
    color: white;
    text-shadow: none;
}

div.playerScore {
    color: #46b5b1;
    text-shadow: none;
}

div.roundStatus {
    color: #ffffff;
}

div.userElement.borderThin.overlayTrigger {
    background-color: #1f1f1f;
    border-color: #757575;
}

div.userElement.borderThin.overlayTrigger.guessed {
    background-color: #315fcc;
}

div.wordHint {
    background-color: #000000;
    color: #ff8c8c;
}

i.fas.fa-chevron-circle-left.fa-2x {
    color: #ffffff;
}

input.form-control {
    background-color: #424242;
    border-color: black;
    border-radius: 5px;
    color: #f5f5f5;
    font-family: monospace;
    letter-spacing: 0;
    margin: 0;
    margin-left: 15pxpx;
    margin-right: 25pxpx;
    padding: 0;
    padding-left: 11px;
    text-transform: capitalize;
    width: 50%;
}

p.scoreElement {
    border-color: #c752c7;
    color: #000000;
}

select.form-control {
    background-color: #424242;
    border-color: #000000;
    border-radius: 0;
    color: #f5f5f5;
    height: 25px;
    margin: 0;
    margin-left: 55pxpxpx;
    padding: 0;
}

span.input-group-text {
    margin-bottom: 5px;
    margin-left: 2px;
    margin-right: 5px;
    padding: 3px;
}

}
