css:
@-moz-document domain("optifine.net") {
body {
    background-image:url("https://i.imgur.com/7kuq5kw.png");
    background-size:64px;
}
tbody {
    background-color: #565656;
    border-color:black;
}
.tableRoot {
    margin: 0 auto;
    width: 800px;
    background: #7d7d7d;
    border: 2px solid rgb(128,128,128);
    color: #d6d6d6;
}
.tableNav {
    border-radius:0px;
    padding:2px;
    background: rgb(107, 107, 107);
}
.tableNav TR TD {
    padding-left: 8px;
    padding-right: 8px;
    border-radius:5px;
    margin-left:2px;
}
.title, .footer {
    background: rgb(82, 82, 82);
    color: white;
}
A, .title A, .tableNav A, .footer A {
    color: /*[[colorsec]]*/;
    text-decoration: none;
    text-shadow: 0 0 1px /*[[colorsec]]*/;
}
A:hover, .title A:hover, .tableNav A:Hover {
    color: /*[[colorhlt]]*/;
    text-shadow: 0 0 2px /*[[colorhlt]]*/;
}
.downloadLineFirst {
    background: linear-gradient(to right, rgb(64, 64, 64) 0%,rgba(86,86,86,0) 100%);
}
}
