@-moz-document domain("yls8.mtheall.com") {
body
{
    background-color: #212121;
    color: #E0E0E0;
    text-align: center;
    font-family: 'Roboto', sans-serif;
}

a:link
{
    color: #9E9E9E;
}

a:visited
{
    color: #B39DDB;
}

a:hover
{
    color: #F48FB1;
}

h3 a
{
    font-size: 25px;
    text-decoration: none;
    border-style: groove;
    border-radius: 10px;
    padding-right: 8px;
    padding-left: 8px;
    padding-bottom: 4px;
    padding-top: 4px;
    background-color: #263238;
}

h3 a:link
{
    color: #78909C;
}

h3 a:visited
{
    color: #78909C;
}

h3 a:hover
{
    color: #90A4AE;
    background-color: #455A64;
}

h3
{
    text-align: center;
}

table
{
    margin: auto;
}

iframe
{
    display: block;
    margin: auto;
}

table,
iframe
{
    border-style: groove;
    border-radius: 8px;
    border-color: #455A64;
    box-shadow: 0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22);
    background-color: #263238;
}
}

@-moz-document url("https://yls8.mtheall.com/ninupdates/scanstatus.php") {
body
{
    background-color: #263238!important;
}
}
