css:
@-moz-document domain("eksisozluk.com") {
body {
background-color: #efeed2 !important;
color:#222222 !important;
}

a {
color: #883c1a !important;
}

.more-data {
background-color: #C4C2A5 !important;
border: 1px solid #c2c2a9 !important;
}

html.no-touch a.more-data:hover {
background-color: #BBB89B !important;
}

html.no-touch #threads li article:hover {
background-color: #C4C2A5 !important;
}

.pager > a {
background-color: #C4C2A5 !important;
border: 1px solid #c2c2a9 !important;
}

html.no-touch .pager > a:hover {
background-color: #BBB89B !important;
}

.topic-list li {
border-top:1px dotted #c2c2a9 !important;
}

h1, h2, h3, h4, h5, h6 {
color:#222222 !important;
}

h1 small, h2 small, h3 small, h4 small, h5 small, h6 small {
color: #222222 !important;
}

.share-dialog, #in-topic-search-options {
background-color: #C4C2A5 !important;
border:1px solid #c2c2a9 !important;
border-top:none !important;
}

html.no-touch #in-topic-search-options > li > a:hover {
background-color: #BBB89B !important;
}

.dropdown .dropdown-menu {
background-color: #C4C2A5 !important;
border:1px solid #c2c2a9 !important;
border-top:none !important;
}

.dropdown .dropdown-menu li.seperated {
border-top:1px solid #c2c2a9 !important;
}

#top-bar > #advanced-search-form {
background-color: #C4C2A5 !important;
border:1px solid #c2c2a9 !important;
border-top:none!important;
}

#threads li article h2 {
color:#222222 !important;
}

#threads li article.unread h2 {
color:#bd362f !important; /* buna dokunma */
font-weight: bold;
}

.modal {
background-color: #BBB89B !important;
border: 1px solid #c2c2a9 !important;
}

.tabs>li.active:hover {
background-color: #BBB89B !important;
}

.tabs>li:hover {
background-color: #BBB89B !important;
}

html.no-touch .dropdown .dropdown-menu li > a:hover {
background-color: #BBB89B !important;
}

.confirmbox {
background-color: #BBB89B !important;
border: 1px solid #c2c2a9 !important;
}

html.no-touch .topic-list li > a:hover {
background-color: #C4C2A5 !important;
}

body > header {
background-color: #3c3519 !important;
border-bottom:1px dashed #c2c2a9 !important;
}

#top-navigation > ul > li > a, #sub-navigation > ul > li > a {
color: #C4C2A5 !important;
}

#sub-navigation {
background-color: #3c3519 !important;
}

#a3-toggle {
color: #C4C2A5 !important;
}

#user-notifications ul li {
background-color:#BBB89B !important;
border:1px solid #c2c2a9 !important;
border-top:none !important;
}

#profile-cards > li > h3, #profile-cards > li h4 {
border-bottom: 1px solid #c2c2a9 !important;
}

.dropdown .dropdown-menu li.separated {
border-top: 1px solid #c2c2a9 !important;
}

.edittextbox.with-helpers {
border:2px double #c2c2a9 !important;
background-color: #efeed2 !important;
color: #222222 !important;
}

.edittools {
border:0px !important;
}

input[type="text"]:focus, input[type="email"]:focus, input[type="search"]:focus, input[type="datetime"]:focus, input[type="date"]:focus, input[type="password"]:focus, textarea:focus, button:focus, input[type="submit"]:focus, input[type="button"]:focus {
border-color: rgba(194, 194, 169)!important;
box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1) inset, 0 0 2px rgba(163, 134, 58, 0.6)!important;
}

#message-search-form > input[type="search"] {
background-color: #efeed2 !important;
border: 1px solid #c2c2a9 !important;
}

input, select, textarea {
color: #222222 !important;
background-color: #efeed2 !important;
border: 1px solid #c2c2a9 !important;
}

fieldset legend {
color: #333333 !important;
border-bottom: 1px double #c2c2a9 !important;
}

#message-thread .incoming, #message-thread .outgoing {
background-color: transparent !important;
border: 1px double #c2c2a9 !important;
}

#site-footer {
border-top: 1px dashed #c2c2a9 !important;
}

#threads li article p {
color: #222222 !important;
}

#threads li + li {
border-top: 1px solid #c2c2a9 !important;
}

.topic-list li > a.empty-index-item {
color: gray !important;
}

#stats li .value {
color: #222222 !important;
}

.popped-player {
background-color: #C4C2A5 !important;
border: 1px solid #c2c2a9 !important;
}

.tabs {
background-color: #E1E0D7 !important;
border-bottom: 1px solid #c2c2a9 !important;
}

.tabs > li.active {
border: 1px solid #c2c2a9 !important;
border-bottom:none!important;
}

#entry-list > li:before {
border-bottom: 1px solid #c2c2a9 !important;
}

#user-badges a, #user-badges a:visited {
background-color: #726645 !important;
color: #ffffff !important;
border: 1px outset #353535 !important;
}

#top-navigation > ul .new-update, #sub-navigation > ul .new-update {
color: #fef5f3 !important;
font-weight: bold !important;
background-color: rgb(206, 59, 16) !important;
box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.1) inset, 0px 0px 8px rgb(97, 28, 8) !important;
}

button, input[type="submit"], input[type="button"] {
background-image:none !important;
border:1px solid #c2c2a9 !important;
color:#ffffff !important;
background: #5F5A43 !important;
}

.ui-autocomplete {
background-color:#efeed2 !important;
border:1px solid #c2c2a9 !important;
-webkit-border-radius:3px;
-moz-border-radius:3px;
border-radius:3px;
-webkit-box-shadow:0 5px 10px rgba(0,0,0,0.25);
-moz-box-shadow:0 5px 10px rgba(0,0,0,0.25);
box-shadow:0 5px 10px rgba(0,0,0,0.25);
position:absolute;
z-index:1000;
margin-top:-1px
}

.ui-autocomplete li a:hover {
background-color: #C4C2A5 !important;
}

.ui-autocomplete li a.ui-state-focus {
background-color: #C4C2A5 !important;
}

#topic-channel-info #topic-channels li.channel > a {
background-color: #C4C2A5 !important;
border: 1px solid #c2c2a9 !important;
}

html.no-touch #channel-follow-list li:hover {
    background-color: #C4C2A5 !important;
}

#entry-list .hidden, #entry-list .deleted {
    background-color: #C4C2A5 !important;
}

.topic-list.partial > li >a {
    padding: 3px 20px 3px 0;
    line-height: 1.3;
}

.topic-list.partial>li>a>small {
    position: absolute;
    font-size: 0.75rem;
    right: 0;
    padding: 0 0 0 5px;
    margin: 0;
    top: 30%;
    color: #888 !important;
}

.topic-list.partial>li:hover>a>small {
    color: #000 !important;
}

::selection { background-color: #222; color: #eaeaea; }
::-moz-selection { background-color: #222; color: #eaeaea; }

#top-bar { display: flex; }
#logo { display: none; }
#nav-extra-buttons { display: none !important; }
#sub-navigation { order: 2; float: left; margin-left: -20px; position: relative; }
#sub-navigation > ul > li { padding: 0 8px !important; }
#sub-navigation > ul > li > a { font-size: 0.8rem; border-bottom: 3px solid transparent; line-height: 1.5; margin-top: 11px; }
#top-navigation + div { display: none !important; }
#top-navigation { order: 3; position: absolute; right: 0; height: 50px !important; vertical-align: super; }
#top-navigation > ul > li > a { vertical-align: top; line-height: 40px; padding: 0 8px 0 0; }
#top-bar .eksico { fill: #C4C2A5 !important; }

/*
#quick-index-nav a[href="/basliklar/kanal/ili%C5%9Fkiler"] { display: none; }
#quick-index-nav a[href="/basliklar/tarihte-bugun"] { display: none; }
#quick-index-nav a[href="/basliklar/kanal/seyahat"] { display: none; }
*/

html>body>header #top-bar { height: 40px; width: 100%; }

#search-form { margin: 6px 0 0 1px; }

body > div.container { display: none; }
#main { padding-top: 0  !important; margin-top: -75px !important; }
#index-section { top: 0 !important; margin-top: 30px !important; }

svg { fill: #888 !important; }
.pager { color: #000; }
}
