css:
@-moz-document domain(tujavortaro.net) {

body {
  margin: 0;
  padding: 0;
  height: 100%;

  background-color: #2A2A2E;

  /* Ĉiam montru la dekstran glitilon por preventi retumilan saltadon. */
  overflow-y: scroll;
  color: #BABABA;
}

#wrapper {
  min-height: 100%;
  position: relative;
}

/* Uzata de ĝenerala retumilo, ĉar kutime oni rigardas supere. */
header {
  margin: 0;
  padding: 0 0 0 10px;
  top: 0;

  background-color: #00B000;

  width: 100%;
  height: 50px;

  display: flex;
  position: fixed;
  justify-content: center;
  align-items: center;
}

#header-right {
  margin-left: auto;
  margin-right: 30px;
}

/* Uzata nur de FxOS, ĉar la tajpilo aperos ĉe la subo. */
#footer {
  margin: 0;
  padding: 0;

  position: fixed;
  bottom: 0;

  background-color: #00B000;

  width: 100%;
  height: 50px;

  display: flex;
  position: fixed;
  justify-content: center;
  align-items: center;
}

.results-web {
  padding-top: 55px;
}

.results-phone {
  padding-bottom: 55px;
}

/* Even result rows. */
.resultrow-0 {
  padding: 5px 10px 5px 10px;
  background-color: #2A2A2E;
}

/* Odd result rows. */
.resultrow-1 {
  padding: 5px 10px 5px 10px;
  background-color: #3B3B3B;
}

.eo-result {
  color: #00E600;
}

.eo-informacio {
  color: #D4D4D4;
}

.en-result {
  color: #DBDBDB;
  margin-left: 10px;
}

a.fonto {
  text-decoration: none;
  color: #ffffff;
}

a.fonto:hover {
  text-decoration: underline;
}

hr.exactsep {
  padding: 0px;
  margin: 0px;
  border: 0;
  height: 1px;
  background: #333;
}

input[type="text"] {
  align-self: center;
  font-size: 18px;

  outline: none;
  padding: 3px;
  margin: 5px 5px 3px 0;
  border: 1px solid #006600;
  box-shadow: 0 0 5px #007700;
}

select {
  align-self: center;
  margin-left: 3px;
}

}
