@-moz-document domain("jsfiddle.net") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#app-updates .body,
#hello-bar .upgradeToPro em,
#layout label,
#sidebar .sidebarItem .toggler #resource-counter,
#sidebar .sidebarItem .toggler .resourcesLabel,

.checkboxCont small,

li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

#hello-bar .upgradeToPro strong,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

#editor-options .selectPair .label,

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

.checkboxCont.disabled {
    color: #adadad;
}

/* Misc 
_____________________________________________________________________________________________________*/

.token.punctuation {
    color: #fff!important;
}

.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword {
    color: #00b3ff!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #ff6cbe!important;
}

.CodeMirror-focused .CodeMirror-activeline .CodeMirror-linenumber {
    color: #ff0;
}

.CodeMirror pre,
.CodeMirror-linenumber {
    color: #fff;
}

.cm-s-default .cm-bracket {
    color: #e7e7e7;
}

.cm-s-default .cm-keyword {
    color: #c669ff;
}

.cm-s-default .cm-meta {
    color: #99ebff;
}

.cm-s-default .cm-number {
    color: #edd65c;
}

.cm-s-default .cm-property {
    color: #ff9797;
}


}

