css:
@-moz-document domain("rbx-forum.com") {
/*Another product created by BladefuryDev*/

/*Thanks to BladefuryDev for the script*/

/*----------------------------------------------------*/
.forum-content-background,
.forum-table-row {
    background-color: #b9b9b936;
}


body .forum-table-header,
body .forum-table-header th,
body .forum-table-footer {
    background-color: #313131;
}


body {
    background-color: #313131;
    margin: 0;
}


#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    width: 970px;
    background-color: #444;
}


a.linkSmallBold:visited,
a.linkMenuSink:visited {
    font-weight: normal;
    color: #000;
}


.CenterColumn a.btn-control {
    margin-right: 5px;
    color: #000;
    box-shadow: 0px 1px 2px 0px #0000;
}


.CenterColumn input.btn-control,
.CenterColumn select {
    margin-right: 0px;
    box-shadow: 0 1px 2px #fff0;
}


input[type=text] {
    border: 1px solid #000;
    padding-left: 5px;
    box-shadow: 0 1px 2px #fff0;
}


.normalTextSmall,
.normalTextSmallBold,
.normalTextSmaller {
    color: #000000;
    text-shadow: 2px 4px 20px #dfdfdf00;
}

element.style {
    box-shadow: 0 1px 7px #ccc0;
}


a.post-list-subject,
a.post-list-subject:hover,
a.post-list-author,
a.post-list-author:hover {
    text-decoration: none;
    color: #000000;
    text-shadow: 0px 6px 20px #fff0;
}


a.forum-summary,
a.forum-summary:hover {
    text-decoration: none;
    color: #000000;
    text-shadow: 0 1px 20px #bfbfbf00;
}




h2,
h2 a:link,
h2 a:visited,
h2 a:active,
h2 .text {
    font-size: 30px;
    font-weight: normal;
    color: #000c;
    letter-spacing: -1px;
    text-decoration: none;
    text-shadow: 0px 4px 20px #0000;
}

table#ctl00_cphRoblox_PostView1_ctl00_PostList {
    box-shadow: 0 1px 13px #0000;
}

table#ctl00_cphRoblox_ThreadView1_ctl00_ThreadList {
    box-shadow: 0px 1px 13px #0000;
}

table.table {
    border-top: none;
    box-shadow: 0 1px 13px #0000;
}

.forumTitle {
    font-size: 1.0em;
    font-weight: bold;
    color: #7d7d7d;
}
}
