css:
@-moz-document domain("jeuxvideo.com") {
/* Mes changements */
.gameHeaderBanner,
#jv-footer .legal {
    background: #1c1c1c;
}
.gameHeaderSubNav,
#jv-footer .highlight {
    background: #202020;
}
#jv-footer {
    background-color: #292929;
}
#jv-footer .external-links .social-networks {
    border-color: transparent!important;
}
#jv-footer .external-links .headline {
    color: #d0d0d0;
}
@media screen and (min-width: 50.4375rem) {
    .gameHeaderSubNav__itemLink {
        color: #d0d0d0;
    }
    .gameHeaderSubNav__itemLink:not(.gameHeaderSubNav__itemLink--active):not(.gameHeaderSubNav__itemLink--disabled):hover {
        color: #e7e7e7!important;
        background-color: #292929;
    }
    .gameHeaderSubNav__itemLink--active,
    .gameHeaderSubNav__item--active::before {
        background-color: #262626;
    }
    .gameHeaderBanner__bottom {
        display: none;
    }
    .gameHeaderSubNav__item:not(.gameHeaderSubNav__item--active) {
        border-color: transparent!important;
    }
    .gameHeaderBanner__platforms {
        display: inline-block;
        float: right;
        margin-top: 0.312rem;
    }
    .gameHeaderBanner__platformLink--active,
    .gameHeaderBanner__title {
        color: #e7e7e7;
    }
    .gameHeaderBanner__platformLink--active {
        -webkit-box-shadow: 0 2px 0 #cd4c2c;
        box-shadow: 0 2px 0 #cd4c2c;
    }
    .gameHeaderBanner__platform {
        margin-right: 0.625rem;
    }
    .jv-header-menu .live-indicator::before {
        top: -25%;
        left: 90%;
    }
    .jv-header-menu .live-indicator::before {
        background-color: #858585;
        border-color: #7b7b7b;
        -webkit-box-shadow: 0 0 0 0.0625rem #202020 inset;
        box-shadow: 0 0 0 0.0625rem #202020 inset;
    }
}
@media screen and (max-width: 74.9375rem) {
    .jv-header-menu .jv-nav-open,
    .jv-header-menu .jv-main-nav.on:after,
    .jv-header-menu .jv-nav-platform {
        background-color: #292929;
    }
    .jv-header-menu .jv-nav-platform .jv-nav-lvl1-item,
    .jv-header-menu .jv-nav-primary .jv-nav-lvl1-item,
    .jv-header-menu .jv-nav-primary .jv-nav-lvl2-item,
    .jv-header-bottom .jv-nav-primary .jv-nav-toggler {
        border-color: #3c3c3c!important;
    }
    .jv-header-menu .jv-header-bottom .jv-nav-primary .live-indicator {
        border-color: transparent;
        background-color: transparent;
    }
    .jv-header-menu .jv-header-bottom .jv-nav-primary .jv-nav-toggler:after {
        border-left-color: rgb(41, 41, 41);
    }
    .jv-header-menu .jv-header-bottom .jv-nav-primary .jv-nav-toggler:before {
        border-left-color: #bcbcbc;
    }
    .jv-nav-open.on .jv-nav-open-hamburger {
        background: #2E2E2E!important;
    }
    .jv-nav-lvl1-item-toggler .jv-nav-toggler-more,
    .jv-nav-lvl1-item-toggler .jv-nav-toggler-more:after,
    .jv-nav-lvl1-item-toggler .jv-nav-toggler-more:before {
        background-color: #e0e0e0!important;
    }
    .jv-header-bottom .jv-nav-primary .jv-nav-toggler:hover {
    background-color: #434343!important;
}
}
}
