css:
@-moz-document domain("en.internationalism.org") {
:root {
    --tc: #a5a5a5 !important;
    --tc2: #b6b6b6 !important;
    --bgc: rgb(15, 15, 15) !important;
    --border: rgb(195, 195, 195);
}

body, div {
    background: var(--bgc);
    color: var(--tc);
}

input {
    background: var(--bgc);
    border: solid var(--tc) 1px !important;
    color: rgb(187,187,187);
}

a {
    color: #d0132b !important;
}

p, span {
    color: var(--tc);
    
}

h1.title, h2.title, h4 {
    color: var(--tc2);
}

#languages {
    margin-left: 95px !important;
}

#block-nice_menus-1 {
    width: 120% !important;
}

#nice-menu-1 {
    border-right: solid var(--border) 1px !important;
    margin-left: -10px !important;
}

#nice-menu-1 li a {
    background: rgb(40, 40, 40) !important;
    padding: 8px !important;
    border-left: solid var(--border) 1px !important;
    border-top: solid var(--border) 1px !important;
    border-bottom: solid var(--border) 1px !important;
}

#nice-menu-1 li ul li a {
    border-right: solid var(--border) 1px !important;
}

ul.nice-menu li ul {
    border-top: transparent !important;
}

li {
    border: transparent !important;
}

#block-block-13 div div a img {
    margin-left: -8px !important;
}

#block-search-0, #block-simplenews-1980, #block-relatedlinks-3, #block-views-b9cecaa0570613fe18c0e7d902d5ddbc, #block-forum-0 {
    position: relative !important;
    width: 275px !important;
    right: -105px !important;
}

#block-book-0 {
    position: absolute !important;
    width: 245px !important;
    left: -90px !important;
}

#content-inner {
    position: absolute !important;
    left: 210px !important;
    width: 640px !important
}

#page-inner {
    position: absolute !important;
    width: 1150px !important;
    left: 100px !important;
}
}
