css:
@-moz-document url-prefix("https://runnerid.global-pix.com/v3/") {
html, body, ._4rtl {
    background-repeat: repeat !important;
    background-color: black !important;
}
#RelatedPhotosPanel .tabs {
  background-color: black;
  border-bottom: 1px solid #404040;
}
#RelatedPhotosPanel .container .image-container {
  background-color: black;
}
#RelatedPhotosPanel .container .image-container .inner-container .image-card .image-card-box {
  border: 9px solid black;
  background-color: black;
}
#RelatedPhotosPanel .container .image-container .inner-container .image-card .image-card-box .image-card-footer {
  background-color: black;
}
#RelatedPhotosPanel .container .image-container .inner-container .image-card .image-card-box .image-card-edit-box {
  border-top: 1px solid #404040;
  outline: 2px solid #404040;
  background-color: black;
  -webkit-box-shadow: 0 10px 20px rgba(0, 0, 0, 0.4), 0 2px 2px rgba(0, 0, 0, 0.4);
          box-shadow: 0 10px 20px rgba(0, 0, 0, 0.4), 0 2px 2px rgba(0, 0, 0, 0.4);
}
#RelatedPhotosPanel .checkbox-styled + label:before {
  background: black;
  border: 1px solid #404040;
}
#RelatedPhotosPanel .checkbox-styled:disabled + label:before {
  background: black;
  border: 1px solid #404040;
}
.VerticalBibFields input[type=text] {
  color: white;
  background-color: black;
  border: 2px solid #404040;
}
.VerticalBibFields input.highlighted:not(.red) {
  color: white;
  background-color: black;
  border-color: rgba(58, 105, 205, 0.5);
  -webkit-box-shadow: 0 0 0 3px rgba(58, 105, 205, 0.5);
          box-shadow: 0 0 0 3px rgba(58, 105, 205, 0.5);
}
.VerticalBibFields input.highlighted:not(.red).active {
  border-color: #3a5fcd;
}
div#VerticalBibFieldsContainer {
    color: lightgray;
}
.VerticalBibFields input.disabled {
  border-color: #404040;
  -webkit-box-shadow: 0 0 0 3px #404040;
          box-shadow: 0 0 0 3px #404040;
  background-color:#404040;
  color: black;
}
.VerticalBibFields {
  border: 1px solid #404040;
  outline: 1px solid #404040;
  background-color: black;
}
.VerticalBibFields.highlight {
  outline: 1px solid #404040;
}
#RelatedPhotosPanel {
    outline: 1px solid #404040;
    border: 1px solid #404040;
}
div#TopPanelHeader {
    color: lightgray;
}
div#TopPanel Button {
    color: black;
}
#TopPanel button {
    background-color: #708ADB;
    border: 1px solid #617ED7;
}
#stop-button button {
    background-color: #d9534f;
    border-color: #d43f3a;
}
#TopPanel #last-bibs {
    color: lightgray;
}
.lastbib {
    color: black;
}
#RelatedPhotosPanel .tabs button.active {
    background-color: #708ADB;
    border: 1px solid #617ED7;
    color: black;
}
#RelatedPhotosPanel button {
    background-color: gray;
    border: 1px solid gray;
    color: lightgray;
}
h1 {
    color: lightgray;
}
h3 {
    color: lightgray;
}
button#logout-button {
    color: black;
}
div#start-button button {
    color: black;
    background-color: #708ADB;
    border: 1px solid #617ED7;
}
div#messages {
    color: lightgray;
}
}
