css:
@-moz-document domain(baidu.com){
#content_right, #foot {
	display:none;
}
#content_left {
	width:70%;
}
wrapper_wrapper {
	padding-left:12%;
}
#container {
	width: 92%;
	padding-left: 8%;
}
.c-container {
	padding: 10px 25px!important;
}
div[style='display:block !important;visibility:visible !important'], #content_left div[data-xst], #content_left div[data-pos] {
	height: 0;
	position: absolute;
	opacity: 0;
	left:-9999px;
}
#content_left .result, #content_left .result-op {
	position: relative;
	overflow: hidden;
}
#content_left .c-span18 {
	width: 85%;
}
#content_left .result .f13, #content_left .c-span-last p:nth-last-child(2) {
	position: absolute;
	top: 0;
	right: 10px;
}
#content_left .result .f13 {
	margin-right: 0;
	top:10px;
}
#content_left .result div.bbs {
	margin-right:35%;
}
#content_left a[href*='cache.baidu'], #content_left .op-bk-polysemy-move {
	display:none;
}
.c-span24, .c-container {
	width:100%!important;
}
#container #rs tr {
	display: table-cell;
}
#container #rs th {
	padding-right: 15px;
}
#content_left .result p.m, .c-tip-icon i, .c-icon-tieba, .c-icon-zhidao, .c-icon-bear-p, .c-icon-bear-pn {
	display:none;
}
#page {
	margin-top:0;
}
#form {
	width:80%!important;
}
#form .s_ipt_wr {
	width:60%!important;
}
#form .s_ipt {
	width:100%!important;
}
#form .s_btn_wr {
	margin-top:0;
	border: 1px solid #adadad;
	width: 40px;
	height: 40px;
	margin-left:4%;
}
#form .btnhover {
	opacity:0
}
#head {
	padding-left:14%;
}
#head #user {
	padding-left:0;
	padding-top: 15px;
	background:none;
        text-indent: 0px;
        overflow: hidden;
        width: 24px;
}
#head #u .pf, #head #imsg {
	display:none;
}
#head #u .usermenu {
	margin-top: 40px;
	margin-left:60px;
}
#wrapper #s_tab a {
	width:6.5%;
}
#wrapper #s_tab {
	padding-left: 14%;
}
#page {
	margin-top:10px;
}
#u {
	left:65%;
}
#rs .tt {
	float:left;
	width:80px;
}
#rs table {
	width:80%;
	display: inline-block;
}
.result-op .op_dict3_extra {
	position: absolute;
	top: 0;
	left: 30%;
	margin-bottom: 0!important;
	border: 0;
	padding-top: 0;
	border-top:0!important;
}
.result-op .c-border {
	width:100%;
	margin-bottom:0;
	margin-top:0;
	padding-bottom:0;
	border-bottom:0!important;
}
.result-op div.c-gap-top-small {
	position: absolute;
	top: 7px;
	left: 15%;
}
.result-op .op_dict3_lineone_result {
	display: inline-block!important;
}
.result-op .op_dict3_linetwo_result {
	display: inline-block!important;
	margin-bottom: 0;
}
.op-tieba-general-maintable tr {
	display: inline-table;
	width:50%;
}
.op-tieba-general-maintable tr:first-child {
	float:left;
}
.op-tieba-general-maintable .op-tieba-general-mainpl {
	width:60%;
}
.op-tieba-general-maintable td {
	float: left;
	min-width: 100px;
}
.c-span6 .c-img6 {
	width:60px;
}
.op_dict3_lineone_result_tip{
    POSITION: ABSOLUTE;
    LEFT: -20PX;
}
.op_dict3_lineone_result span {
    float: left;
    width: 100%!important;
    MARGIN-LEFT: 40PX;
}
.result-op .op_dict3_lineone_result{width:50%;}

#wrapper #s_tab a {
    width: 10%;
}.s_tab_inner{width:70%;}
}
