css:
/* Dev-CS Night Theme [корявая]
* Version: 0.0.5
* https://userstyles.org/styles/163690/dev-cs-night-theme
*  by wopox1337
*/


@-moz-document url-prefix("https://dev-cs.ru/")
{

.pageNav-jump,
.pageNav-page:first-child,
.pageNav-page.pageNav-page--current,
.pageNavSimple-el.pageNavSimple-el--current,
.pageNavSimple-el.pageNavSimple-el--prev,
.pageNavSimple-el.pageNavSimple-el--next {
	background: linear-gradient(0deg, #252525, #888888);
	color: #d4d4d4;
	border-width: 0px;
}

.structItem {
	border-top: 1px solid #3c3c3c;
}

.message-responseRow {
	background: #424242;
}

.tagItem {
	color: #828282;
	background: #0a0a0a;
}



.p-header-logo.p-header-logo--image,
.p-nav-smallLogo img {
	/* Logo */
	filter: invert(100%);
}

.username--style12 {
	color: #7a3dfffa !important;
}

.username--style4 {
	color: #f600fffa !important;
}

.username--style3 {
	color: #ff0037fa !important;
}

.username--style1,
.username--style2,
.username--style3,
.username--style4,
.username--style5,
.username--style6,
.username--style7,
.username--style8,
.username--style9,
.username--style10,
.username--style11,
.username--style12,
.username--style13,
.username--style14,
.username--style15,
.username--style16,
.username--style17,
.username--style18 {
	color: #02b8ffb8;
}

.node + .node {
	border-top: 1px solid #353535;
}

.contentRow-main .is-unread {
	font-weight: 800;
	color: #fffffff7;
}

.input {
	background: #797979;
}

.tabs--standalone .hScroller-action.hScroller-action--end {
	/* background: #f5f5f5; */
	background: linear-gradient(to right, rgba(232, 47, 47, 0) 0%, #5c5c5cf0 33%);
}

.tabs--standalone .hScroller-action.hScroller-action--start {
	/* background: #f5f5f5; */
	background: linear-gradient(to left, rgba(232, 47, 47, 0) 0%, #5c5c5cf0 33%);
}

.tabs--standalone .tabs-tab.is-active {
	color: #ffffff;
	border-color: #608361;
}

.tabs--standalone {
	border-top-color: #97979721;
	border-right-color: #989898;
	border-bottom-color: #97979721;
	border-left-color: #d8d8d8;
	font-size: 14px;
	color: #f3f3f3;
	background: #464646;
}

.menu-footer {
	color: #505050;
	background: #232323;
	border-top: 1px solid #dfdfdf;
}

.menu-row.menu-row--alt {
	color: #dedede;
	background: #3f3f3f;
}
.menu-row.menu-row--alt:hover {
	color: #dedede;
	background: #5a5a5a;
}

.menu-header {
	color: #dedede;
	background: #5f5f5f;
	border-bottom: 1px solid #878787;
}

.menu-content {
	color: #b1b1b1;
	background: #3e3e3e;
}

.menu-row:hover {
	background: #f00;
}

.button.button--link,
a.button.button--link {
	color: #b9b9b9;
}
.breadcrumb {
	border: 1px solid #383838;
}

.breadcrumb .crust a.crumb {
	color: #b5b5b5;
	background-color: #464646;
}

.breadcrumb .crust .arrow span {
	border-left-color: #444444
}

.fr-toolbar {
	background: #212121;
	border-top: 3px solid #284c29;
}

.blockMessage.blockMessage--warning {
	border-left: 3px solid #aeab75;
	background: #1e1e1e;
	color: #906c3e;
}
.message-userExtras {
	background: #1f1f1f;
}

.breadcrumb {
	background-color: #2d2d2d;
}

body {
	color: #a9a9a9;
}

.p-nav {
	color: #a9a9a9;
}

.p-nav-list .p-navEl {
	color: #a9a9a9;
}
.block-container {
	color: #727272;
}
.p-nav-scroller .hScroller-action.hScroller-action--end {
	background: #ebebeb;
	background: linear-gradient(to right, rgba(117, 21, 21, 0) 0%, #1f1f1f 33%);
}

.p-nav-scroller .hScroller-action.hScroller-action--start {
	background: #ebebeb;
	background: linear-gradient(to left, rgba(117, 21, 21, 0) 0%, #1f1f1f 33%);
}

.node--unread .node-icon i:before,
.node--unread .no-icon i:before {
	color: #a9a9a9;
}

a {
	color: #a9a9a9;
}

.p-body {
	background: #252525 !important;
}

.p-header {
	background: #252525 !important;
}

.p-nav {
	background: #252525 !important;
}

.p-sectionLinks {
	background: #252525 !important;
	border-bottom: 1px solid #9a9a9a;
}

.p-nav-list .p-navEl.is-selected {
	background: #4e4e4e;
	border: 1px solid #5d5d5d;
}

.block-container {
	background: #1e1e1e !important;
	border: 1px solid #000;
}

.contentRow-lesser {
	color: #a9a9a9;
}

.pairs.pairs--justified > dd {
	color: #00fdff;
}

.pairs.pairs--rows > dd {
	color: #00dfff;
}

.block-header {
	background: linear-gradient(0deg, #242424, #313131);
	border: 1px solid #5d5d5d;
}
.block-minorHeader {
	background: #151515;
}

.block-tabHeader {
	background: #151515;
}

.block-tabHeader .tabs-tab.is-active {
	background: none;
	color: #edf6fd;
	border-color: #404040;
}

.p-staffBar {
	font-size: 13px;
	color: #989898;
}

.p-nav-scroller .hScroller-action {
	color: #9f9f9f;
}

.block-footer {
	color: #ababab;
	background: linear-gradient(180deg, #383838, #414141);
}

.block--messages .message,
.block--messages .block-row {
	color: #585858;
	background: #17171b;
}

.likesBar {
	background: darkgrey;
	border: 1px solid #47a7eb;
	border-left: 2px solid #47a7eb;
}

.message-cell.message-cell--user,
.message-cell.message-cell--action {
	background: #2d2d32;
}

.message-userArrow:after {
	border-right-color: #17171b;
}

.button.button--link,
a.button.button--link {
	background: #2d2d32;
}

.contentRow-fader {
	background: transparent;
}

.fr-box.fr-basic .fr-wrapper {
	background: #2d2d32;
}

.fr-box.fr-basic .fr-element {
	color: grey;
}

.ratingStarsRow.ratingStarsRow--justified .ratingStarsRow-text {
	color: #8c8c8c;
}

.structItem-resourceTagLine {
	color: #8c8c8c;
}

.bbCodeBlock {
	background: #2d2d32;
}

.bbCodeBlock-title {
	background: #17171b;
}

.tooltip--member .tooltip-content {
	background: #17171b;
}

.memberTooltip-header {
	background: #265071;
}



/* Using dark scrollbar */
::-webkit-scrollbar {
	background-color: #2e2e2e;
	width: 15px;
}

::-webkit-scrollbar-thumb {
	background-color: rgba(86, 86, 86, 0.4);
}

::-webkit-scrollbar-thumb:hover {
	background-color: rgba(102, 102, 102, 0.4);
}

textarea::-webkit-scrollbar:vertical {
	border-left: 1px solid #3e3e3e;
}

textarea::-webkit-scrollbar-thumb:vertical {
	border-left: 1px solid #3e3e3e;
}

textarea::-webkit-scrollbar:horizontal {
	border-top: 1px solid #3e3e3e;
}

textarea::-webkit-scrollbar-thumb:horizontal {
	border-top: 1px solid #3e3e3e;
}

}
