/* START Checkboxes Styles */
.hide-seen input[type="checkbox"] { 
	display: none; 
}
.hide-seen input[type="checkbox"] + label {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 20px;
  font: 14px/20px "Open San", Arial, sans-serif;
  color: /*[[primary-color]]*/;
  cursor: pointer;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
}
.hide-seen input[type="checkbox"] + label:last-child { 
	margin-bottom: 0; 
}
.hide-seen input[type="checkbox"] + label:before {
  content:"";
  display: block;
  width: 20px;
  height: 20px;
  border: 2px solid /*[[primary-color]]*/;
  position: absolute;
  left: 0;
  top: 0;
  -webkit-transition: all .12s, border-color .08s;
  transition: all .12s, border-color .08s;
}
.hide-seen input[type="checkbox"]:checked + label:before {
  width: 10px;
  top: -5px;
  left: 5px;
  border-radius: 0;
  opacity: 1;
  border-top-color: transparent;
  border-left-color: transparent;
  -webkit-transform: rotate(45deg);
  transform: rotate(45deg);
}
/* END Checkboxes Styles */
