css:
@-moz-document domain("www.bopimo.com") {
nav2 ul {
    margin: 0px 0px 10px 0px;
    padding: 6px;
    text-align: center;
    list-style-type: none;
    background-color: #56bfff;
}

.banner.warning {
    background-image:url('http://diarioveaonline.com/wp-content/uploads/2018/06/amazing-cartoon-wallpaper-for-desktop-background-49.jpg');
    background-color: #65c565;
}

html,body {
    background-color: #242424;
    margin: 0;
    padding: 0px;
    font-family: 'Quicksand', sans-serif;
    font-weight: 500;
    width: 100%;
    
}




}

@-moz-document url-prefix("https://www.bopimo.com/forum/t/") {


.forum-topic .subforum {
    border: 2px dashed #00ff00;
    overflow: auto;
    background-color: #333;
    padding: 10px;
}
}

@-moz-document url-prefix("https://www.bopimo.com/forum/b/") {


.forum-topic .subforum.pinned {
    border: 2px dashed #00ff00;
}
  .forum-topic .subforum {
    border: 2px dashed #00ff00;
    border-radius: 1px;
    margin-bottom: 10px;
    overflow: auto;
    background-color: #595959;
    padding: 10px;
}
}

@-moz-document url-prefix("https://www.bopimo.com/forum/create_thread") {


.dotted-body {
    border: 6px dashed #00ff00;
    border-radius: 5px;
    margin-bottom: 10px;
    overflow: auto;
    background-color: #4d4d4d;
    padding: 10px;
}
element.style {
    height: 200px;
    z-index: auto;
    position: relative;
    line-height: normal;
    font-size: 15px;
    transition: none 0s ease 0s;
    background: #1e1c2a !important;
}
}
