css:
@-moz-document url-prefix("https://idp.feide.no/simplesaml/module.php/feide/login.php") {
.layout .main {
    background: #303030 no-repeat scroll url(https://image.ibb.co/k7ko3U/emblem_white_cut.png);
    border: 1px solid gray;
    border-width: 1px 1px 0px 1px !important;
}

.layout.content {
    border: 1px solid gray;
    margin-top: 20px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
}

.layout,
.expandable {
    background-color: #252525;
    border: 1px solid gray !important;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23);
}

body {
    background: #222;
    background-image: url("https://orig00.deviantart.net/633e/f/2012/076/3/f/salt_and_pepper_tiled_wallpaper_by_evolventity-d4t2c6f.png")
}

.layout.content {
    background: #181818;
    color: white;
}

a:link,
a:visited {
    color: #eee;
}

img.orglogo {
    background: url("https://image.ibb.co/fWNhxp/987eff4d22d6b17e01d675ea4a67a9b0aa7fa9ead6d85ad4cb3327c7a0e51601.png");
    background-size: 64px 64px;
    text-indent: -9999px;
    display: none;
}

img {
    display: none;
}
}
