css:
@-moz-document domain("ambito.com") {
:root {
  --main-bg-color: #1f1f1f;
  --main-text-color: #c7c7c7;
  --main-social-nav-vis: none;
}


body {
    background-color: #292929 !important;
    color: #d6d6d6 !important;
}

.despliegue-noticia p {
    color: #b9b9b9;
}

.comm-section article > p {
    color: var(--main-text-color);
}

#noticia .dia {
    color: var(--main-text-color);
}

iframe#twitter-widget-0 {
    position: relative;
    width: 0px;
    height: 0px;
    max-width: 0px;
    min-height: 0px;
}

nav.redes-sociales {
    display: none;
}

.portada-article .portada-article-titulo {
    background-color: var(--main-bg-color);
}
.portada-article .portada-article-titulo > h4 {
    color: var(--main-text-color);
}

.opciones li.active + .desplegable {
    background-color: var(--main-bg-color);
}

.modulo-economia > .container-fluid > .row {
    background-color: var(--main-bg-color);
}

.opciones .item {
    background-color: var(--main-bg-color);
}

.opciones .item.active {
    background-color: var(--main-bg-color);
}

.opciones li.active + .desplegable {
    border-bottom: 5px solid var(--main-bg-color);
}

#encuesta {
    background-color: #292929 !important;
}

.header-ambito-financiero {
    padding: 15px;
}

.header-ambito-financiero .header-ambito-logo {
    padding: 0px;
}

.editors-carousel {
    background-color: var(--main-bg-color) !important;
    padding-top: 10px;
    padding-left: 15px;
    padding-right: 15px;
    height: auto;
}

div .slick-slide .editors-content {
    color: var(--main-text-color);
}

div .slick-slide .editors-content h5{
    margin-top: 20px;
    font-weight: bold;
}

.mod-esp > div > section {
    background-color: var(--main-bg-color) !important;
}

.mod-esp > div > section .mod-esp-bajada {
    color: var(--main-text-color) !important;
}

.social-min {
    display: var(--main-social-nav-vis) !important;
}

.fw-varios {
    background-color: var(--main-bg-color) !important;
}

}
