css:
@-moz-document url-prefix("https://www.tapatalk.com/groups/nuzlocke_forum/") {
/* main body formatting */
#phpbb {
    background: #5a70b3;
    font-size: 11px;
    font-family: "Helvetica", "Arial", "Bitstream Vera Sans", "Verdana", sans-serif;
}

.site-description,
.navbar {
    background: #445a9e;
    background: -moz-linear-gradient(top, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
    background: -webkit-linear-gradient(top, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
    background: linear-gradient(to bottom, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
    border: 1px solid #3a5197;
    border-left: none;
    border-right: none;
    padding-bottom: 15px;
}

.particles_container {
    background: #5a70b3;
    /* Old browsers */
    background: -moz-linear-gradient(top, #5a70b3 60%, #e86a03 61%, #fc8705 83%, #fb9d3a 100%);
    /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #5a70b3 60%, #e86a03 61%, #fc8705 83%, #fb9d3a 100%);
    /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #5a70b3 60%, #e86a03 61%, #fc8705 83%, #fb9d3a 100%);
    /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}

.forabg,
.forumbg,
.viewtopic_wrapper {
    background: #445a9e;
    background: -moz-linear-gradient(top, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
    background: -webkit-linear-gradient(top, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
    background: linear-gradient(to bottom, #445a9e 1%, #536aae 40%, #566cb0 70%, #5f75b9 100%);
}

div#inner-wrap {
    background: #fff;
    border: 1px solid #3a5197;
    padding: 15px!important;
}
ul.topiclist.forums {
    display: block;
    list-style-type: none;
    margin: 0;
    border-top: 1px solid #3a5197;
}
ul.topiclist {
    border-top: 1px solid #3a5197;
}

.header {
    border: 1px solid #566caf;
    color: white!important;
}
.header a {
    color: white!important;
}

body.content_block_header_block li.header dd,
body.content_block_header_block li.header dt,
.topiclist .header .flex-title .start,
ul.topiclist .header .row-item .list-inner,
ul.topiclist li.header dt .list-inner a {
    color: white;
}



/* forum and thread list formatting */
.forum-unread-mark.tapatalk-theme-background {
    width: 66px!important;
    background-color: transparent!important;
}

.forum-title a,
.forum-title a:visited {
    color: #5a70b3!important;
    padding: 0 15px;
}

p.forum-description {
    display: none;
}

a,
ul#nav-breadcrumbs .breadcrumbs .crumb a,
.pagination li a {
    color: #e77700;
}

ul#nav-breadcrumbs .breadcrumbs .crumb,
ul#nav-breadcrumbs .breadcrumbs .crumb:last-child {
    color: #5a70b3;
    font-weight: bold;
}

a.username {
    color: #676767;
}

.action-bar .pagination {
    color: white;
}


.forabg ul.topiclist.forums dt .list-inner {
    margin-left: 45px!important;
}

ul.topiclist.topics dt .list-inner {
    margin-left: 15px!important;
}

dl.row-item dt .list-inner {
    padding-left: 15px;
}

ul.forums div.list-inner .subforum-box .subforum {
    display: inline;
    position: relative;
    float: none;
    background: none;
    line-height: normal;
    padding: 0px;
    margin: 0;
}

.subforum-box:before {
    content: "Subforums:";
    font-size: 10px!important;
}

ul.forums div.list-inner .subforum-box .subforum:hover {
    background: none;
}

.subforum-box .subforum .unread-mark {
    background: none!important;
}

ul.forums div.list-inner .subforum-box .subforum:after {
    content: ",";
    margin-left: -3px;
    color: #000;
}
ul.forums div.list-inner .subforum-box .subforum:last-child:after {
    content: "";
}
a.subforum {
    color: #e77700!important;
    font-style: italic;
    font-size: 10px!important;
}

ul.forums div.list-inner .subforum-box .subforum {
    background: none!important
}

a.subforum:hover {
    opacity: .5
}
dl a.row-item-link {
    top: 0;
    left: 0;
    margin-top: -15px;
    margin-left: -15px;
}

i.avatar-bg {
    border-radius: 3px!important
}

dl.row-item.forum_unread,
dl.row-item.forum_unread_subforum,
dl.row-item.forum_read,
dl.row-item.forum_read_subforum,
dl.row-item.forum_link,
dl.row-item.forum_unread_locked,
dl.row-item.forum_read_locked,
dl a.row-item-link {
    background-position: 0 50%;
}

dl.row-item.forum_unread,
dl.row-item.forum_unread_subforum {
    background: url(https://78.media.tumblr.com/f78defb55073d42d126548ddb8aa0abc/tumblr_inline_pe81p18LDN1r5ob8p_540.png) no-repeat;
}

dl.row-item.forum_read,
dl.row-item.forum_read_subforum {
    background: url(https://78.media.tumblr.com/bd170f12085b92a1df3fd72f370db0a5/tumblr_inline_pe81plE4Qk1r5ob8p_540.png) no-repeat;
}

dl.row-item.forum_link {
    background: url(https://78.media.tumblr.com/a0e066f948d3a8605b1355e355074087/tumblr_inline_pe8su9fU2D1r5ob8p_540.png) no-repeat;
}

dl.row-item.forum_unread_locked,
dl.row-item.forum_read_locked {
    background: url(https://78.media.tumblr.com/b56ddbdd724d7e86fac20930fc8b803a/tumblr_inline_pe8suyQ8QB1r5ob8p_540.png) no-repeat;
}

dl a.row-item-link {
    top: 0;
    left: 0;
    margin-top: 10px;
    margin-left: 10px;
}

.unread-mark {
    display: none!important;
}

dl.row-item.sticky_read,
dl.row-item.topic_read,
dl.row-item.topic_read_poll,
dl.row-item.topic_read_locked,
dl.row-item.sticky_read_locked {
    background: url(https://78.media.tumblr.com/3512f6ba2330fa7bfa26c9db99d0aacd/tumblr_inline_pe81rqktgk1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}

dl.row-item.sticky_unread,
dl.row-item.topic_unread,
dl.row-item.topic_unread_poll,
dl.row-item.topic_unread_locked,
dl.row-item.sticky_unread_locked {
    background: url(https://78.media.tumblr.com/c429f1dd210274d9f926a65702800c73/tumblr_inline_pe81rmiT6U1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}
dl.row-item.topic_unread_hot_poll,
dl.row-item.topic_unread_hot {
    background: url(https://78.media.tumblr.com/25658971762889e87b34259773f3b254/tumblr_inline_pe8r8grDFz1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}
dl.row-item.topic_read_hot_poll,
dl.row-item.topic_read_hot {
    background: url(https://78.media.tumblr.com/1c4740cb8bb1d05b251383e1119be29b/tumblr_inline_pe8r8kGoVz1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}

dl.row-item.sticky_read_mine,
dl.row-item.topic_read_mine,
dl.row-item.topic_read_locked_mine {
    background: url(https://78.media.tumblr.com/2316439415231f675dc521c222450a4c/tumblr_inline_pe8sepDjch1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}

dl.row-item.sticky_unread_mine,
dl.row-item.topic_unread_mine,
dl.row-item.topic_unread_locked_mine {
    background: url(https://78.media.tumblr.com/18c49e57c0a23ab5d14c42922e628a5a/tumblr_inline_pe8skxi8yH1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    height: 40px;
}

dl.row-item.topic_unread_hot_poll_mine,
dl.row-item.topic_read_hot_mine {
    background: url(https://78.media.tumblr.com/ea1da3e006501b2618f4a9b70fd8495e/tumblr_inline_pe8sepcmPb1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}
dl.row-item.topic_read_hot_poll_mine,
dl.row-item.topic_read_hot_mine {
    background: url(https://78.media.tumblr.com/f8305155d8efffbdea4655d523ce36bd/tumblr_inline_pe8sepoGBI1r5ob8p_540.png) no-repeat!important;
    background-position: 1px 50%!important;
    min-height: 40px;
}


.posteravatar.responsive-hide,
.lastpostavatar i.avatar-bg {
    display: none;
}

.search-result-content,
.topic-poster,
.topiclist.topics li.row dl.row-item dt.item-name .list-inner .tp-pg {
    margin: 0;
}

ul.topics li.row:nth-child(odd) {
    background: #f6f6f6
}
ul.topics li.row:nth-child(even) {
    background: #fafafa
}

ul.topics li.row {
    min-height: 40px;
    padding: 5px 10px;
}

ul.topiclist.topics dd.posts,
ul.topiclist.topics dd.views,
ul.topiclist.topics dd.lastpost {
    padding-top: 0px!important;
}


.pagination.tp-pg {
    float: right!important;
    position: relative;
    top: -25px;
    height: 0px;
}

ul.topics li.row span.lastpostavatar img,
ul.topics li.row span.lastpostavatar i.avatar-bg {
    display: none;
}

li.row.deleted,
li.row.moved {
    opacity: 1;
}

dl.row-item.topic_moved,
dl.row-item.topic_deleted {
    opacity: 0.5;
}

.header > .row-item > dd.posts,
.header > .row-item > dd.views,
.header > .row-item > dd.lastpost,
.header > .row-item > dd.topics {
    visibility: hidden;
}

.header > .row-item > dt {
    margin: 5px;
}

.header > .row-item,
.header {
    height: 30px!important;
}

div.rules {
    background-color: #e6e9f7;
    color: #000;
    border: 1px solid #BFC8E3;
    margin-bottom: 15px;
}

div.rules strong {
    color: #5a70b3
}

.action-bar .button {
    margin-right: 15px;
    float: left;
}

.pagination li a:hover,
.pagination li.active span,
.pagination li a:focus,
blockquote,
.codebox code,
.jumpbox-cat-link,
input.button1:focus,
input.button2:focus,
input.button3:focus,
input.specialbutton,
.inputbox:hover,
.inputbox:focus,
.specialbutton,
a.specialbutton,
.button:focus {
    border-color: #5a70b3;
}
input,
.pagination li a:hover,
.pagination li.active span,
.pagination li a:focus,
.codebox code,
.jumpbox-cat-link,
input.button1:focus,
input.button2:focus,
input.button3:focus,
input.specialbutton,
.specialbutton,
a.specialbutton,
.button:focus {
    background: #5a70b3;
}

.topic_type,
.pagination li a:hover,
.pagination li.active span,
.pagination li a:focus,
.jumpbox-cat-link,
.dropdown-contents > li > a:hover,
a.button1:hover,
input.button1:hover,
a.button2:hover,
input.button2:hover,
input.button3:hover,
a.no_avatar:hover,
.jumpbox-cat-link:hover,
input.button1:focus,
input.button2:focus,
input.button3:focus,
.specialbutton,
input.specialbutton,
.panel .specialbutton,
a.specialbutton,
input.quick-relay.button1 {
    background: #5e74b8;
}

button.tapatalk-theme-background {
    background: #5a70b3!important;
}

ul.topiclist.forums > li {
    background: #fafafa;
}

.codebox {
    background-color: #fafafa;
}

.codebox.spoiler > dt {
    background: #6677AF;
    border: 1px solid #3a5197;
}

.codebox.spoiler > dt a {
    color: #fff;
    width: 100%;
    display: inline-block;
    padding: 5px;
}

.codebox.spoiler > dd {
    border: 1px solid #ccc;
    border-top: none;
    padding: 15px 5px
}

.post .inner {
    border: 1px solid #eee;
    background: #fafafa;
}

.post .postbody {
    border-left: 1px solid #eee;
    background: #fafafa;
}

.postbody > div {
    padding: 15px;
}

.post .postprofile {
    margin-top: 10px;
}

.post {
    margin-bottom: 0;
    border-bottom: 1px solid #fff;
}

.post p.author a,
.postcontent_footer a span {
    color: #676767;
}

blockquote {
    background-color: #eeeeee;
}

.dropdown-extended .header {
    background-color: #566cb0;
    height: 40px!important;
}

.postlink {
    word-break: normal;
}
}
