css:
@-moz-document domain("pcwelt.de") {
/*HELLSTES DESIGN*/
#header .panel, #header .bar, #sidebar .box, #sidebar .box ul li:before, .add-block .holder, .visual .description, #sidebar .box .cycle-gallery, #sidebar .box .top-products .search, .image-detail .description .box, .video-detail .description .box, #main .add-box2 .banner-left, #main .add-box2 .banner-left .content div:last-child.more, .table-holder table, .billiger-breaker .content, div.idgAmazonTextBox, #main .add-block .cycle-gallery.carousel.wide
{
    background-color: #253447
}
/*DUNKLES DESIGN*/
body, #sidebar .box ul li:hover .holder, .inline-box
{
    background-color: #141d26;
}
/*HELLES DESIGN*/
#main>div, #main>header, #outer > div.blank, .comments .holder, .table-holder table .even td, .add-block .carousel .slide-block, #main.video .gallery-block .add-block .mask .slideset .slide .slide-block .text-block:hover, #main.video .heading-page .add-block .mask .slideset .slide .slide-block .text-block:hover, #main.gallery .gallery-block .add-block .mask .slideset .slide .slide-block .text-block:hover, #main.gallery .heading-page .add-block .mask .slideset .slide .slide-block .text-block:hover, .title-page strong
{
    background-color: #1b2836 !important; 
    border-color: #1c1c1c
}
*{
    color:white
}
/* BLUE TEXT */
.article a, a, #main .add-box2 .banner-left .content div:last-child.more .pricehistory, .billiger-breaker .content .data-box .more .pricehistory, .section ul .title-news, .section .top-block .only-news, span, #footer li a, .title-page strong
{
    color: #1ea1f2
}
/*WHITE Text*/
.post a h2, .post-odd a h2, .post-even a h2, .post-even a:hover p, .post a:hover p, .post-odd a:hover p,  .post a p, .post-odd a p, .post-even a p, .nav a, #header .panel strong, #header .add-nav a, .login,#sidebar .box .list, #sidebar .box .list dt, #sidebar .box p, #sidebar .box ul .text-holder, #sidebar .box h5, #sidebar .box h4, #sidebar .box h3, #sidebar .box h2, #sidebar .box h1, #sidebar .box h6, #sidebar .box h7, #sidebar .box .list a, #sidebar .box .list h5, #sidebar .box .list dd span, .add-block h2, .add-block .holder.hover:hover p, .add-block .holder.hover p, #main .add-box2 h1.headline, .article .add-box .title, .article, .breadcrumb a, strong.author, .visual .description .title, .article .add-box .visual .description .title, .visual .copy, .article h2, .article h3, #sidebar .box h5 a, #sidebar .box .top-products .text-holder div strong, #sidebar .box, .image-detail .description .box a, .video-detail .description .box a, .image-detail .description, .video-detail .description, #main .add-box2 .banner-left .content .header, #main .add-box2 .banner-left .content div span, .billiger-breaker .content .summ, .billiger-breaker .content .data-box ul li a span, .section .rubric, .section .top-block .title, #main #breadcrumb-headline, .breadcrumb, .block-title h1, #main.video .gallery-block .block-title h2, #main.video .heading-page .block-title h2, #main.gallery .gallery-block .block-title h2, #main.gallery .heading-page .block-title h2, #main .carousel .text-block a
{
    color: #ffffff !important
}


}

@-moz-document domain("pcwelt.de") {
#main>div, #main>header, img, #sidebar .box .top-products a.button span, .heading-page .nav-page-holder #nav-page li a, .inline-box, .billiger-breaker .content .data-box a.button span, .comments .holder .frame .btn
{
    border-radius: 15px !important
}
#headersearch-form > fieldset > div{
    border-radius: 5px 
}
.article h2, .article h3{
    text-decoration: underline;
}
#content > article > div.table-holder.embedded-video{
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
    border-bottom-right-radius: 25px;
    border-bottom-left-radius: 25px
}
#sidebar .box .top-products img, .inline-box, div.idgAmazonTextBox{
    border: none
}
.title-page strong{
    font-size: 120%
}
}

@-moz-document domain("pcwelt.de") {
.blue .tabset-holder .title{
    border-top-left-radius: 10px   
}
#header .panel, #sidebar .box{
    border-bottom-left-radius: 10px;
    border-bottom-right-radius: 10px
}
.nav ul{
    background: none
}
.tab-content .text-holder, .post-big .holder .text-holder, .visual.landingpage-head .holder .text-holder{
    border-bottom-left-radius: 15px;
    border-bottom-right-radius: 15px
}
*, #main .add-box2 .banner-left .content .header, #main .carousel .text-block a{
    text-shadow: none
}
#sidebar .box{
    background: #253447
}
#header > div.bar > strong > a > img{
    border-radius: 0px !important
}
#content > article > div > div > table > tbody > tr > td, .table-holder table td {
    border: 1px solid #141d26 !important;
}
}

@-moz-document domain("disqus.com") {
*{
    color: white
}
}

@-moz-document domain("newsletter.pcwelt.de") {
#content > form > div > div, #wrapper, #content, #footer, #content .w3, #content .modul{
    background: #141d26 !important;
    background-color: #141d26 !important;
    color: white
}
#content .modul.w3 h1, #content .modul.w3 h3, #content .modul h1 span, #content .modul h3 span, input.big{
    background: #253447;
    border: 0 !important;
    margin: 0 !important;
}
#nav_wrapper{
    visibility: hidden !important
}
input.big{
    background: #253447;
    border-radius: 15px
}

}
