css:
@-moz-document domain("alemjahan.net") {
#header {
    position: fixed;
    width: 100%;
    z-index: 10;
    background: #14677f;
    box-shadow: 0 2px 3px -1px rgba(0, 0, 0, .12)
}
[_preloader] {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: #fff0;
    z-index: -1
}
.btn-theme {
    background: #14677f;
    color: #fff
}
.share i {
    padding: 4px 6px;
    border: 1px solid #4c75a3;
    color: #14677f;
    font-size: 20px;
    width: 40px;
    text-align: center;
    border-radius: 4px;
    -webkit-transition: color .16s ease, background .12s ease;
    -moz-transition: color .16s ease, background .12s ease;
    -o-transition: color .16s ease, background .12s ease;
    transition: color .16s ease, background .12s ease
}
#news ul.tags li a.active {
    color: #14677f;
    font-weight: 700
}
ul.pagination .page-item.active .page-link {
    color: #fff;
    background-color: #14677f;
    border-color: #14677f
}
#reports ul.tags li a.active {
    color: #14677f;
    font-weight: 700
}
#report .buttons span.sold {
    background: #14677f;
    border-color: #14677f;
    box-shadow: 0 5px 10px -4px #14677f
}
.side-categories ul li a.active {
    border-left: 3px solid #14677f;
    color: #14677f
}
.side-categories span[action=extend].active {
    color: #14677f;
    transform: rotate(180deg)
}
#profile .tabs li a:after {
    content: "";
    -webkit-transition: all .15s ease;
    -moz-transition: all .15s ease;
    -o-transition: all .15s ease;
    transition: all .15s ease;
    width: calc(100% + 10px);
    height: 3px;
    background: #14677f;
    display: block;
    position: relative;
    top: 13px;
    left: -5px
}
#blogs .options {
    background: #14677f;
    margin-bottom: 1em;
    color: #fff;
    padding: 10px 20px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8
}
#blogs .options .create {
    float: right;
    background: #fff;
    color: #14677f;
    border-radius: 3px;
    padding: 2px 4px 2px 8px;
    position: relative;
    top: 0;
    font-size: 14px
}
#create-form h2.title {
    background: #14677f;
    color: #fff;
    font-size: 16px;
    padding: 10px 15px;
    border-radius: 4px
}
#blog-page .btn-edit {
    background: #14677f;
    border-color: #14677f;
    box-shadow: 0 5px 10px -4px #14677f
}
#commerce-list .main-commerce .owl-nav button {
    padding: 0 10px!important;
    background: #14677f;
    color: #fff;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    font-size: 18px;
    margin-right: 10px
}
#commerce-list div.options a.create {
    background: #14677f;
    color: #fff;
    float: right
}
#commerce-list div.products div.item.sold:after {
    content: "";
    background: #14677f;
    height: 58px;
    width: 58px;
    position: absolute;
    bottom: 0;
    right: 0;
    -webkit-clip-path: polygon(100% 1%, 0 100%, 100% 100%);
    clip-path: polygon(100% 1%, 0 100%, 100% 100%)
}
[_right_side] p[data-toggle=collapse]:hover,
[_right_side] p[data-toggle=collapse]:not(.collapsed) {
    background: #14677f;
    color: #fff
}
#posts .options ul.tags li a.active {
    color: #14677f;
    font-weight: 700
}
.top-bar {
    background: #14677f;
    margin-bottom: 1em;
    color: #fff;
    padding: 10px 20px;
    box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8
}
.top-bar .create {
    float: right;
    background: #fff;
    color: #14677f;
    border-radius: 3px;
    padding: 2px 4px 2px 8px;
    position: relative;
    top: 0;
    font-size: 14px
}
#blogs .options {
    background: #14677f;
    margin-bottom: 1em;
    color: #fff;
    padding: 10px 20px;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)
}
#blogs .options .create {
    float: right;
    background: #fff;
    color: #14677f;
    border-radius: 3px;
    padding: 2px 4px 2px 8px;
    position: relative;
    top: 0;
    font-size: 14px
}
#blogs .blogs .item.mobile i {
    position: absolute;
    top: 12px;
    right: 12px;
    font-size: 14px;
    color: #14677f;
    font-weight: 700;
    font-style: normal
}
#commerce-list div.options a.create {
    background: #14677f;
    color: #fff;
    float: right
}

#sidebar .list-group-item:hover {
    border-color: rgba(0,0,0,.125);
    color: #14677f;
}

[_right_side] .actions ul li p a {
    color: #14677f;
}

#posts .posts .item .top > a {
    color: #14677f;
}

#blogs .blogs .item .image-holder {
    width: 170px;
    height: 100px;
}
}
