css:
@-moz-document url("https://www.cnblogs.com/") {
body {
    font-family: "Source Han Sans SC", "Helvetica Neue", Helvetica, Arial, "PingFang SC", "Hiragino Sans GB", "Heiti SC", "Microsoft YaHei", "WenQuanYi Micro Hei", sans-serif !important;
    --color-primary: #2196f3;
    --color-faded: #8590a6;
    --color-dark: #1a1a1a;
    --color-light: #f7f7f7;
}
#main {

    padding: 0;
    margin: 10px 320px 0 25vw;
}

#hd_info {
    height: 36px;
    line-height: 36px;
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}

#site_nav_top {
    display: none;
}

#login_area a:hover {
    color: var(--color-primary);
    font-size: 14px;
    text-decoration: none;
}

#login_area {
    color: #5b5b5b;
    font-size: 14px;
    overflow: hidden;
    text-align: right;
    height: 36px;
    line-height: 36px;
}

#header,
.cate_bottom {
    display: none;
}

#nav_menu {
    background: var(--color-primary) !important;
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    height: 3rem;
    line-height: 3rem;
    border-radius: 2px;
    max-width: 50vw;
    margin-left: 25vw;
}

#nav_menu a {
    font-size: 16px;
    padding-left: 24px;
}

#nav_menu a:hover {
    text-decoration: none;
}

.post_nav_block a:link,
.post_nav_block a:visited,
.post_nav_block a:active {
    border: none;
    background-color: white;
    color: var(--color-primary) !important;
}

.post_nav_block a.current_nav:link,
.post_nav_block a.current_nav:visited,
.post_nav_block a.current_nav:active {
    border: unset;
    border-bottom: solid 2px var(--color-primary);
}

.post_nav_block_wrapper {
    border: none;
}
.post_nav_block {
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    border-radius: 2px;
    padding: 4px;
    padding-bottom: 0;
}
.rss_link,
.right_more {
    display: none;
}
#cate_title_block {
    background-color: white;
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
}
.cate_title {
    color: var(--color-faded);
}
#cate_title_title,
#cate_item {
    background: none;
    border: none;
    border-radius: 2px;
}
#cate_item li {
    border: none;
    font-size: 14px;
    line-height: 2.5em;
    height: unset;
}
#cate_item li a:link,
#cate_item li a:visited,
#cate_item li a:active {
    color: var(--color-faded);
}
#cate_item .cate_item_hover {
    background: none !important;
    color: var(--color-primary) !important;
}
#cate_item .cate_item_hover a {
    color: var(--color-primary) !important;
}
#side_nav {
    background-color: white;
    left: 12vw;
}
.cate_content_top,
.cate_content_bottom {
    background: none;
}
.cate_content_block ul {
    border: none !important;
}
.cate_content_block {
    border: none !important;
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    border-radius: 2px;
}
.cate_content_block a:hover {
    color: var(--color-primary);
}
#headline_block {
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    border-radius: 2px;
    padding: 8px;
    font-size: 14px;
    max-width: 50vw;
}
.editor_pick {
    color: var(--color-primary) !important;
    font-size: 16px;
}
#headline_block ul li {
    line-height: 2.4;
}
#headline_block ul li a:hover {
    color: var(--color-primary);
    text-decoration: none;
    margin-right: 0;
}
#post_list {
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    border-radius: 2px;
    margin-top: 4px;
    padding: 8px;
    max-width: 50vw;
}
a.titlelnk:link,
a.titlelnk:active {
    color: var(--color-dark);
    font-size: 18px;
    font-weight: 600;
    line-height: 1.6;
    text-decoration: none;
}
a.titlelnk:hover {
    color: var(--color-primary);
}
#post_list .post_item_summary {
    color: var(--color-dark);
    font-size: 14px;
    font-weight: 400;
    line-height: 1.6;
}
a.lightblue:link,
a.lightblue:active,
a.lightblue:visited {
    color: #555555;
    font-size: 15px;
    font-weight: 600;
}
a.lightblue:hover {
    text-decoration: none;
}
.post_item_foot {
    font-size: 13px;
}
.post_item {
    position: relative;
}
#post_list .article_comment {
    background-image: url(data:image/svg+xml;base64,);
    padding-left: 22px;
}
.article_comment a:hover,
.gray:hover {
    font-size: 13px;
}

.article_view {
    display: none;
}
.pfs {
    position: absolute;
    border: none;
    border-radius: 2px;
    left: 0;
    top: 50%;
}
.w_l,
.r_l_1,
.r_l_2,
.r_l_3 {
    border: none;
}
.w_l,
.w_r {
    border: none;
    box-shadow: 0 1px 3px rgba(26, 26, 26, .1);
    border-radius: 2px;
    font-size: 14px;
    color: var(--color-faded);
}
.w_l a:link,
.w_l a:active,
.w_l a:visited {
    color: var(--color-faded);
    font-size: 14px;
}
.w_l h4 {
    color: var(--color-faded);
    font-size: 16px;
}
.w_l a:hover,
.w_r ul a:hover {
    text-decoration: none;
    color: var(--color-primary);
}
#footer,
#footer_bottom {
    display: none;
}
#side_right {
    border-radius: 2px;
    background-color: white;
    border: none;
    right: 6vw;
}
.w_r h4 a:link,
.w_r h4 a:visited,
.w_r h4 a:active,
.w_r h4 a:hover {
    color: var(--color-primary);
}
.diggit {
    background: url(data:image/png;base64,);
    background-repeat: no-repeat;
    background-origin: unset;
    background-position: bottom;
    background-size: contain;
    width: 18px;
    height: 48px;
    margin-left: 16px;
}
}
