css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("horriblesubs.info") {
  
  body
  {background-image: url("/*[[pic]]*/");background-repeat: no-repeat;background-position: right bottom;background-attachment: fixed;}
  
  #message
  {background: #161616 !important;}
  
  .latest-releases a:hover .badge, .rls-label:hover .badge,.latest-releases-res .badge, .rls-label .badge,.la-badge
  {background-color: #000 !important;}
  
  #disqus_thread
  {background: rgba(22,22,22,0.8) !important;}
  
  .well,.post-inner-content,.navbar.navbar-default,.dropdown-menu
  {background-color: rgba(22,22,22,0.8) !important;}
  
  body,#searchdiv
  {background-color: #212121 !important;}
  
  .latest-releases a:hover, .latest-show-more a:hover,.latest-releases .active a:hover,.latest-releases .active,.rls-label:hover,.ind-show:hover,.loading
  {background: transparent !important;}
  
  .form-control,.navbar-default .navbar-nav .dropdown-menu > li:hover, .navbar-default .navbar-nav .dropdown-menu > li:focus, .dropdown-menu > .active,
  .table-hover > tbody > tr:hover,#c1,.table-hover > tbody > tr:hover, .schedule-table > tbody > tr:hover,.schedule-today,.botlist,.anime0,div.header,.anime1,#status,.alert-success
  {background-color: transparent !important;}
  
  .rls-link
  {color: transparent;}
  
  #message,.dropdown-menu > li > a,#secondary .widget a,.badge,.default,.search,.loading,.latest-releases a, .latest-show-more a, body, .form-control,.rls-link-label,.alert-success
  {color: #bcbcbc;}
  
  .latest-releases a, .latest-show-more a,body,.form-control
  {color: #999daa;}
  
  #secondary .widget a:hover,.latest-releases a:hover, .latest-show-more a:hover
  {color: #da4453;}
  
  .form-control,#message
  {border: transparent !important;}
  
  #status,.alert-success
  {border-color: transparent !important;}
  
  .hs-shows,.alpha-nav-wrapper,div.footer
  {border-top: transparent !important;}
  
  .widget ul li,.table > tbody > tr > td,.rls-info-container,.latest-releases ul li,.table > tbody > tr > td, .schedule-table > tbody > tr,.alpha-nav-wrapper,.alphabet-title,.anime0 td,div.header,div.footer,th,
  #searchdiv,.anime1 td
  {border-bottom: transparent !important;}
  
  .botlist
  {border-right: transparent !important;}
  
  #searchdiv
  {border-left: transparent !important;}
  
  .navbar.navbar-default,.well,.post-inner-content,#disqus_thread
  {box-shadow: none !important;}
  
  [src="/images/show_placeholder.jpg"]
  {visibility: hidden;}
  
  [src="https://horriblesubs.info/images/hexchatguide.png"]
  {opacity: 0.7;transition: ease-out 0.5s;}
  
  [src="https://horriblesubs.info/images/hexchatguide.png"]:hover
  {opacity: 1;transition: ease-out 0.8s;}

  .center-block,.header-ad.text-center
  {display: none;}
  
  .latest-spons
  {margin-bottom: 0px;}

}

@-moz-document url-prefix("https://disqus.com/embed/comments/?base=default&f=horriblesubs"),url-prefix("https://disqus.com/home/forums/horriblesubs/") {
  
  .dropdown-menu,.thread-likes .label-count
  {background: #090909;}
  
  .alert.alert--realtime, .alert.info, .alert.success, .dark .alert.alert--realtime, .dark .alert.info, .dark .alert.success,.tooltip,.tooltip-point::after, .tooltip-point::before,.home-drawer #container-main,.btn,
  .btn:not(:disabled):hover
  {background: #111 !important;}
  
  .btn
  {box-shadow: none;}
  /*
  .btn
  {background: rgba(29,47,58,.6) !important;}
  
  .btn:not(:disabled):hover
  {background: rgba(29,47,58,.7) !important;}
  */
  .cover-nav-option.active > a::before
  {background-color: #da4453;}
  
  .post-content .post-body spoiler:focus, .post-content .post-body spoiler:hover,.home-drawer .viewport,.home-drawer #container-main > div,.home-drawer .cover-profile, .home-drawer .notifs-fixed,
  .dropdown-cover-profile li a:not([disabled]):hover,.post-content .post-body spoiler,spoiler, spoiler .media-content-placeholder,spoiler:focus, spoiler:hover,.cover,.home-drawer .viewport,
  .post-content.new .indicator, .post-content.target .indicator,.realtime-button .indicator,.hljs
  {background: transparent;}
  
  .textarea-wrapper,.input--text,.post-actions,.layout .more-wrapper,.tooltip__footer
  {background: transparent !important;}
  
  .upvoters-outer::after,.upvoters-outer::before
  {background-image: none !important;}
  
  .highlighted-post .highlighted,.highlighted > .post-content,.drawer-footer,.dropdown-menu .active > a, .dropdown-menu .active > a:hover, .dropdown-menu li > a:hover,.follow-btn.private,.textarea
   {background-color: transparent !important;} 
  
  .comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a
  {color: #999daa;}
  
  .name,.tooltip__content h3 a
  {color: rgb(218, 68, 83) !important;}
  
  body,.alert,.card-comment .comment-message,.dropdown-cover-profile li a:not([disabled]):hover,.link-gray-darker,.thread-likes .label-count,.btn,.btn:not(:disabled):hover,.hljs,.textarea
  {color: #bcbcbc;}
  
  .dropdown-menu .active > a, .dropdown-menu .active > a:hover, .dropdown-menu li > a:hover
  {color: #da4453 !important;}  
  
  .tab-conversation.active > a,.nav-tab > a:hover,.icon-cog:hover, .icon-export:hover, .label:hover,.comment__footer .vote-down:hover, .comment__footer a:hover, .comment__header .vote-down:hover, .comment__header a:hover,
  .upvoters-outer .upvoters .user .username:hover,.no-touch .card-profile .card-reason .link-community:hover, .no-touchevents .card-profile .card-reason .link-community:hover,
  .no-touch .link-gray-darker:hover, .no-touchevents .link-gray-darker:hover,.cover-nav-option.active > a, .no-touch .cover-nav-option > a:hover, .no-touchevents .cover-nav-option > a:hover,.cover-profile-name,
  .dropdown__link:not([disabled]):hover .dropdown-cover-profile__title,.dropdown-cover-profile__title,.full-profile:hover
  {color: #da4453 !important;}  
  
  .dropdown-cover-profile,.dropdown-menu
  {border: solid 1px #090909;}
  
  .textarea-wrapper,.input--text,.input--text:focus,.thread-likes .label-count,.textarea-outer-wrapper .textarea-wrapper
  {border: 2px solid #2e2e2e;}
  
  .post-content .post-menu .dropdown-toggle
  {border-color: transparent !important;}
  
  .disqus-footer__wrapper,.post-actions,.cover-nav-wrapper,.drawer-footer,.see-more
  {border-top: transparent !important;}
  
  .nav-primary,.home-drawer .cover-profile, .home-drawer .notifs-fixed,.home-drawer .card-profile,.border-bottom, .border-bottom-dark,.home-drawer .cover-forum
  {border-bottom: transparent !important;}
  
  .post-votes .post-votes__separator
  {border-right: 2px solid transparent;}
  
  .vote-down,.home-drawer #container-main,.drawer-footer
  {border-left: transparent !important;}
  
  .dropdown-menu::before,.dropdown-menu::after
  {border-color: #090909 transparent #090909;}
  
  .caret
  {border-top: 4px solid #656c7a;}
  
  .post-content .post-body spoiler:focus, .post-content .post-body spoiler:hover,.post-content .post-body spoiler,spoiler, spoiler .media-content-placeholder,spoiler:focus, spoiler:hover
  {transition: .8s;}
  
  .post-content .post-body spoiler::before,spoiler::before
  {content: " - Spoiler -  ";color: #FF22C3;font-style: oblique;}
  
  /*.tooltip,.tooltip-outer
  {display: none !important;}*/
  
  
  
  
}

@-moz-document url-prefix("https://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1") {
  
  #c1
  {background-color: transparent !important;}  
  
  #t1c, #t1
  {color: #bcbcbc;}
  
}
