css:
@-moz-document domain("amanz.my"),domain("aksiz.com"),domain("blogserius.com")
{
  /*.home-features .featured-article .featured-link::after,.slot-scroll .slot-poster::before
  {
    background: rgba(0,0,0,0.6) !important;
  }*/
  
   .home-features .featured-article .featured-link::after,.slot-scroll .slot-poster::before
  {background: linear-gradient(to top, rgba(0,0,0,0.9),rgba(0,0,0,0.8) ,rgba(0,0,0,0.7) ,rgba(0,0,0,0.4),rgba(0,0,0,0.3),rgba(0,0,0,0.1)) !important;}
  
  /* .home-features .featured-article.f3 .featured-meta */
  
  table,table tfoot, table thead,table tbody tr:nth-child(2n),.amanzme .popover,.atwho-view,.about-title,.widget .streamtitle,.wp-embed
  {
    background: #000 !important;
  }

  body,.row .hrow.dividers,.brand-bar
  {
    background: #111 !important;
  }

  .atwho-view .cur
  {
    background: #222 !important;
  }

  .amanzme .dropdown-menu,.comment-list .thumb .label-default,.modal-content,.amanzme .modal-header,.amanzme .alert-warning,.button.secondary,.button.secondary:hover
  {
    background-color: #000;
  }

  .prev-post a:hover,.next-post a:hover,input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], 
  input[type="time"], input[type="url"], textarea,.row .hrow.dividers .labels,.grand-selections .selection-posts .selection-post,.grand-selections,.shortcode-information,.shortcode-download,.widget .widgettitle,
  .video-list-title,.video-list-title .labels,.footer
  {
    background: transparent !important;
  }

  .title a,.comment-list .comment,.amanzme .dropdown-menu .divider,.amanzme .dropdown-menu > li > a:hover, .amanzme .dropdown-menu > li > a:focus,.single-flex .timeline-block .streamlist .post-date span,
  .comments .comment-title,.single-flex .timeline-block .streamlist .post-date::after,.amanzme .form-control,.promo-code-price,#tie-block_611 .mag-box-title::after,.senarai-artikel .artikel-gambar .img
  {
    background-color: transparent !important;
  }

  .home-features .featured-article .featured-link,.grand-selections::after,.slot-scroll .slot-poster::before
  {
    background-image: none !important;
  }

  body,.amanzme .dropdown-menu > li > a,.single-flex .timeline-block .streamlist .post-stream,.amanzme .form-control,.contents .related a,table tbody tr td,.video-list-title .labels,.atwho-view,.amanzme .text-muted,.wp-embed,
  input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], input[type="time"], input[type="url"], textarea,
  .button.secondary
  {
    color: #bcbcbc;
  }

  .home-features .featured-article .featured-title,.widget .streamlist .row a .post-stream
  {
    color: #dddddd !important;
  }

  .row a,.single-flex .timeline-block .streamlist .post-date,table tfoot tr td, table thead tr th,.wp-embed-heading a
  {
    color: #80b1b7;
  }

  h1, h2, h3, h4, h5, h6
  {
    color: #80b1b7 !important;
  }

  .amanzme .dropdown-menu > li > a:hover, .amanzme .dropdown-menu > li > a:focus,.title a:hover,a:hover,.single-flex .timeline-block .streamlist .post-stream:hover,.amanzme a:hover, .amanzme a:focus,
  .prev-post a:hover,.next-post a:hover,.footer a:hover,.brand-bar .social-link a:hover,.contents .related a:hover,.button.secondary:hover
  {
    color: lightblue;
  }

  .home-features .featured-article .featured-title:hover
  {
    color: lightblue !important;
  }

  .amanzme .alert-warning
  {
    color: #8e0c1b;
  }

  .footer
  {
    color: transparent;
  }

  .amanzme .alert-warning
  {
    border-color: #8e0c1b;
  }

  .amanzme .popover,.atwho-view,.button.secondary
  {
    border: 1px solid #000 !important;
  }

  .amanzme .form-control,input[type="date"], input[type="datetime"], input[type="email"], input[type="number"], input[type="password"], input[type="search"], input[type="tel"], input[type="text"], 
  input[type="time"],   input[type="url"], textarea,.comment-list .thumb .label-default,.amanzme .dropdown-menu,.modal-content
  {
    border: 1px solid #424242 !important;
  }

  .amanzme .popover.bottom > .arrow::after
  {
    border-bottom-color: #000 !important;
  }

  .row .srow.twelve,.grand-selections,hr,table,.amanzme.profile-menu .open .dropdown-toggle .profile-img,.row.mode-2-border,.amanzme.profile-menu .dropdown-toggle .profile-img,.contents p img,.wp-embed
  {
    border: transparent !important;
  }

  .comment-list .child
  {
    border-top: transparent !important;
  }

  .comment-list .parent,.amanzme.profile-menu .dropdown-menu .dropdown-header,.row .archives .archives-list,.atwho-view ul li,.modal-header,.senarai-artikel .artikel,.widget .streamlist .headline .post-title
  {
    border-bottom: transparent !important;
  }

  .brand-bar.headroom .share-bar,.footer .flist li,.row .archives,.widget .streamlist .row a .post-date
  {
    border-right: transparent !important;
  }

  .brand-bar.headroom .single-title,.row .archives,blockquote,.row .archives
  {
    border-left: transparent !important;
  }

  *,.single-featured-image::after
  {
    box-shadow: none !important;
  }

  .single-featured-image::after,*
  {
    -webkit-box-shadow: none !important;
  }

  *
  {
    text-decoration: none !important;
  }
  
  /*.single-flex .single-content header
  {padding-top: 10rem;}*/
  
  .single-featured-image img
  {
    height: 82%;
  }

  .comment-list .thumb .vote
  {
    bottom: -20px;
  }

  .amanzme .img-circle,.amanzme.profile-menu .dropdown-toggle .profile-img,.navbar .user-dropdown .dropdown-toggle .profile-img
  {
    border-radius: 0%;
  }
  
  /*.home-features .featured-article.f3 .featured-meta
  {bottom: 0rem; left: 0rem; right: 0rem;}*/
  
  table
  {
    padding: 10px 10px 15px 15px;
    border-radius: 15px;
  }

  .comment-list .thumb .vote
  {
    font-size: 14px;
  }

  .share-bar a svg path
  {
    fill: #bcbcbc !important;
  }

}

@-moz-document domain("amanz.me")
{
  .selectize-dropdown,.selectize-dropdown [data-selectable], .selectize-dropdown .optgroup-header
  {
    background: #000 !important;
  }

  .alert-info,.alert-warning,.navbar,.profile-timeline .activity .text-content,.node-preview,.dropdown-menu,.input-group-addon,
.btn-default,.label-default
  {
    background-color: #000 !important;
  }

  body,.form-control,.forum
  {
    background-color: #111 !important;
  }

  .comment-list .thumb .vote-up:hover
  {
    background-color: green !important;
  }

  .comment-list .thumb .vote-down:hover
  {
    background-color: darkred !important;
  }
  
  .profile-stats
  {
    background: linear-gradient(to top, rgba(0,0,0,0.9),rgba(0,0,0,0.6),rgba(0,0,0,0.3),rgba(0,0,0,0.1)) !important; position: relative; top: -142px;
  }
  
  .profile-about,.filter-bar,.profile-timeline .activity-list
  {
    position: relative; top: -142px;
  }

  .selectize-input.dropdown-active::before
  {
    background: transparent !important;
  }

  .profile-timeline,.profile-content,.filter-bar,.profile-about,.well,.dropdown-menu .divider,.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus,.forum .breadcrumb,.selectize-control.single .selectize-input,
  .comment-list .comment,.thumbnail,.pagination > li > a, .pagination > li > span
  {
    background-color: transparent !important;
  }

  .selectize-control.single .selectize-input
  {
    background-image: none !important;
  }

  body,.dropdown-menu > li > a,.form-control,.input-group-addon,.selectize-dropdown, .selectize-input, .selectize-input input,.btn-default
  {
    color: #bcbcbc !important;
  }

  .alert-info,.dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus
  {
    color: #59dec6;
  }

  .forum-node .title a
  {
    color: #80b1b7;
  }

  .forum-node .title a:hover,.selectize-dropdown [data-selectable]:hover, .selectize-dropdown .optgroup-header:hover
  {
    color: lightblue;
  }

  .alert-warning
  {
    color: red;
  }

  .alert-warning
  {
    border-color: red;
  }

  .alert-info
  {
    border-color: #59dec6;
  }

  .dropdown-menu,.form-control,.selectize-input
  {
    border: solid 1px #444 !important;
  }

  .profile-timeline,.profile-content,.profile-about,.well,.profile-timeline .activity .text-content,.amanzme.profile-menu .open .dropdown-toggle .profile-img,.input-group-addon,.selectize-dropdown,.node-preview,
  .thumbnail,.pagination > li > a, .pagination > li > span
  {
    border: transparent !important;
  }

  .navbar-inverse,.navbar,.btn-default
  {
    border-color: transparent !important;
  }

  hr,.comment-list .child
  {
    border-top: transparent !important;
  }

  .forum .main
  {
    border-right: transparent !important;
  }

  .profile-timeline .activity .text-content,.node-preview .title,.profile-about .subhead,.node-compose,.forum-node,.widget,.forum .breadcrumb,
.node-details,.comment-list .parent
  {
    border-bottom: transparent !important;
  }

  .profile-timeline .activity .text-content::after
  {
    border-right-color: #000;
  }

  .profile-timeline .activity .text-content::before
  {
    border-right-color: #000;
  }

  .selectize-control.single .selectize-input
  {
    box-shadow: none !important;
  }

  *
  {
    text-decoration: none !important;
  }

  .avatar-form .avatar,.img-circle,.navbar .user-dropdown .dropdown-toggle .profile-img,.profile-card .avatar
  {
    border-radius: 0%;
  }
  
  .profile-timeline .activity-list
  {
    border-style: none !important;
  }
  
  .profile-card .info
  {
    padding-bottom: 135px;  /* 125px */
  }
}
